INSERT INTO `quest_template_locale` VALUES (97, 'frFR', '[PÉRIMÉ]La légende de Stalvan', 'Prévenez le commandant Bouclenoire.', 'Prévenez immédiatement le commandant Bouclenoire que les écritures concordent, $n ! Ses soupçons étaient fondés !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (97, 'itIT', '[DEPRECATED]The Legend of Stalvan', 'Report the news to Commander Ebonlocke.', 'Let Commander Ebonlocke know immediately that the handwriting matched, $n!  Her suspicions were correct!', '', '', '', '', '', '', 0);
