INSERT INTO `quest_template_locale` VALUES (404, 'deDE', 'A Putrid Task', 'Bring 7 Putrid Claws to Deathguard Dillinger in Brill.', 'Scourge have trickled into Tirisfal Glades and have infested the area west of Brill, near the old bridge.$B$BGo there and beat back the rotting dead and ravaged corpses you find.  Scatter their bones and bring back their putrid claws, and the Deathguards will reward you.', '', '', '', '', '', '', 0);
