INSERT INTO `quest_template_locale` VALUES (620, 'deDE', 'Die Schärpe mit Monogramm', 'Bringt die Schärpe mit Monogramm zu ihrem Besitzer zurück.', 'Auf dem Saum dieser feinen Schärpe sind die Buchstaben \"HS\" eingestickt.', '', '', '', '', '', 'Findet den Besitzer der Schärpe mit Monogramm in Beutebucht im Schlingendorntal.', 0);
INSERT INTO `quest_template_locale` VALUES (620, 'esES', 'La banda con monograma', 'Devuelve la banda con monograma a su propietario.', 'Esta preciosa banda tiene bordadas las letras \"HS\" en el dobladillo.', '', '', '', '', '', 'Encuentra al dueño del fajín con monogramas en Bahía del Botín, en la Vega de Tuercespina.', 0);
