INSERT INTO `quest_template_locale` VALUES (645, 'zhCN', '托卡拉尔', '将四块符印放在一起，它们就能互相结合形成一块完整的符印。毫无疑问，这块符印能够击破托尔贝恩地墓室里的防护魔法。$b$b托尔贝恩的遗体就埋葬在激流堡教堂后的密室中。去取回托卡拉尔。', '从激流堡的托尔贝恩墓室中取回托卡拉尔剑。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (646, 'deDE', 'Trol\'kalar', 'Bringt Trol\'kalar zu Zengu nach Hammerfall.', 'Das Licht verglüht langsam, das Schwert Trol\'kalar, das sich in einer steinernen Scheide befindet, ist freigegeben.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (646, 'esES', 'Trol\'kalar', 'Entrega Trol\'kalar a Zengu en Sentencia.', 'La luz se desvanece despacio, la espada Trol\'kalar, retenida en una funda pétrea se ha liberado.', '', '', '', '', '', '', 0);
