INSERT INTO `quest_template_locale` VALUES (646, 'frFR', 'Trol\'kalar', 'Apporter Trol\'kalar à Zengu au Trépas-d\'Orgrim.', 'La lumière s\'éteint, doucement, et l\'épée Trol\'kalar, reposant dans un fourreau de pierre, est libérée.', '', '', '', '', '', '', 0);
