INSERT INTO `quest_template_locale` VALUES (779, 'zhCN', '大地封印', '', '', '', '去找荒芜之地的大地封印。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (780, 'deDE', 'Die Kampfeber', 'Grull Falkenwind im Camp Narache möchte, dass Ihr für ihn Kampfeber tötet und ihm 8 Kampfeberschnauzen und 8 Kampfeberflanken bringt.', 'Die Kampfeber der Dornrankenklamm im Osten sind in unser Stammesjagdterritorium eingedrungen. Sie wurden von den Borstennackenstachelebern, mit denen wir uns im Krieg befinden, abgerichtet, niederträchtig zu sein.$b$bTötet diese elenden Kreaturen und bringt mir ein paar ihrer Schnauzen und Flanken, damit wir daraus einen Eintopf für unsere Jungen zubereiten können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (780, 'esES', 'Los jabaguerreros', 'Grull Viento de Halcón quiere que le lleves 8 hocicos y 8 costillares de jabaguerrero al Campamento Narache.', 'Los jabaguerreros del Barranco Cortazarza se están instalando en nuestro terreno de caza tribal. Los jabaespines Erizapúas, con quienes estamos en guerra, los amaestran para aumentar su ferocidad.$B$BAcaba con esas bestias y trae algunos hocicos y costillares para hacer caldo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (780, 'esMX', 'Los jabaguerreros', 'Grull Viento de Halcón quiere que le lleves 8 hocicos y 8 costillares de jabaguerrero al Campamento Narache.', 'Los jabaguerreros del Barranco Cortazarza se están instalando en nuestro terreno de caza tribal. Los jabaespines Erizapúas, con quienes estamos en guerra, los amaestran para aumentar su ferocidad.$B$BAcaba con esas bestias y trae algunos hocicos y costillares para hacer caldo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (780, 'frFR', 'Les sangliers de guerre apprivoisés', 'Grull Vent-du-faucon, du Camp Narache, voudrait que vous tuiez des sangliers de guerre, et que vous rapportiez 8 Groins de sanglier de guerre et 8 Flanchets de sanglier de guerre.', 'Les sangliers de guerre apprivoisés du ravin de Roncelame se répandent sur nos terrains de chasse. Ils sont entraînés au combat et rendus agressifs par les hurans dos-hirsutes contre lesquels nous sommes en guerre.$b$bPartez, massacrez ces infâmes créatures et revenez avec leurs groins et leurs flancs, que nous puissions en faire un bon ragoût pour les jeunes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (780, 'itIT', 'The Battleboars', 'Grull Hawkwind in Camp Narache wants you to kill Battleboars and bring back 8 Battleboar Snouts and 8 Battleboar Flanks.', 'The Battleboars of Brambleblade Ravine to the east are encroaching on our tribal hunting grounds. They are trained to be malicious by the Bristleback Quilboars with whom we are at war.$b$bGo and slay the vile creatures and bring back some snouts and flanks so that we can make stew for our young.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (780, 'ptBR', 'The Battleboars', 'Grull Hawkwind in Camp Narache wants you to kill Battleboars and bring back 8 Battleboar Snouts and 8 Battleboar Flanks.', 'The Battleboars of Brambleblade Ravine to the east are encroaching on our tribal hunting grounds. They are trained to be malicious by the Bristleback Quilboars with whom we are at war.$b$bGo and slay the vile creatures and bring back some snouts and flanks so that we can make stew for our young.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (780, 'ruRU', 'Боевые вепри', 'Принесите 8 боков и 8 рыл боевых вепрей Груллу Соколиному Ветру.', 'Боевые вепри из Ежевичной лощины, что к востоку отсюда, вторгаются в охотничьи угодья нашего племени. Их натаскивают свинобразы из племени Дыбогривов, наши заклятые враги. $b$bПойди и покажи гнусным тварям, кто хозяин в этих землях! Мы сварим из их боков и рыл славную похлебку для наших детишек!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (780, 'zhCN', '斗猪', '纳拉其营地的格鲁尔·鹰风要求你去干掉斗猪，并带给他8块斗猪头和8块斗猪肋排。', '生活在东边的刺刃峡谷中的斗猪已经侵占了我们部族的狩猎场。正在与我们交战的刺背野猪人把这些野猪训练成了凶暴而富有攻击性的生物。$b$b去把这些可恶的家伙杀掉，再带些猪头和肋排回来，我们可以炖肉给孩子们吃。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (780, 'zhTW', '鬥豬', '納拉其營地的格盧爾·鷹風要求你去幹掉鬥豬，並帶給他8塊鬥豬頭和8塊鬥豬肋排。', '生活在東邊的刺刃峽谷中的鬥豬已經侵佔了我們部族的狩獵場。正在與我們交戰的刺背野豬人把這些野豬訓練成了兇暴而富有攻擊性的生物。$b$b去把這些可惡的傢伙殺掉，再帶些豬頭和肋排回來，我們可以燉肉給孩子們吃。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (781, 'deDE', 'Attack on Camp Narache', 'Bring the Bristleback Attack Plans to Chief Hawkwind in Camp Narache.', 'After carefully evaluating the map, you realize that what you discovered are the plans made by the Bristleback warchief for an extended siege on Camp Narache.$b$bSurely Chief Hawkwind could use this information!', '', '', '', '', '', '', 0);
