INSERT INTO `quest_template_locale` VALUES (781, 'esMX', 'Ataque al Campamento Narache', 'Lleva los planes de ataque Erizapúas al jefe Viento de Halcón al Campamento Narache.', 'Tras estudiar cuidadosamente el mapa, te das cuenta de que lo que has descubierto son los planes del jefe Erizapúas para un asedio prolongado sobre el Campamento Narache.$b$b¡Seguro que al jefe Viento de Halcón le hace falta esta información!', '', '', '', '', '', '', 0);
