INSERT INTO `quest_template_locale` VALUES (897, 'deDE', 'Der Ernter', 'Bringt den Ernterkopf zu Jorn Himmelsdeuter.', 'Diese seltsame, insektenartige Kreatur scheint außerhalb der natürlichen Ordnung zu stehen, so als würde sie nicht ins Brachland gehören.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (897, 'esES', 'El Cosechador', 'Llévale la cabeza del Cosechador a Jorn Vidente del Cielo.', 'Esta extraña criatura insectoide parece de un orden natural por fuera, como si no perteneciera a Los Baldíos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (897, 'esMX', 'El Cosechador', 'Llévale la cabeza del Cosechador a Jorn Vidente del Cielo.', 'Esta extraña criatura insectoide parece de un orden natural por fuera, como si no perteneciera a Los Baldíos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (897, 'frFR', 'Le Moissonneur', 'Apportez la Tête du Moissonneur à Jorn Oeil-des-cieux.', 'Cette étrange créature, qui ressemble à un insecte, n\'a pas l\'air de faire partie de l\'ordre naturel, comme si elle n\'appartenait simplement pas aux Tarides.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (897, 'itIT', 'The Harvester', 'Bring the Harvester\'s Head to Jorn Skyseer.', 'This strange, insect-like creature looks outside of the natural order, as though it does not belong in the Barrens.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (897, 'ptBR', 'The Harvester', 'Bring the Harvester\'s Head to Jorn Skyseer.', 'This strange, insect-like creature looks outside of the natural order, as though it does not belong in the Barrens.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (897, 'ruRU', 'Жнец', 'Это странное, похожее на насекомое существо выглядит так, будто попало в Степи случайно.', 'Принесите голову Жнеца Джорну Заклинателю Небес.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (897, 'zhCN', '收割者', '这种奇特的昆虫状生物看起来仿佛不是贫瘠之地的生物，而更像是个天外来物。', '把收割者的头颅交给乔恩·星眼。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (898, 'deDE', 'Free From the Hold', 'Safely escort Gilthares Firebough back to Captain Brightsun in Ratchet.', 'Finally! Someone to rescue me!$b$bI can\'t believe you got past the guards. These zealots from Theramore are out of their minds. They sank our ship and imprisoned me, the only survivor. I was called an enemy and threat to the Alliance.$b$bMe! A threat to their great sham of an Alliance? I was a deckhand on a moonshine transport between Ratchet and Booty Bay.$b$bEnough talk. Help me get back to Ratchet will you? Let me know when you\'re ready and we\'ll make our break.', 'Escort Gilthares Firebough back to Ratchet', '', '', '', '', 'Return to Captain Thalo\'thas Brightsun in The Barrens.', 0);
