INSERT INTO `quest_template_locale` VALUES (1268, 'deDE', 'Verdächtige Hufabdrücke', 'Berichtet Krog in Brackenwall von den verdächtigen Abdrücken beim Gasthaus \"Zur Süßen Ruh\".', 'Reihen von Hufspuren führen von den noch rauchenden Überresten des Gasthauses \"Zur Süßen Ruh\" weg. Wenigstens einige Abdrücke sind klar erkennbar, weitere Abdrücke sind im weichen Schlamm des Sumpfs kaum zu sehen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (1268, 'esES', '[DEPRECATED]Huellas de pezuñas sospechosas', 'Informa a Krog en el Poblado Murohelecho sobre las huellas sospechosas de la Posada Reposo Umbrío.', 'Hay hileras de huellas que se alejan del esqueleto, aún humeante, de la Posada Reposo Umbrío. Hay unas pocas impresiones visibles y algunas hileras de huellas casi imperceptibles en el barro blando del pantano.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (1268, 'esMX', '[DEPRECATED]Huellas de pezuñas sospechosas', 'Informa a Krog en el Poblado Murohelecho sobre las huellas sospechosas de la Posada Reposo Umbrío.', 'Hay hileras de huellas que se alejan del esqueleto, aún humeante, de la Posada Reposo Umbrío. Hay unas pocas impresiones visibles y algunas hileras de huellas casi imperceptibles en el barro blando del pantano.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (1268, 'frFR', '[PÉRIMÉ]Des empreintes de sabots suspectes', 'Faire votre rapport sur les empreintes suspectes de l\'auberge du repos ombragé à Krog, au village de Mur-de-Fougères.', 'Des lignes d\'empreintes de sabots partent des ruines encore fumantes de l\'auberge du Repos ombragé. Il y a au moins quelques traces visibles avec d\'autres empreintes peu marquées dans la boue du marais.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (1268, 'itIT', '[DEPRECATED]Suspicious Hoofprints', 'Report about the suspicious prints at the Shady Rest Inn to Krog in Brackenwall Village.', 'Rows of hoofprints lead away from the still-smoking skeleton of the Shady Rest Inn. There are at least a few visible impressions, with more rows of prints almost imperceptible in the soft mud of the marsh.', '', '', '', '', '', '', 0);
