INSERT INTO `quest_template_locale` VALUES (1520, 'itIT', 'Call of Earth', 'Find Kodo Rock and drink the Earth Sapta.', 'A sapta is a drink created to bind our spirits to the elements. Fused with magic, the potion will allow the open minded to see the elements as no one has seen them before. Your spirit will forever be tied to the element the sapta was created for, and only a select few of our kind know the secrets of its creation.$B$BThe sapta is always imbibed in a holy place, and never anywhere else.$B$BSeek out the shaman shrine to the east of here and drink of the sapta.', '', '', '', '', '', '', 0);
