INSERT INTO `quest_template_locale` VALUES (1520, 'ruRU', 'Зов Земли', 'Найдите Дольмен Кодо и выпейте сапту земли.', 'Сапта – это напиток, призванный объединить твой дух со стихиями. Сотворенное с помощью магии зелье позволит тем, чье сознание открыто, видить стихии так, как их никто не видел ранее. Твой дух будет навеки связан со стихией, для которой был создан сапта – и лишь немногим из нашего племени известен секрет его изготовления. $B$BСапта должна быть выпита в только в священном месте и более нигде. $B$BНайди шаманское святилище к востоку отсюда и выпей сапту там.', '', '', '', '', '', '', 0);
