INSERT INTO `quest_template_locale` VALUES (1560, 'deDE', 'Toogas Bitte', 'Zeigt Tooga den Weg zurück zu seiner Frau Torta.', 'Ich habe mich vollkommen verirrt!$b$bTorta, meine Frau, schickte mich Essen holen, aber ich muss irgendwo falsch abgebogen sein.$b$bWürdet Ihr so freundlich sein, mich zu Torta zurückzuführen? Sie wartet südlich vom Dampfdruckpier auf mich.', 'Führt Tooga zu Torta.', '', '', '', '', 'Kehrt zu Torta am Dampfdruckpier in Tanaris zurück.', 0);
INSERT INTO `quest_template_locale` VALUES (1560, 'esES', 'La misión de Tooga', 'Muéstrale a Tooga el camino de vuelta con su esposa, Thorta.', '¡Estoy totalmente perdido!$B$BMi esposa, Thorta, me envió a buscar la cena y en algún momento debí de coger el camino equivocado.$B$B¿Serías tan amable de llevarme de vuelta con mi esposa Thorta? Ella me está esperando al sur del Puerto Bonvapor.', 'Lleva a Tooga hasta Thorta', '', '', '', '', 'Vuelve con: Thorta. Zona: Puerto Bonvapor, Tanaris.', 0);
INSERT INTO `quest_template_locale` VALUES (1560, 'esMX', 'La misión de Tooga', 'Muéstrale a Tooga el camino de vuelta con su esposa, Thorta.', '¡Estoy totalmente perdido!$B$BMi esposa, Thorta, me envió a buscar la cena y en algún momento debí de coger el camino equivocado.$B$B¿Serías tan amable de llevarme de vuelta con mi esposa Thorta? Ella me está esperando al sur del Puerto Bonvapor.', 'Lleva a Tooga hasta Thorta', 'Lleva a Tooga hasta Thorta', '', '', '', 'Vuelve con: Thorta. Zona: Puerto Bonvapor, Tanaris.', 0);
INSERT INTO `quest_template_locale` VALUES (1560, 'frFR', 'La quête de Tooga', 'Montrer à Tooga le chemin du retour vers sa femme, Torta.', 'Je suis complètement perdu !$b$bMa femme, Torta, m\'a envoyé chercher le dîner et j\'ai dû prendre le mauvais chemin quelque part.$b$bAuriez-vous l\'amabilité de me guider vers Torta ? Elle m\'attend au sud de Port Gentepression.', 'Guider Tooga vers Torta', '', '', '', '', 'Retournez voir Torta au Port Gentepression, en Tanaris.', 0);
INSERT INTO `quest_template_locale` VALUES (1560, 'itIT', 'Tooga\'s Quest', 'Show Tooga the way back to his wife, Torta.', 'I am so lost!$b$bMy wife, Torta, sent me out to fetch dinner and I must have taken a wrong turn somewhere.$b$bWould you be so kind as to lead me back to Torta? She\'s waiting for me just south of Steamwheedle Port.', 'Lead Tooga to Torta', '', '', '', '', 'Return to Torta at Steamwheedle Port in Tanaris.', 0);
