INSERT INTO `quest_template_locale` VALUES (1943, 'esES', 'Habla con Deino', 'Habla con Deino.', 'Deino, la maga trol, necesita tu ayuda, $n. Vive en Orgrimmar, en la Cabaña de Brezo.', '', '', '', '', '', '', 0);
