INSERT INTO `quest_template_locale` VALUES (2784, 'ruRU', 'Попавший в немилость', 'Выслушай мою историю, |3-6($r).', 'Послушайте, как павший герой Орды рассказывает свою историю.', 'Печальная история', 'Вернитесь к павшему герою Орды на Развилку Расщепленного Копья, что на Болоте Печали.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2784, 'zhCN', '失落的荣耀', '请听听我的故事，$r。', '聆听部落英雄的灵魂讲述他的故事。', '悲伤的故事', '去悲伤沼泽断矛路口找部落英雄的灵魂。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2801, 'deDE', 'Eine Geschichte voller Leid', 'Lauscht der Geschichte des gefallenen Helden der Horde.', 'Bevor wir fortfahren können, müsst Ihr meine Geschichte hören.', 'Eine Geschichte voller Elend', '', '', '', '', 'Kehrt zum gefallenen Helden der Horde an der Splitterspeerkreuzung in den Sümpfen des Elends zurück.', 0);
INSERT INTO `quest_template_locale` VALUES (2801, 'esES', 'Una historia triste', 'Escucha la historia del héroe caído de la Horda.', 'Antes de que podamos proseguir, debes oír mi historia.', 'Una historia triste', '', '', '', '', 'Vuelve con: Héroe caído de la Horda. Zona: Cruce Lanzarrota, Pantano de las Penas.', 0);
INSERT INTO `quest_template_locale` VALUES (2801, 'esMX', 'Una historia triste', 'Escucha la historia del héroe caído de la Horda.', 'Antes de que podamos proseguir, debes oír mi historia.', 'Una historia triste', '', '', '', '', 'Vuelve con: Héroe caído de la Horda. Zona: Cruce Lanzarrota, Pantano de las Penas.', 0);
INSERT INTO `quest_template_locale` VALUES (2801, 'frFR', 'Une histoire de chagrin', 'Écoutez le Héros de la Horde tombé au combat raconter son histoire.', 'Avant que nous ne continuions, vous devez écouter mon histoire.', 'Une histoire de chagrin', '', '', '', '', 'Retournez voir le Héros de la Horde tombé au combat au Croisement de Lance-brisée, au Marais des Chagrins.', 0);
INSERT INTO `quest_template_locale` VALUES (2801, 'itIT', 'A Tale of Sorrow', 'Listen to the Fallen Hero of the Horde tell his story.', 'Before we may continue, you must hear my story.', 'A Tale of Sorrow', '', '', '', '', 'Return to Fallen Hero of the Horde at Splinterspear Junction in Swamp of Sorrows.', 0);
INSERT INTO `quest_template_locale` VALUES (2801, 'ptBR', 'A Tale of Sorrow', 'Listen to the Fallen Hero of the Horde tell his story.', 'Before we may continue, you must hear my story.', 'A Tale of Sorrow', '', '', '', '', 'Return to Fallen Hero of the Horde at Splinterspear Junction in Swamp of Sorrows.', 0);
INSERT INTO `quest_template_locale` VALUES (2801, 'ruRU', 'Печальная история', 'Послушайте, как павший герой Орды рассказывает свою историю.', 'Прежде чем мы продолжим, тебе следует услышать мою историю.', 'Печальная история', '', '', '', '', 'Вернитесь к павшему герою Орды на Развилку Расщепленного Копья, что на Болоте Печали.', 0);
INSERT INTO `quest_template_locale` VALUES (2801, 'zhCN', '悲伤的故事', '请你一定要先听听我的故事。', '聆听部落英雄的灵魂讲述他的故事。', '悲伤的故事', '去悲伤沼泽断矛路口找部落英雄的灵魂。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2821, 'deDE', 'Das Zeichen der Qualität', 'Bringt Pratt McGrubben in der Mondfederfeste 10 dicke Yetibälge.', 'Meine Einstellung zur Lederverarbeitung verlangt, dass die Arbeit von bestmöglicher Qualität zu sein hat - ohne Kompromisse. Jeder, der behauptet, Qualität sei unwichtig, wird auch versuchen, Euch ein Küstengrundstück in Dun Morogh zu verkaufen!$B$BAuf meiner jüngsten Suche nach hochwertigem Material fand ich dicke Yetibälge. Die Yetis sind in Feralas zuhause und viele findet man im Wildschrammtal. Ich hätte gern welche, und wenn Ihr mir zehn davon bringt, dürft Ihr Euch ein beliebiges Stück meiner Lederwaren aussuchen. Haltet Ihr das für angemessen? Dann sind wir im Geschäft.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2821, 'esES', 'La marca de la calidad', 'Lleva 10 pellejos de yeti gruesos a Pratt McGrubben al Bastión Plumaluna.', 'Tengo mi filosofía: en peletería, la calidad del trabajo debe ser la mejor posible, sin excusas. Cualquiera que te diga que la calidad no es importante, está intentando venderte un chalé en primera fila de playa en Dun Morogh.$B$BSiempre estoy buscando materiales de primera calidad y los pellejos de yeti son mi gran hallazgo. Los yetis son criaturas de Feralas. Se encuentran muchos en la Vega Cicatriz Feral. Quiero algunos pellejos. Te daré uno de mis artículos de cuero si me traes 10. Si te parece justo, tenemos un trato.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2821, 'esMX', 'La marca de la calidad', 'Lleva 10 pellejos de yeti gruesos a Pratt McGrubben al Bastión Plumaluna.', 'Tengo mi filosofía: en peletería, la calidad del trabajo debe ser la mejor posible, sin excusas. Cualquiera que te diga que la calidad no es importante, está intentando venderte un chalé en primera fila de playa en Dun Morogh.$B$BSiempre estoy buscando materiales de primera calidad y los pellejos de yeti son mi gran hallazgo. Los yetis son criaturas de Feralas. Se encuentran muchos en la Vega Cicatriz Feral. Quiero algunos pellejos. Te daré uno de mis artículos de cuero si me traes 10. Si te parece justo, tenemos un trato.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2821, 'frFR', 'La marque de la qualité', 'Rapporter 10 Peaux de yéti épaisses à Pratt McGrubben au bastion de Pennelune.', 'Ma philosophie, concernant le travail du cuir, c’est que mon travail doit être le meilleur possible : pas de compromis. Quelqu’un disant que la qualité ne compte pas essaye de vous escroquer !$B$BDans ma recherche de matières premières de qualité, rien ne vaut la peau de yéti épaisse. Elle provient des yétis de Féralas, et il y en a beaucoup au val des Griffes farouches. J\'en ai besoin et j’échangerai l\'un de mes objets en cuir contre un ballot de dix peaux. Si cela vous semble équitable, le marché est conclu.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2821, 'itIT', 'The Mark of Quality', 'Bring 10 Thick Yeti Hides to Pratt McGrubben in Feathermoon Stronghold.', 'My philosophy on leatherworking is that the work must be the best quality possible - no compromises.  Anyone who says that quality doesn\'t matter is trying to sell you ocean-front property in Dun Morogh!$B$BIn my latest pursuit of high-quality materials, I have run across thick yeti hides.  The Yeti are native to Feralas, with many found in Feral Scar Vale.  I want some, and I\'ll give you a choice of one of my leather goods for a stack of ten.  If that sounds fair to you, then we\'re in business.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2821, 'koKR', '최고의 품질', '페더문 요새에 있는 프랫 맥그루벤에게 두꺼운 설인 가죽 10개를 가져가야 합니다.', '나의 가죽세공 철학은 가능한 품질이 최상인 제품을 만들어야 한다는 것입니다. 이 점에는 절대 타협이란 있을 수 없지요. 품질은 중요하지 않다고 말하는 자는 던 모로에서 바닷가 별장을 팔려고 하는 자입니다!$B$B얼마 전 고품질 가죽 재료를 찾다가 두꺼운 설인 가죽을 발견하게 되었습니다. 그 설인은 거친흉터 골짜기에 많이 서식하는 페랄라스의 토착민입니다. 그 가죽이 좀 필요한데, 두꺼운 설인 가죽 10개를 가져다주면 내 가죽세공품 중 하나를 주도록 하죠. 조건이 마음에 들면 거래하게 되는 겁니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2821, 'ptBR', 'The Mark of Quality', 'Bring 10 Thick Yeti Hides to Pratt McGrubben in Feathermoon Stronghold.', 'My philosophy on leatherworking is that the work must be the best quality possible - no compromises. Anyone who says that quality doesn\'t matter is trying to sell you ocean-front property in Dun Morogh!$B$BIn my latest pursuit of high-quality materials, I have run across thick yeti hides. The Yeti are native to Feralas, with many found in Feral Scar Vale. I want some, and I\'ll give you a choice of one of my leather goods for a stack of ten. If that sounds fair to you, then we\'re in business.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2821, 'ruRU', 'Знак качества', 'Принесите 10 толстых шкур йети Пратту Макгруббену в Крепость Оперенной Луны.', 'У меня принцип простой: если что делаешь, делай как можно лучше – и никаких компромиссов! А тот, кто станет говорить, будто качество не имеет значения, того гляди, продаст тебе недвижимость в Дун Мороге с видом на море!$b$bИ вот недавно в поисках качественных материалов я обнаружил толстые шкуры йети. Йети – это существа, которые водятся в Фераласе. Особенно много их в Долине Свирепого Утеса. За каждые десять шкур ты получишь одно из моих кожаных изделий. Ну что, по рукам?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2821, 'zhCN', '质量的保证', '给羽月要塞的普拉特·马克格鲁比带去10张厚重的雪人皮。', '我对于皮革制作的哲学是要做就做最好——没有商量余地。任何说质量不要紧的人都是想要把丹莫罗的那些水货卖给你！$B$B在寻求高质量材料的过程中，我接触到了厚重的雪人皮。雪人是菲拉斯土生土长的一种生物，大多在深痕谷活动。我需要一些它们的皮，如果你给我十张的话，我可以让你在我的皮货中选一件。如果你觉得听起来还算公平，那我们就成交了吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2821, 'zhTW', '品質的保證', '給羽月要塞的普拉特·馬克格魯比帶去10張厚重的雪人毛皮。', '我對於皮革製作的哲學是要做就做最好─沒有商量餘地。任何說品質不要緊的人都是想要把丹莫洛的那些水貨賣給你!$B$B在尋求高品質材料的過程中，我接觸到了厚重的雪人毛皮。雪人是菲拉斯土生土長的一種生物，大多在深痕谷活動。我需要一些它們的皮，如果你給我十張的話，我可以讓你在我的皮貨中選一件。如果你覺得聽起來還算公平，那我們就成交了吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2822, 'deDE', 'Das Zeichen der Qualität', 'Bringt Jangdor Flinkschreiter im Camp Mojache 10 dicke Yetibälge.', 'Ich bin stolz auf meine Lederarbeiten und ich glaube, das sieht man ihnen an; Qualität sollte niemals zweitrangig sein. Jeder, der behauptet, Qualität sei unwichtig, wird Euch wahrscheinlich auch glauben machen wollen, dass die Erde keine Geheimnisse enthält!$B$BAuf meiner jüngsten Suche nach hochwertigem Material habe ich dicke Yetibälge entdeckt. Die Yetis sind in Feralas beheimatet und man findet sie häufig westlich des Wildschrammtals. Ihr dürft Euch ein beliebiges Stück meiner Lederwaren aussuchen für einen Zehnerstapel Yetibälge. Wenn das für Euch akzeptabel ist, dann sind wir im Geschäft!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2822, 'esES', 'La marca de la calidad', 'Lleva 10 pellejos de yeti gruesos a Jangdor Zanco Veloz al Campamento Mojache.', 'Me enorgullezco de mi trabajo de peletería y creo que jamás se debería renunciar a la calidad. Cualquiera que te diga que la calidad no es importante, ¡intentará también convencerte de que la tierra no tiene secretos!$B$BSiempre estoy buscando materiales de primera calidad y en mi búsqueda he descubierto los pellejos de yeti. Los yetis son nativos de Feralas y a menudo se encuentran al oeste de aquí, en la Vega Cicatriz Feral. Te dejaré elegir uno de mis artículos de cuero si me traes 10 pellejos. Si te parece una buena oferta, ¡tenemos un trato!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2822, 'esMX', 'La marca de la calidad', 'Lleva 10 pellejos de yeti gruesos a Jangdor Zanco Veloz al Campamento Mojache.', 'Me enorgullezco de mi trabajo de peletería y creo que jamás se debería renunciar a la calidad. Cualquiera que te diga que la calidad no es importante, ¡intentará también convencerte de que la tierra no tiene secretos!$B$BSiempre estoy buscando materiales de primera calidad y en mi búsqueda he descubierto los pellejos de yeti. Los yetis son nativos de Feralas y a menudo se encuentran al oeste de aquí, en la Vega Cicatriz Feral. Te dejaré elegir uno de mis artículos de cuero si me traes 10 pellejos. Si te parece una buena oferta, ¡tenemos un trato!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2822, 'frFR', 'La marque de la qualité', 'Apporter 10 Peaux de yéti épaisses à Jangdor Rôdeur-agile au Camp Mojache.', 'Je suis fier de mon travail et je sais que cela se voit : la qualité ne doit jamais être dévalorisée. Quiconque dit que la qualité n’est pas primordiale ferait également croire que les abysses sont lumineux.$B$BDans ma recherche de matières premières de qualité, rien ne vaut la peau de yéti épaisse. Elle provient des yétis de Féralas, et il y en a beaucoup au val des Griffes farouches, à l\'ouest d\'ici. Je vous laisserai choisir un objet parmi mes créations contre un ballot de dix peaux. Si cela vous convient, nous faisons affaire !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2822, 'itIT', 'The Mark of Quality', 'Bring 10 Thick Yeti Hides to Jangdor Swiftstrider in Camp Mojache.', 'I take pride in my leather work, and I feel it shows; quality should never be compromised.  Anyone who says that quality doesn\'t matter would also have you believe that the earth does not keep secrets!$B$BIn my latest pursuit of high-quality materials, I have discovered thick yeti hides.  Yetis are native to Feralas, often found west of here in Feral Scar Vale.  I\'ll give you a choice of one of my leather goods for a stack of ten.  If that is agreeable to you, then we are set!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2822, 'ptBR', 'The Mark of Quality', 'Bring 10 Thick Yeti Hides to Jangdor Swiftstrider in Camp Mojache.', 'I take pride in my leather work, and I feel it shows; quality should never be compromised. Anyone who says that quality doesn\'t matter would also have you believe that the earth does not keep secrets!$B$BIn my latest pursuit of high-quality materials, I have discovered thick yeti hides. Yetis are native to Feralas, often found west of here in Feral Scar Vale. I\'ll give you a choice of one of my leather goods for a stack of ten. If that is agreeable to you, then we are set!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2822, 'ruRU', 'Знак качества', 'Принесите 10 толстых шкур йети Джангору Быстрому Страннику в Лагерь Мохаче.', 'Я горжусь своим мастерством. Мои вещи сразу видно! А тот, кто станет говорить, будто качество не имеет значения, пожалуй, захочет убедить тебя и в том, будто земля не хранит тайн!$b$bИ вот недавно в поисках качественных материалов я обнаружил толстые шкуры йети. Йети – это существа, которые водятся в Фераласе. Особенно много их к западу отсюда, в Долине Свирепого Утеса. За каждые десять шкур ты получишь одно из моих кожаных изделий. Ну что, по рукам?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2822, 'zhCN', '质量的保证', '我为自己制作的皮革作品感到骄傲，它们也值得我骄傲，质量是永不可能妥协的东西。任何说质量不要紧的人都是在信口乱说！$B$B在寻求高质量材料的过程中，我接触到了厚重的雪人皮。雪人是菲拉斯土生土长的一种生物，大多在深痕谷活动。我需要一些它们的皮，如果你给我十张的话，我可以让你在我的皮货中选一件。如果你觉得听起来还算公平，那我们就成交了吧。', '给莫沙彻营地的杉多尔·迅蹄带去十张厚重的雪人皮。', '', '去菲拉斯找莫沙彻营地的杉多尔·迅蹄。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2841, 'deDE', 'Maschinenkriege', 'Besorgt die Maschinenblaupausen und Thermadrahts Safekombination aus Gnomeregan und bringt sie zu Nogg nach Orgrimmar.', 'Vor zwei Jahren noch hat man sich jedes Mal darüber lustig gemacht, wenn die Rede auf Gnomentechnologie kam. Diese Zeiten haben sich definitiv geändert. Jetzt haben sie eine neue Maschine gebaut, die besser ist als all unsere Schreddermodelle zusammen. Ich brauche die Blaupausen dafür, $n!$B$BDummerweise sind die Pläne in einem Safe bei Robogenieur Thermadraht sicher verwahrt. Was die Sache noch erschwert: Nur Robogenieur Thermadraht kennt die Kombination zu diesem Safe.$B$BTut, was Ihr tun müsst, um diese Kombination zu erlangen, $n. Dann öffnet den Safe und bringt mir diese Blaupausen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2841, 'esES', 'Las guerras de la plataforma', 'Consigue la combinación de la caja fuerte de Termochufe en Gnomeregan y lleva los planos de la plataforma a Nogg a Orgrimmar.|n', 'Hasta hace dos años, la tecnología gnómica solo servía para echarse unas risas. Las cosas han cambiado mucho; han construido una plataforma mejor que todas las nuestras juntas. ¡Quiero los planos, $n!$B$BEstán en una caja fuerte cerca del mekigeniero Termochufe. Para empeorar las cosas, él es el único que sabe la combinación.$B$BHaz lo que sea por conseguirla, $n. ¡Tienes que abrir la caja y traerme los planos!|n', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2841, 'esMX', 'Las guerras de la plataforma', 'Consigue la combinación de la caja fuerte de Termochufe en Gnomeregan y lleva los planos de la plataforma a Nogg a Orgrimmar.|n', 'Hasta hace dos años, la tecnología gnómica solo servía para echarse unas risas. Las cosas han cambiado mucho; han construido una plataforma mejor que todas las nuestras juntas. ¡Quiero los planos, $n!$B$BEstán en una caja fuerte cerca del mekigeniero Termochufe. Para empeorar las cosas, él es el único que sabe la combinación.$B$BHaz lo que sea por conseguirla, $n. ¡Tienes que abrir la caja y traerme los planos!|n', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2841, 'frFR', 'Guerre des plates-formes', 'Obtenez la Combinaison du coffre de Thermojoncteur, prenez les Plans de la plateforme à Gnomeregan et apportez-les à Nogg, à Orgrimmar.', 'Technologie gnome… Ce terme était autrefois brandi comme un slogan. Les temps ont changé. Ils ont désormais une nouvelle plateforme, supérieure à tous nos modèles de déchiqueteurs combinés. J\'ai besoin des plans de cette plateforme, $n !$B$BMalheureusement ceux-ci sont enfermés dans le coffre du mekgénieur Thermojoncteur. Et pour compliquer les choses, la combinaison du coffre n\'est connue que de lui seul.$B$BFaites ce qu’il faut pour obtenir cette combinaison, $n. Puis ouvrez le coffre et rapportez-moi ces plans !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2841, 'itIT', 'Rig Wars', 'Retrieve the Rig Blueprints and Thermaplugg\'s Safe Combination from Gnomeregan and bring them to Nogg in Orgrimmar.', 'Gnome technology - two years ago its most common use was as a punch line. Times certainly have changed. Now they\'ve got a new rig built that\'s better than all our shredder models combined. I need the blueprints, $n!$B$BUnfortunately, the plans are locked away in a safe near Mekgineer Thermaplugg. To make matters worse, the combination to the safe is only known by Mekgineer Thermaplugg.$B$BDo whatever you have to do to get that combination, $n. Then crack open that safe and bring back those blueprints!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2841, 'koKR', '기술 전쟁', '놈리건에서 장치 설계도와 텔마플러그의 금고 암호를 찾아서 오그리마에 있는 노그에게 가져가야 합니다.', '노움의 기술이라... 2년 전만 해도 농담으로나 하던 소리였는데... 시대가 많이 변했죠. 이제 노움은 우리 벌목기를 다 모은 것보다 더 좋은 장비를 가지고 있어요. 녀석들의 설계도가 필요해요!$B$B유감스럽게 그 설계도는 금고에 보관되어 있는데 기계박사 텔마플러그가 항상 근처를 지키고 있어요. 게다가 그만이 금고 암호를 알고 있답니다.$B$B무슨 수를 써서라도 금고 번호를 알아내세요. 번호를 알아내면 금고를 열고 설계도를 가져오세요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2841, 'ptBR', 'Rig Wars', 'Retrieve the Rig Blueprints and Thermaplugg\'s Safe Combination from Gnomeregan and bring them to Nogg in Orgrimmar.', 'Gnome technology - two years ago its most common use was as a punch line. Times certainly have changed. Now they\'ve got a new rig built that\'s better than all our shredder models combined. I need the blueprints, $n!$B$BUnfortunately, the plans are locked away in a safe near Mekgineer Thermaplugg. To make matters worse, the combination to the safe is only known by Mekgineer Thermaplugg.$B$BDo whatever you have to do to get that combination, $n. Then crack open that safe and bring back those blueprints!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2841, 'ruRU', 'Техновойны', 'Добудьте в Гномрегане чертежи боевой машины и код от сейфа Термоштепселя в Гномрегане и принесите их Ноггу в Оргриммар.', 'О-о, гномьи технологии! Еще пару лет назад они использовались в основном в анекдотах. Да уж, времена изменились! Теперь они выстроили такую машину, которая лучше, чем все наши крошшеры вместе взятые! Мне нужны ее чертежи, $n!$b$bУвы, эти бумаги хранятся в сейфе Анжинера Термоштепселя. А что хуже всего – код от сейфа известен только самому Термоштепселю!$b$bДелай что хочешь, $n, но добудь этот код, открой сейф и достань оттуда эти чертежи!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2841, 'zhCN', '设备之战', '从诺莫瑞根拿到钻探设备蓝图和麦克尼尔的保险箱密码，把它们交给奥格瑞玛的诺格。', '侏儒的技术——两年前它最普遍的用途是被当作笑话，但是时代确实变了，现在他们建造的新型钻探设备比我们所有伐木机加起来都要精密。我需要它的设计图，$n！$B$B不幸的是，这些设计图被锁在机械师瑟玛普拉格身边的一只保险箱里了。更糟的是，保险箱的密码只有机械师瑟玛普拉格一人知道。$B$B尽你所能得到那个密码，$n。把设计图给我带回来！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2841, 'zhTW', '設備之戰', '從諾姆瑞根拿到鑽探設備藍圖和麥克尼爾的保險箱密碼，把它們交給奧格瑪的諾格。', '地精的技術─兩年前它最普遍的用途是被當作笑話，但是時代確實變了，現在他們建造的新型鑽探設備比我們所有伐木機加起來都要精密。我需要它的設計圖，$n!$B$B不幸的是，這些設計圖被鎖在機電師瑟瑪普拉格身邊的一隻保險箱裡了。更糟的是，保險箱的密碼只有機電師瑟瑪普拉格一人知道。$B$B盡你所能得到那個密碼，$n。把設計圖給我帶回來!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2842, 'deDE', 'Chefingenieur Scooty', 'Sprecht mit Scooty in Beutebucht.', 'In dem Moment, als wir durch Kernobee von der neuen Gnomenmaschine hörten, haben Scooty und ich mit der Arbeit am Transpolyporter 6000 begonnen: dem schnellsten und verlässlichsten Weg nach Gnomeregan.$B$BEndlich ist er bereit, Passagiere aufzunehmen, $n. Wenn Ihr es einmal versuchen wollt, geht nach Beutebucht und redet mit Scooty am Windreiterhorst. Um nach Beutebucht zu gelangen, nehmt das Boot von Ratschet aus.$B$BNatürlich könnt Ihr auch versuchen, Euch durch die von Zwergen verseuchten Berge von Dun Morogh nach Gnomeregan durchzuschlagen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2842, 'esES', 'Scooty, ingeniero jefe', 'Habla con Scooty en Bahía del Botín.', 'En cuanto Kernobee nos avisó de lo de la plataforma gnómica, Scooty y yo empezamos a trabajar en el Transpoliportador 6000. Es el medio más seguro y rápido para ir a Gnomeregan.$B$BYa está a punto para despegar. Si quieres probarlo, $n, ve a Bahía del Botín y habla con Scooty en la alcándara de jinete de Viento. Para ir a Bahía del Botín tienes que coger el barco en Trinquete.$B$BPor supuesto, puedes ir a Gnomeregan cruzando las montañas de Dun Morogh, que están infestadas de enanos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2842, 'esMX', 'Scooty, ingeniero jefe', 'Habla con Scooty en Bahía del Botín.', 'En cuanto Kernobee nos avisó de lo de la plataforma gnómica, Scooty y yo empezamos a trabajar en el Transpoliportador 6000. Es el medio más seguro y rápido para ir a Gnomeregan.$B$BYa está a punto para despegar. Si quieres probarlo, $n, ve a Bahía del Botín y habla con Scooty en la alcándara de jinete de Viento. Para ir a Bahía del Botín tienes que coger el barco en Trinquete.$B$BPor supuesto, puedes ir a Gnomeregan cruzando las montañas de Dun Morogh, que están infestadas de enanos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2842, 'frFR', 'L\'ingénieur en chef Scooty', 'Parler à Scooty à Baie-du-Butin.', 'Dès que nous aurons des nouvelles de Kernobee à propos de la nouvelle plateforme des gnomes, Scooty et moi commencerons à travailler sur le Transpolyporteur 6000 : le moyen le plus rapide et le plus sûr d’aller à Gnomeregan.$B$BIl est enfin prêt à recevoir des passagers, $n. Si vous désirez l\'essayer, allez à Baie-du-Butin et parlez avec Scooty au perchoir des coursiers du vent. Pour aller à Baie-du-Butin, prenez le bateau depuis Cabestan.$B$BVous pouvez aussi traverser les montagnes de Dun Morogh, mais elles sont infestées de nains…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2842, 'itIT', 'Chief Engineer Scooty', 'Speak with Scooty in Booty Bay.', 'As soon as we got word from Kernobee about the new gnomish rig, Scooty and I began work on the Transpolyporter 6000: The fastest and most reliable way to Gnomeregan.$B$BIt\'s finally ready for passengers to board, $n. If you want to give it a try, go to Booty Bay and talk with Scooty at the wind rider roost. To get to Booty Bay, take the boat from Ratchet.$B$BOf course, you could always try and make the run to Gnomeregan through the dwarf infested mountains of Dun Morogh!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2842, 'koKR', '선임기술자 스쿠티', '무법항에 있는 스쿠티와 대화해야 합니다.', '케르노비에게서 새 노움 장치에 대한 소식을 듣자마자 스쿠티와 나는 순간이동기 6000을 만들기 시작했습니다. 순간이동기 6000은 가장 빠르고 안전하게 놈리건에 가는 방법이랍니다.$B$B드디어 승객들을 태울 준비가 됐군요. 한번 타보고 싶으시다면 무법항으로 가서 와이번 횃대에 있는 스쿠티와 얘기를 나눠보시기 바랍니다. 무법항으로 가려면 톱니항에서 배를 타야 한답니다.$B$B물론 드워프가 득실거리는 던 모로의 산맥을 지나서 놈리건까지 뛰어가도 상관없지만요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2842, 'ptBR', 'Chief Engineer Scooty', 'Speak with Scooty in Booty Bay.', 'As soon as we got word from Kernobee about the new gnomish rig, Scooty and I began work on the Transpolyporter 6000: The fastest and most reliable way to Gnomeregan.$B$BIt\'s finally ready for passengers to board, $n. If you want to give it a try, go to Booty Bay and talk with Scooty at the wind rider roost. To get to Booty Bay, take the boat from Ratchet.$B$BOf course, you could always try and make the run to Gnomeregan through the dwarf infested mountains of Dun Morogh!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2842, 'ruRU', 'Главный инженер Скути', 'Поговорите со Скути в Пиратской Бухте.', 'Как только Керноби сообщил нам о новой гномской машине, мы со Скути принялись строить \"Транстелепорт-6000\" – лучший способ добраться до Гномрегана.$b$bОн уже готов, $n. Если хочешь его испытать, ступай в Пиратскую Бухту и поговори со Скути у Гнезда Ветрокрылов. Из Кабестана туда можно добраться на лодке.$b$bНет, разумеется, ты можешь попробовать добраться до Гномрегана через горы Дун Морог, кишащие дворфами!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2842, 'zhCN', '主工程师斯库提', '和藏宝海湾的斯库提谈一谈。', '斯库提和我刚刚从克努比那里得到关于新型侏儒钻探设备的消息，就马上开始动手研制捷运器6000型了，它是去诺莫瑞根最快捷可靠的方法！$B$B现在它已经完成了，$n。如果你想试试的话，就到藏宝海湾去和空运处的斯库提谈谈吧。你可以从棘齿城乘船去藏宝海湾。$B$B当然，你也可以试试从满地都是矮人的丹莫罗山脉走到诺莫瑞根去！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2842, 'zhTW', '首席工程師斯庫提', '和藏寶海灣的斯庫提談話。', '斯庫提和我剛剛從克努比那裡得到關於新型地精鑽探設備的消息，就馬上開始動手研製捷運器6000型了，它是去諾姆瑞根最快捷可靠的方法!$B$B現在它已經完成了，$n。如果你想試試的話，就到藏寶海灣去和雙足飛龍棲息處的斯庫提談談吧。你可以從棘齒城乘船去藏寶海灣。$B$B當然，你也可以試試從滿地都是矮人的丹莫洛山脈走到諾姆瑞根去!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2843, 'deDE', 'Gnomer-weeeeg!', 'Wartet, bis Scooty den Goblintransponder kalibriert hat.', 'Solange Ihr den Transponder bei Euch habt, müsst Ihr lediglich auf diese Plattform hier treten und Ihr werdet nach Gnomeregan teleportiert.$B$BIhr werdet vielleicht ein paar ungewöhnliche Nebenwirkungen erleben, aber keine davon sollte dauerhaft sein... zumindest nicht mehr.$B$BIch hätte fast den wichtigsten Teil vergessen - die Rückkehr. Wenn Ihr wieder zurückkommen wollt, tretet einfach auf die Transpolyporter-Plattform in Gnomeregan!$B$BJetzt lasst uns diesen Transponder auf Euer Gewicht und Eure Größe einstellen.', 'Goblintransponder', 'Goblintransponder', '', '', '', 'Kehrt zu Scooty in Beutebucht im Schlingendorntal zurück.', 0);
INSERT INTO `quest_template_locale` VALUES (2843, 'esES', '¡Gnomer-yaaaaa!', 'Espera a que Scooty calibre el transpondedor goblin.', 'Mientras tengas el transpondedor, todo lo que necesitas hacer es colocarte sobre la plataforma y serás $gteletransportado:teletransportada; a Gnomeregan.$B$BQuizás experimentes algunos extraños efectos secundarios, pero ninguno será permanente... al menos ahora.$B$BCasi me olvido de lo más importante, la vuelta. Cuando quieras volver, colócate en la plataforma del Transpoliportador en Gnomeregan.$B$BTienes que calibrar el transpondedor según tu talla y peso.|n', 'El transpondedor goblin', 'El transpondedor goblin', '', '', '', 'Vuelve con: Scooty. Zona: Bahía del Botín, Vega de Tuercespina.', 0);
INSERT INTO `quest_template_locale` VALUES (2843, 'esMX', '¡Gnomer-yaaaaa!', 'Espera a que Scooty calibre el transpondedor goblin.', 'Mientras tengas el transpondedor, todo lo que necesitas hacer es colocarte sobre la plataforma y serás $gteletransportado:teletransportada; a Gnomeregan.$B$BQuizás experimentes algunos extraños efectos secundarios, pero ninguno será permanente... al menos ahora.$B$BCasi me olvido de lo más importante, la vuelta. Cuando quieras volver, colócate en la plataforma del Transpoliportador en Gnomeregan.$B$BTienes que calibrar el transpondedor según tu talla y peso.|n', 'El transpondedor goblin', 'El transpondedor goblin', '', '', '', 'Vuelve con: Scooty. Zona: Bahía del Botín, Vega de Tuercespina.', 0);
INSERT INTO `quest_template_locale` VALUES (2843, 'frFR', 'Gnomer-paaarti !', 'Attendre que Scooty calibre le Transpondeur gobelin.', 'Aussi longtemps que vous êtes en possession du transpondeur gobelin, tout ce que vous avez à faire est de traverser le portail et vous vous retrouverez à Gnomeregan.$B$BIl y aura peut-être des effets secondaires bizarres, mais aucun ne devrait être permanent... plus maintenant.$B$BJ\'oubliais de parler du plus important : le retour. Lorsque vous serez $gprêt:prête; à revenir, allez au portail de multitéléportation de Gnomeregan !$B$BBon, il faut maintenant régler le transpondeur selon votre poids et votre taille.', 'Transpondeur gobelin', '', '', '', '', 'Retournez voir Scooty à Baie-du-Butin, dans la Vallée de Strangleronce.', 0);
INSERT INTO `quest_template_locale` VALUES (2843, 'itIT', 'Gnomer-gooooone!', 'Wait for Scooty to calibrate the Goblin Transponder.', 'As long as you have the transponder in your possession, all you need to do is step on the pad and you\'ll be teleported to Gnomeregan.$B$BYou may experience some unusual side effects, but none of those should be permanent... anymore.$B$BI almost forgot the most important part - getting back. When you\'re ready to come back, just step on the transpolyporter pad in Gnomeregan!$B$BNow to calibrate the transponder to your size and weight.', 'Goblin Transponder', 'Goblin Transponder', '', '', '', 'Return to Scooty at Booty Bay in Stranglethorn Vale.', 0);
INSERT INTO `quest_template_locale` VALUES (2843, 'ptBR', 'Gnomer-gooooone!', 'Wait for Scooty to calibrate the Goblin Transponder.', 'As long as you have the transponder in your possession, all you need to do is step on the pad and you\'ll be teleported to Gnomeregan.$B$BYou may experience some unusual side effects, but none of those should be permanent... anymore.$B$BI almost forgot the most important part - getting back. When you\'re ready to come back, just step on the transpolyporter pad in Gnomeregan!$B$BNow to calibrate the transponder to your size and weight.', 'Goblin Transponder', 'Goblin Transponder', '', '', '', 'Return to Scooty at Booty Bay in Stranglethorn Vale.', 0);
INSERT INTO `quest_template_locale` VALUES (2843, 'ruRU', 'Поехалиии!', 'Пока импульсный повторитель у тебя с собой, стоит только ступить на площадку, и перенесешься в Гномреган.$B$BМожет быть, ощущения будут необычные, но ни одно из них не останется навсегда... Теперь.$B$BЧуть не забыл самое главное – как вернуться назад. Когда ты решишь уносить ноги, поднимись на площадку транстелепорта в Гномрегане!$B$BА теперь я настрою импульсный повторитель на твой рост и вес.', 'Дождитесь, пока Скути настроит гоблинский импульсный повторитель.', 'Гоблинский импульсный повторитель', 'Вернитесь к Скути в Пиратскую Бухту, что в Тернистой долине.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2843, 'zhCN', '出发！诺莫瑞根！', '只要你有传送器，你所要做的就只是踩上踏板，然后就会被传送到诺莫瑞根！$B$B你可能会有一些不适感，但这些副作用应该都不是永久的……$B$B我几乎忘了最重要的部分——如何回来。当你准备好要回来时，只要在诺莫瑞根踩上传送器的踏板就行了！$B$B现在请把传送器按照你的体型和体重做一些调整。', '等斯库提调整好地精传送器。', '地精传送器', '去荆棘谷找藏宝海湾的斯库提。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2844, 'deDE', 'Der Riesenwächter', 'Findet das verlorene Nachtelfenmädchen.', 'Ich bin so unfähig, $n. Ich sollt\' auf meine kleine Freundin, Shay, aufpassen, aber irgendwie ist sie mir verlor\'n gegangen. Ich kann nicht genau sagen, was passiert ist, ehrlich, aber als ich sie das letzte Mal gesehen habe, lief\'n wir durch die Ruinen direkt nördlich von hier.$B$BAlles war wunderbar, aber dann hab ich mich umgedreht, un\'... sie war weg.$B$BUnd jetz\' hab ich keine Ahnung, was ich tun soll. Habt Ihr \'ne Idee, $n? Ich mache mir furchtbare Sorgen.$B$BHey, wie wär\'s, wenn Ihr nach Shay suchen würdet? Eure Beine sind viel schneller als meine.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2844, 'esES', 'El guardián gigante', 'Localiza a la elfa de la noche perdida.', 'No valgo para nada, $n. Se supone que tenía que vigilar a mi amiguita Shay y, no sé cómo, se me perdió. No sé muy bien qué ocurrió, pero la última vez que la vi estábamos andando por las ruinas que hay al norte de aquí.$B$BIba todo bien. Y de repente, me giro... ¡y ella ya no estaba!$B$BNo sé qué hacer... ¿Alguna idea, $n? Estoy terriblemente preocupado.$B$B¡Espera! ¡Tú puedes ir a buscar a Shay! Con tus piernas, irás más rápido que yo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2844, 'esMX', 'El guardián gigante', 'Localiza a la elfa de la noche perdida.', 'No valgo para nada, $n. Se supone que tenía que vigilar a mi amiguita Shay y, no sé cómo, se me perdió. No sé muy bien qué ocurrió, pero la última vez que la vi estábamos andando por las ruinas que hay al norte de aquí.$B$BIba todo bien. Y de repente, me giro... ¡y ella ya no estaba!$B$BNo sé qué hacer... ¿Alguna idea, $n? Estoy terriblemente preocupado.$B$B¡Espera! ¡Tú puedes ir a buscar a Shay! Con tus piernas, irás más rápido que yo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2844, 'frFR', 'Le garde géant', 'Retrouver la jeune elfe de la nuit perdue.', 'Je suis vraiment bon à rien, $n. J\'étais supposé surveiller mon amie Shay, et je l’ai perdue. Je ne suis pas sûr de ce qui s\'est passé, mais la dernière fois que je l\'ai vue, nous étions en train de marcher dans les ruines au nord d\'ici.$B$BTout allait bien, mais lorsque je me suis retourné vers elle... elle avait disparu.$B$BMaintenant je ne sais plus quoi faire. Vous avez une idée, $n ? Je suis affreusement inquiet.$B$BEt si vous alliez la chercher ? Vos jambes vous permettent d\'aller plus vite que moi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2844, 'itIT', 'The Giant Guardian', 'Locate the lost night elf girl.', 'I am no good, $n. I was \'spose to be watchin\' over my little friend, Shay, and somehow, she got away from me. I\'m not all that sure what happened, really, but last time I saw her, we was walkin\' through the ruins just north of here.$B$BEverythin\' was goin\' fine, but then I turned around, and... she was gone.$B$BNow I jus can\'t quite figure out what I should do here. Got any ideas, $n? I\'m awful worried.$B$BHey, how \'bout you go look fer Shay? Yer legs can travel much faster \'n mine.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2844, 'koKR', '거대한 수호자', '길 잃은 나이트 엘프 소녀를 찾아야 합니다.', '난 쓸모없다. 내 친구 샤이를 지켜 주기로 했었는데 샤이가 날 떠나버렸다. 무슨 일이 일어난 건지 모르겠다. 샤이를 마지막으로 본 건 우리가 북쪽에 있는 폐허를 지나고 있을 때였다. 모든 게 다 괜찮았는데 뒤돌아보니까... 샤이가 없었다.$B$B이제 뭘 어떡해야 할지 모르겠다. 무슨 좋은 방법 없을까? 샤이가 걱정된다.$B$B샤이를 좀 찾아봐 줬으면 한다. 너는 나보다 훨씬 더 빨리 걸어다닐 수 있을 거다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2844, 'ptBR', 'The Giant Guardian', 'Locate the lost night elf girl.', 'I am no good, $n. I was \'spose to be watchin\' over my little friend, Shay, and somehow, she got away from me. I\'m not all that sure what happened, really, but last time I saw her, we was walkin\' through the ruins just north of here.$B$BEverythin\' was goin\' fine, but then I turned around, and... she was gone.$B$BNow I jus can\'t quite figure out what I should do here. Got any ideas, $n? I\'m awful worried.$B$BHey, how \'bout you go look fer Shay? Yer legs can travel much faster \'n mine.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2844, 'ruRU', 'Великан-опекун', 'Нет от меня никакого толку, $n! Я должен был стеречь мою маленькую подружку, Шаю, а она взяла и убежала! Я даже не знаю, как это вышло: в последний раз, как я ее видел, мы шли через развалины к северу отсюда.$b$bВсе было хорошо, а потом я поворачиваюсь, а ее нет!$b$bИ теперь я даже и не знаю, чего мне делать-то. Что мне делать, $n? Я так переживаю!$b$bСлушай, а может, ты сходишь ее поищешь? У тебя-то ноги куда резвее моих!', 'Найдите потерявшуюся ночную эльфийку.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2844, 'zhCN', '大保姆', '寻找走丢了的精灵小姑娘。', '都是我不好，$n。我本应细心照顾我的小朋友，沙恩。但是不知道为什么，她离开了我。我并不十分清楚发生了什么事，真的，但我最后一次看到她的时候，我们正在穿越这里北面的废墟。$B$B一切都很正常，但我转过身一看……她就不见了。$B$B现在我不知道该怎么办了。你有办法吗，$n？我很担心。$B$B嘿，你帮我去找沙恩怎么样？你看上去应该比我跑得快一些。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2844, 'zhTW', '大保姆', '尋找走丟了的精靈小姑娘。', '都是我不好，$n。我本應細心照顧我的小朋友，沙恩。但是不知道為什麼，她離開了我。我並不十分清楚發生了什麼事，真的，但我最後一次看到她的時候，我們正在穿越這裡北面的廢墟。$B$B一切都很正常，但我轉過身一看...她就不見了。$B$B現在我不知道該怎麼辦了。你有辦法嗎，$n?我很擔心。$B$B嘿，你幫我去找沙恩怎麼樣?你看起來應該比我跑得快一些。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2845, 'deDE', 'Streunende Shay', 'Nehmt Shays Glocke aus der Truhe.$B$BBegleitet Shay Laubschleicher zu Felsbeißers Lager.', 'Glaubt Ihr, Ihr könnt mir helfen, meinen Weg zurück zu meinem Freund, Felsbeißer, zu finden? Er sollte auf mich aufpassen... Aber ich neige dazu, gelegentlich davonzuschlendern. Es gibt immer so viele Dinge zu sehen und ich lasse mich dann ablenken.$B$BEs gibt allerdings etwas, was immer meine Aufmerksamkeit erregt.$B$BIn dieser Truhe findet Ihr eine Glocke, die mir sehr teuer ist. Ich liebe einfach ihren Ton. Ihr habt mein Wort: Wenn ich davonschlendere, müsst Ihr lediglich damit klingeln und ich komme angelaufen.', 'Bringt Shay Laubschleicher zu Felsbeißers Lager', 'Bringt Shay Laubschleicher zu Felsbeißers Lager', '', '', '', 'Kehrt zu Felsbeißer bei den Zwillingskolossen in Feralas zurück.', 0);
INSERT INTO `quest_template_locale` VALUES (2845, 'esES', 'Las andanzas de Shay', 'Coge la campana de Shay del cofre.$B$BEscolta a Shay Correhoja al campamento de Muerdepiedras.', '¿Puedes ayudarme a encontrar a mi amigo Muerdepiedras? Él me estaba cuidando... Pero tengo la mala costumbre de perderme de vez en cuando. Hay tantas cosas que ver que me distraigo y me pierdo por ahí.$B$BPero hay algo que siempre consigue llamar mi atención.$B$BDentro de este cofre encontrarás una campana que para mí es muy valiosa. Me encanta cómo suena. Te prometo que si me alejo y tú tocas la campana, volveré corriendo.', 'Lleva a Shay Correhoja al campamento de Muerdepiedras', 'Lleva a Shay Correhoja al campamento de Muerdepiedras', '', '', '', 'Vuelve con: Muerdepiedras. Zona: Los Dos Colosos, Feralas.', 0);
INSERT INTO `quest_template_locale` VALUES (2845, 'esMX', 'Las andanzas de Shay', 'Coge la campana de Shay del cofre.$B$BEscolta a Shay Correhoja al campamento de Muerdepiedras.', '¿Puedes ayudarme a encontrar a mi amigo Muerdepiedras? Él me estaba cuidando... Pero tengo la mala costumbre de perderme de vez en cuando. Hay tantas cosas que ver que me distraigo y me pierdo por ahí.$B$BPero hay algo que siempre consigue llamar mi atención.$B$BDentro de este cofre encontrarás una campana que para mí es muy valiosa. Me encanta cómo suena. Te prometo que si me alejo y tú tocas la campana, volveré corriendo.', 'Lleva a Shay Correhoja al campamento de Muerdepiedras', 'Lleva a Shay Correhoja al campamento de Muerdepiedras', '', '', '', 'Vuelve con: Muerdepiedras. Zona: Los Dos Colosos, Feralas.', 0);
INSERT INTO `quest_template_locale` VALUES (2845, 'frFR', 'Shay l\'égarée', 'Prendre la Cloche de Shay dans le coffre.$B$BEscorter Shay Coursefeuille au Camp de Croque-roc.', 'Pouvez-vous m\'aider à retrouver mon chemin jusqu\'à mon ami, Croque-roc ? Il était supposé me surveiller… Mais j\'ai tendance à folâtrer de-ci de-là. Il y a tant de choses à voir et je me laisse facilement distraire.$B$BIl y a toutefois une chose qui me ramène toujours.$B$BDans ce coffre, vous trouverez une cloche qui m\'est précieuse. J\'adore sa sonorité. Si je m\'égare, vous n\'avez qu\'à la faire sonner, je reviendrai immédiatement.', 'Ramener Shay au Camp de Croque-roc', '', '', '', '', 'Retournez voir Croque-roc aux Colosses jumeaux, en Féralas.', 0);
INSERT INTO `quest_template_locale` VALUES (2845, 'itIT', 'Wandering Shay', 'Pick up Shay\'s Bell from the chest.$B$BEscort Shay Leafrunner to Rockbiter\'s camp.', 'Do you think you could help me find my way back to my friend, Rockbiter? He was supposed to be watching me... But I do have a tendency to wander off now and then. There are just so many things to see, and I get so distracted.$B$BThere is one thing that will always work to get my attention, though.$B$BInside this chest, you\'ll find a bell that is very precious to me. I just love the sound of it. You have my word, if I wander off, all you have to do is ring it and I\'ll come running back.', 'Take Shay Leafrunner to Rockbiter\'s camp', 'Take Shay Leafrunner to Rockbiter\'s camp', '', '', '', 'Return to Rockbiter at The Twin Colossals in Feralas.', 0);
INSERT INTO `quest_template_locale` VALUES (2845, 'ptBR', 'Wandering Shay', 'Pick up Shay\'s Bell from the chest.$B$BEscort Shay Leafrunner to Rockbiter\'s camp.', 'Do you think you could help me find my way back to my friend, Rockbiter? He was supposed to be watching me... But I do have a tendency to wander off now and then. There are just so many things to see, and I get so distracted.$B$BThere is one thing that will always work to get my attention, though.$B$BInside this chest, you\'ll find a bell that is very precious to me. I just love the sound of it. You have my word, if I wander off, all you have to do is ring it and I\'ll come running back.', 'Take Shay Leafrunner to Rockbiter\'s camp', 'Take Shay Leafrunner to Rockbiter\'s camp', '', '', '', 'Return to Rockbiter at The Twin Colossals in Feralas.', 0);
INSERT INTO `quest_template_locale` VALUES (2845, 'ruRU', 'Шая-бродяжница', 'Не поможешь ли ты мне отыскать моего друга, Камнедробителя? Он должен был следить за мной... Но со мной иногда случается, что я ухожу, куда глаза глядят. Вокруг столько интересного, я увлекаюсь, забываю обо всем на свете, и...$b$bНо есть одна вещь, которая обязательно привлекает мое внимание.$b$bВ этом сундучке хранится колокольчик. Он мне очень дорог. Мне ужасно нравится его звон. Так что, если я вдруг куда-нибудь уйду, позвони в колокольчик, и я тотчас прибегу обратно!', 'Достаньте из сундучка колокольчик Шаи.$b$bПроводите Шаю Листогон в лагерь Камнедробителя.', 'Проводить Шаю Листогон в лагерь Камнедробителя.', 'Вернитесь к Камнедробителю к Двум Колоссам, что в Фераласе.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2845, 'zhCN', '迷路的沙恩', '你能带我回到我的朋友罗克比特身边吗？他本来是要照看我的……但我总是不知不觉之间就迷路了。这里有这么多好看的东西，我东看看西看看就不知道该怎么走了。$B$B但是有一样东西可以一直吸引我的注意力。$B$B在这个箱子里有一只对我来说最为珍贵的铃铛，我就喜欢听它的声音。我保证，如果我走失了，只要你摇响铃铛，我就会马上跑回来。', '从箱子里拿取沙恩的铃铛，然后护送沙恩·飘叶回到罗克比特的营地。', '带沙恩·飘叶回到罗克比特的营地', '去菲拉斯找双塔山的罗克比特。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2846, 'deDE', 'Tiara der Tiefen', 'Bringt die Tiara der Tiefen zu Tabetha in den Düstermarschen.', 'Vor langer Zeit besaß ich ein wunderschönes Schmuckstück, die Tiara der Tiefen. Diese war nicht nur schön, sondern enthielt auch große Macht für diejenigen, die damit umzugehen wussten.$B$BAls nun die Kunde von der Tiara die Hydromantin Velratha erreichte, musste sie sie haben. Sie schickte Handlanger in mein Haus und die stahlen sie, während ich weg war. Diese Diebe!$B$BIch will meine Tiara wiederhaben! Geht nach Zul\'Farrak, der Trollstadt in Tanaris, findet Velratha und ringt ihr die Tiara ab. Bringt sie mir zurück und Ihr steht in meiner Gunst.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2846, 'esES', 'Tiara de las profundidades', 'Lleva la tiara de las profundidades a Tabetha en el Marjal Revolcafango.', 'Hace mucho tiempo tenía una bonita joya, la tiara de las profundidades. No solo era hermosa... tenía un gran poder para quien sabía usarlo.$B$BPor eso, cuando la hidromántica Velratha oyó hablar de ella quiso tenerla. Envío a sus agentes a mi hogar y me la robaron cuando estaba fuera. ¡Ladrones!$B$B¡Quiero recuperar mi tiara! Ve a Zul\'Farrak, la ciudad trol de Tanaris, encuentra a Velratha y quítale la tiara. Cuando la tengas vuelve a verme y te ganarás mi favor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2846, 'esMX', 'Tiara de las profundidades', 'Lleva la tiara de las profundidades a Tabetha en el Marjal Revolcafango.', 'Hace mucho tiempo tenía una bonita joya, la tiara de las profundidades. No solo era hermosa... tenía un gran poder para quien sabía usarlo.$B$BPor eso, cuando la hidromántica Velratha oyó hablar de ella quiso tenerla. Envío a sus agentes a mi hogar y me la robaron cuando estaba fuera. ¡Ladrones!$B$B¡Quiero recuperar mi tiara! Ve a Zul\'Farrak, la ciudad trol de Tanaris, encuentra a Velratha y quítale la tiara. Cuando la tengas vuelve a verme y te ganarás mi favor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2846, 'frFR', 'La tiare des abysses', 'Apportez la Tiare des abysses à Tabetha au marécage d\'Âprefange.', 'Il y a longtemps, je possédais une splendide pièce de joaillerie, la tiare des abysses. Elle n\'était pas seulement belle, elle détenait un grand pouvoir, pour ceux qui savaient l\'utiliser.$B$BQuand l\'hydromancienne Velratha a appris son existence, elle l’a convoitée. Elle a envoyé des agents chez moi pour la voler pendant mon absence. Voleurs !$B$BJe veux ma tiare ! Allez à Zul\'Farrak, la cité des trolls à Tanaris, trouvez Velratha et reprenez-lui la tiare. Rapportez-la et vous gagnerez mes faveurs.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2846, 'itIT', 'Tiara of the Deep', 'Bring the Tiara of the Deep to Tabetha in Dustwallow Marsh.', 'Long ago I possessed a beautiful piece of jewelry, the Tiara of the Deep.  And not only was it pretty--it held great power for those with the knowledge to use it.$B$BSo when word of the tiara reached the Hydromancer Velratha, she had to have it.  She sent agents to my home and they stole it while I was away.  The thieves!$B$BI want my tiara back!  Go to Zul\'Farrak, the troll city in Tanaris, find Velratha and wrench the tiara from her.  Return it to me and you\'ll earn my favor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2846, 'koKR', '심연의 티아라', '먼지진흙 습지대에 있는 타베사에게 심연의 티아라를 가져가야 합니다.', '오래전에 난 아름다운 보물을 하나 갖고 있었어요. 심연의 티아라라는 물건인데, 아주 아름다울 뿐만 아니라 제대로 사용할 줄 아는 이에게는 엄청난 힘을 주었죠.$B$B그 소문이 유체술사 벨라타의 귀에 들어가자 욕심을 낸 거예요. 그래서 내가 집을 비운 사이에 부하들을 보내 훔쳐 가 버렸어요. 망할 놈의 도둑들 같으니!$B$B내 티아라를 되찾고 싶어요! 타나리스에 있는 트롤 도시, 줄파락에 가서 벨라타를 찾아 그 티아라를 빼앗아 주세요. 그걸 갖다주면 보상을 해 드리겠어요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2846, 'ptBR', 'Tiara of the Deep', 'Bring the Tiara of the Deep to Tabetha in Dustwallow Marsh.', 'Long ago I possessed a beautiful piece of jewelry, the Tiara of the Deep. And not only was it pretty--it held great power for those with the knowledge to use it.$B$BSo when word of the tiara reached the Hydromancer Velratha, she had to have it. She sent agents to my home and they stole it while I was away. The thieves!$B$BI want my tiara back! Go to Zul\'Farrak, the troll city in Tanaris, find Velratha and wrench the tiara from her. Return it to me and you\'ll earn my favor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2846, 'ruRU', 'Тиара Глубин', 'Принесите Тиару Глубин Табете в Пылевых топях.', 'Давным-давно у меня была прекрасная драгоценность – Тиара Глубин. Это была не просто красивая безделушка, она была наделена великой силой, предназначенной для тех, кто умел с ней обращаться.$B$BКогда слух о моей тиаре достиг ушей гидроманта Велраты, она решила заполучить ее и послала своих подручных выкрасть ее у меня!$B$BЯ хочу вернуть то, что принадлежит мне. Отправляйся в Зул\'Фаррак, город троллей в пустыне Танарис, найди там Велрату и забери у нее мою тиару. Возвращайся ко мне и заслужишь мою вечную благодарность.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2846, 'zhCN', '深渊皇冠', '将深渊皇冠交给尘泥沼泽的塔贝萨。', '很久以前我曾经拥有一件美丽的珍宝，深渊皇冠。它不仅美丽——而且它对于知道怎么使用它的人来说更是一件拥有巨大力量的宝贝。$B$B所以当水占师维蕾萨听说皇冠的事情之后，她就发誓要得到它。维蕾萨派人在我外出的时候到我家来偷走了皇冠，这些卑鄙的小偷！$B$B我要取回我的皇冠！到位于塔纳利斯沙漠中的巨魔城市祖尔法拉克去，把皇冠从她手上抢回来。把它还给我，你会得到报酬的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2846, 'zhTW', '深淵冠冕', '將深淵冠冕交給塵泥沼澤的塔貝薩。', '很久以前我曾經擁有一件美麗的珍寶，深淵冠冕。它不僅美麗─而且它對於知道怎麼使用它的人來說更是一件擁有巨大力量的寶貝。$B$B所以當水占師維蕾薩聽說冠冕的事情之後，她就發誓要得到它。維蕾薩派人在我外出的時候到我家來偷走了冠冕，這些卑鄙的小偷!$B$B我要取回我的冠冕!到位於塔納利斯沙漠中的食人妖城市祖爾法拉克去，把冠冕從她手上搶回來。把它還給我，你會得到報酬的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2847, 'deDE', 'Wildlederrüstung', '$n, Ihr werdet ziemlich gut als Lederer! Ich kenne da einige Muster, an denen Ihr vielleicht interessiert seid... Wildlederrüstung! Wildleder zieht Kraft aus den mächtigen und chaotischen Eigenschaften von Wildranken, die man in Gegenden mit üppiger Vegetation auf einigen der Kreaturen dort findet. Die daraus gefertigte Rüstung ist garantiert das Beste, was jemand Eures Geschicks herstellen kann.$B$BIch werde es Euch beibringen, aber Ihr werdet dafür eine Weile für mich arbeiten. Das Lehrgeld beträgt nur 10 Stücke dickes Leder. Danach reden wir über Details.', 'Bringt Pratt McGrubben in der Mondfederfeste 10 Stücke dickes Leder.', '', 'Kehrt zu Pratt McGrubben in der Mondfederfeste in Feralas zurück.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2847, 'esES', 'Armadura de cuero salvaje', 'Lleva 10 cueros gruesos a Pratt McGrubben al Bastión Plumaluna.', '¡$n, te estás convirtiendo en $gun peletero:una peletera; bastante $gbueno:buena;! Yo conozco algunos patrones que podrían interesarte... Por ejemplo, ¡el de la armadura de cuero salvaje! El cuero salvaje aprovecha las potentes y caóticas propiedades de la atriplex salvaje que se encuentra en áreas de exuberante vegetación y en algunas criaturas de la región. Las armaduras que se forjan con ella son las mejores para alguien de tu habilidad, te lo garantizo.$B$BTe enseñaré el patrón, pero tendrás que trabajar para mí durante un tiempo. Para empezar, tendrás que traerme 10 piezas de cuero pesado. Ya detallaremos algo más después.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2847, 'esMX', 'Armadura de cuero salvaje', '¡$n, te estás convirtiendo en $gun peletero:una peletera; bastante $gbueno:buena;! Yo conozco algunos patrones que podrían interesarte... Por ejemplo, ¡el de la armadura de cuero salvaje! El cuero salvaje aprovecha las potentes y caóticas propiedades de la atriplex salvaje que se encuentra en áreas de exuberante vegetación y en algunas criaturas de la región. Las armaduras que se forjan con ella son las mejores para alguien de tu habilidad, te lo garantizo.$B$BTe enseñaré el patrón, pero tendrás que trabajar para mí durante un tiempo. Para empezar, tendrás que traerme 10 piezas de cuero pesado. Ya detallaremos algo más después.', 'Lleva 10 cueros gruesos a Pratt McGrubben al Bastión Plumaluna.', '', 'Vuelve con: Pratt McGrubben. Zona: Bastión Plumaluna, Feralas.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2847, 'frFR', 'L\'armure en cuir fauve', 'Rapporter 10 Cuirs épais à Pratt McGrubben au bastion de Pennelune.', '$n, vous avez acquis une bonne connaissance du travail du cuir ! Mais je sais des choses qui pourraient vous intéresser… L\'armure en cuir fauve ! L’intérêt du cuir fauve tient aux propriétés chaotiques des sauvageonnes et de quelques autres créatures d\'ici. L\'armure que vous réaliserez sera ce qu’un artisan de votre compétence peut fabriquer de mieux.$B$BJe vous apprendrai à la réaliser si vous acceptez de travailler pour moi pendant un moment. Le coût initial est juste de dix pièces de cuir épais. Après nous parlerons technique.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2847, 'ruRU', 'Дикарские кожаные доспехи', '$n, ты уже $gопытный кожевник:опытная кожевница;. Я владею искусством изготовления того, что весьма пригодилось бы и тебе. Это дикарские кожаные доспехи. В них используются загадочная и непостоянная сила дикой лозы, который можно отыскать в густых зарослях или получить от некоторых существ. Другого подобного доспеха мастер твоего уровня изготовить не в силах.$b$bЯ научу тебя делать эти доспехи, но для этого тебе придется потрудиться. Для начала мне нужно просто десять кусков плотной кожи. Принеси ее, потом обсудим дальнейшее.', 'Принесите 10 кусков плотной кожи Пратту Макгруббену в Крепость Оперенной Луны.', '', 'Вернитесь к Пратту Макгруббену в Крепость Оперенной Луны, что в Фераласе.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2847, 'zhCN', '蛮皮护甲', '$n，你已经是个很熟练的制皮匠了！我知道一些装备的制作方法，你可能会感兴趣……那就是蛮皮护甲！蛮皮中包含了野葡萄的混乱而强大的力量，你可以在这片茂盛的森林中找到这种植物，也可以在森林中的某些动物身上找到。用它制作的装备简直是无以伦比！$B$B我会把制作蛮皮护甲的方法教给你，条件是你要为我做点事情。首先你得给我带十块厚皮来，等你拿来十块厚皮之后我们再详谈。', '给羽月要塞的普拉特·马克格鲁比带去10块厚皮。', '', '去菲拉斯找羽月要塞的普拉特·马克格鲁比。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2848, 'deDE', 'Wildlederschultern', 'Bringt Pratt McGrubben in der Mondfederfeste 6 dicke Rüstungssets und eine Wildranke.', 'Wildlederschultern verstärken den Körper des Trägers unglaublich gut und bieten dabei erstklassigen Schutz für die Oberarme und die Schultern. Wie bei allen Wildlederrüstungen wird auch hier eine mächtige, allerdings zufällige Verbesserung erzeugt, wenn die Wildranke eingearbeitet wird.$B$BFür dieses Muster werde ich 6 dicke Rüstungssets und eine Wildranke benötigen. Das sollte für Euch kein allzu schwer zu erfüllender Auftrag sein.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2848, 'esES', 'Sobrehombros de cuero salvaje', 'Lleva 6 refuerzos para armadura gruesos y una atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.', 'Los sobrehombros de cuero salvaje ofrecen una protección de primera a la parte superior de los brazos y a los hombros de quien las lleva. Como ocurre con la armadura de cuero salvaje, cuando se cose la atriplex salvaje a las hombreras, esta les imbuye una mejora potente aleatoria.$B$BPara este patrón necesitaré 6 refuerzos para armadura gruesos y 1 atriplex salvaje. No deberías tener problemas para conseguirlos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2848, 'esMX', 'Sobrehombros de cuero salvaje', 'Los sobrehombros de cuero salvaje ofrecen una protección de primera a la parte superior de los brazos y a los hombros de quien las lleva. Como ocurre con la armadura de cuero salvaje, cuando se cose la atriplex salvaje a las hombreras, esta les imbuye una mejora potente aleatoria.$B$BPara este patrón necesitaré 6 refuerzos para armadura gruesos y 1 atriplex salvaje. No deberías tener problemas para conseguirlos.', 'Lleva 6 refuerzos para armadura gruesos y una atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.', '', 'Vuelve con: Pratt McGrubben. Zona: Bastión Plumaluna, Feralas.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2848, 'frFR', 'Les épaulières en cuir fauve', 'Apporter 6 Renforts d\'armure épais et 1 Sauvageonne à Pratt McGrubben au bastion de Pennelune.', 'Les épaulières en cuir fauve protègent efficacement le haut des bras et les épaules. Comme l\'ensemble des pièces d\'armure en cuir fauve, elles bénéficient d\'une qualité supplémentaire, mais aléatoire, en raison de l\'utilisation des sauvageonnes.$B$BPour ce patron, j’ai besoin de six renforts d\'armure épais et d’une sauvageonne. Cela ne devrait pas vous poser de problèmes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2848, 'itIT', 'Wild Leather Shoulders', 'Bring 6 Thick Armor Kits and a Wildvine to Pratt McGrubben in Feathermoon Stronghold.', 'Wild Leather shoulders support the wearer\'s frame extremely well, offering top notch protection of the upper arms and the shoulders themselves.  As with all Wild Leather armor, a potent but random benefit is applied to the item as the wildvine is stitched in.$B$BFor this pattern, I will require six thick armor kits and a wildvine.  That shouldn\'t be too tall of an order for you to fill.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2848, 'ruRU', 'Дикарские кожаные наплечники', 'Дикарские кожаные наплечники обеспечивают превосходную защиту как плеч, так и верхней части руки. Как и все дикарские кожаные доспехи, они прошиты дикой лозой, что придает им мощные, но непостоянные магические свойства.$b$bДля изготовления изделия по этой выкройке мне потребуется шесть комплектов накладок из плотной кожи и дикий виноград. Для тебя это не слишком сложное задание...', 'Принесите 6 комплектов накладок из плотной кожи и дикую лозу Макгруббену в Крепость Оперенной Луны.', '', 'Вернитесь к Пратту Макгруббену в Крепость Оперенной Луны, что в Фераласе.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2848, 'zhCN', '蛮皮护肩', '蛮皮护肩能够很好地支撑穿戴者的身形，为其上臂和肩部提供了极好的保护。和所有的蛮皮护甲一样，制作时所使用的野葡萄会使其附带一种强大的随机魔法效果。$B$B如果你想要这种护甲的图样，就给我弄六块厚重护甲片和一株野葡萄来，这对你来说应该不算什么过分的要求。', '给羽月要塞的普拉特·马克格鲁带去6片厚重护甲片和1株野葡萄。', '', '去菲拉斯找羽月要塞的普拉特·马克格鲁比。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2849, 'deDE', 'Wildlederweste', 'Bringt Pratt McGrubben in der Mondfederfeste 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke.', 'Wildlederwesten sind die Grundlage aller Wildlederrüstungssets. Das Gerüst der Weste verbindet sich mit der Wildranke, um ein Produkt zu erzeugen, das sowohl maximalen Schutz für den Torso bietet als auch der mächtigen Magie erlaubt, sich in dem Gewand auszubreiten.$B$BFür dieses Muster werde ich 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke benötigen. Das ist ein großer Auftrag, aber sicher keiner, bei dessen Erfüllung Ihr Schwierigkeiten haben werdet.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2849, 'esES', 'El Jubón de cuero salvaje', 'Lleva 2 corazas de caparazón de tortuga, 2 guantes de caparazón de tortuga y 1 atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.', 'Los jubones de cuero salvaje son la pieza principal de cualquier conjunto de armadura de cuero salvaje. La tela del jubón se amalgama con la atriplex salvaje para crear un producto que ofrece la máxima protección para el torso a la vez que permite que su potente magia se abra paso a través de ella.$B$BPara este patrón, necesitaré dos petos de caparazón de tortuga, dos pares de guantes de caparazón de tortuga y una atriplex salvaje. Son bastantes cosas, pero no debería resultarte difícil proporcionármelas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2849, 'esMX', 'El Jubón de cuero salvaje', 'Los jubones de cuero salvaje son la pieza principal de cualquier conjunto de armadura de cuero salvaje. La tela del jubón se amalgama con la atriplex salvaje para crear un producto que ofrece la máxima protección para el torso a la vez que permite que su potente magia se abra paso a través de ella.$B$BPara este patrón, necesitaré dos petos de caparazón de tortuga, dos pares de guantes de caparazón de tortuga y una atriplex salvaje. Son bastantes cosas, pero no debería resultarte difícil proporcionármelas.', 'Lleva 2 corazas de caparazón de tortuga, 2 guantes de caparazón de tortuga y 1 atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.', '', 'Vuelve con: Pratt McGrubben. Zona: Bastión Plumaluna, Feralas.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2849, 'frFR', 'Le gilet en cuir fauve', 'Rapporter 2 Cuirasses en écailles de tortue, 2 paires de Gants en écailles de tortue et 1 Sauvageonne à Pratt McGrubben au bastion de Pennelune.', 'Le gilet est la pièce maîtresse de toute armure en cuir fauve. Il procure une protection maximale du torse tout en permettant à la puissante magie des sauvageonnes de se diffuser dans l’armure.$B$BPour ce modèle, je demande deux paires de gants en écailles de tortue, deux cuirasses en écailles de tortue et une sauvageonne. C\'est une grosse commande, mais vous ne devriez pas avoir de problème à me la fournir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2849, 'itIT', 'Wild Leather Vest', 'Bring 2 Turtle Scale Breastplates, 2 Turtle Scale Gloves, and a Wildvine to Pratt McGrubben in Feathermoon Stronghold.', 'Wild Leather vests are the staple of any Wild Leather armor set.  The frame of the vest blends with the wildvine to produce a product that offers maximum protection of the torso while allowing for the potent magic to work its way through the garment.$B$BFor this pattern, I will require two Turtle Scale breastplates, two sets of Turtle Scale gloves, and a wildvine.  It\'s a tall order, but one you should have little difficulty in filling for me.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2849, 'ruRU', 'Дикарский кожаный жилет', 'Дикарский кожаный жилет – основа любых дикарских кожаных доспехов. Основа жилета переплетена диким виноградом, что дает максимальную защиту торса и позволяет использовать мощную магию растения.$b$bДля изготовления изделия по этой выкройке мне потребуется две черепашьих чешуйчатых кирасы, две пары черепашьих чешуйчатых перчаток и дикая лоза. Изготовить это непросто, но, думаю, ты справишься.', 'Принесите 2 черепашьих чешуйчатых кирасы, 2 пары черепашьих чешуйчатых перчаток и дикую лозу Пратту Макгруббену в Крепость Оперенной Луны.', '', 'Вернитесь к Пратту Макгруббену в Крепость Оперенной Луны, что в Фераласе.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2849, 'zhCN', '蛮皮外衣', '蛮皮外衣是蛮皮护甲中最主要的部分，它的结构以及编织于其中的野葡萄能使其在具有很强的保护作用的同时，还附带着强大的魔法力量。$B$BB如果你想要这种护甲的图样，就给我弄两件龟鳞胸甲、两副龟鳞手套和一株野葡萄来。这不是件容易的活儿，但你一定可以完成它。', '给羽月要塞的普拉特·马克格鲁带去2件龟鳞胸甲、2副龟鳞手套和1株野葡萄。', '', '去菲拉斯找羽月要塞的普拉特·马克格鲁比。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2850, 'deDE', 'Wildlederhelm', 'Bringt Pratt McGrubben in der Mondfederfeste 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke.', 'Ein Wildlederhelm bietet dem Träger verbesserten Kopfschutz, ohne dabei an der Bequemlichkeit zu sparen. Wie bei allen Wildlederrüstungen erzeugt die eingearbeitete Wildranke eine mächtige, aber zufällige Verbesserung der Struktur des Helms. Lederträger werden dieses Stück auf jeden Fall haben wollen!$B$BFür dieses Muster brauche ich 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke. Erfüllt diesen Auftrag und das Muster gehört Euch!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2850, 'esES', 'Casco de cuero salvaje', 'Lleva 2 guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y 1 atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.', 'Un casco de cuero salvaje ofrece a quien lo lleva una gran protección de las áreas vitales de la cabeza sin sacrificar el confort. Como en todas las partes de las armaduras de cuero salvaje, cuando se integra la atriplex salvaje al casco, esta le imbuye una potente mejora aleatoria. ¡Quienes aprecian el cuero, sin duda desearán tener esta pieza!$B$BPara este patrón necesitaré 2 guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y una atriplex salvaje. ¡Si me traes eso, el patrón será tuyo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2850, 'esMX', 'Casco de cuero salvaje', 'Un casco de cuero salvaje ofrece a quien lo lleva una gran protección de las áreas vitales de la cabeza sin sacrificar el confort. Como en todas las partes de las armaduras de cuero salvaje, cuando se integra la atriplex salvaje al casco, esta le imbuye una potente mejora aleatoria. ¡Quienes aprecian el cuero, sin duda desearán tener esta pieza!$B$BPara este patrón necesitaré 2 guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y una atriplex salvaje. ¡Si me traes eso, el patrón será tuyo!', 'Lleva 2 guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y 1 atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.', '', 'Vuelve con: Pratt McGrubben. Zona: Bastión Plumaluna, Feralas.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2850, 'frFR', 'Le casque en cuir fauve', 'Rapporter 2 Tuniques de la nuit, 2 Bandeaux de la nuit et 1 Sauvageonne à Pratt McGrubben au bastion de Pennelune.', 'Un casque en cuir fauve offre une protection optimale sans sacrifier au confort. Comme pour l\'armure en cuir fauve, les sauvageonnes lui apportent une amélioration puissante, mais aléatoire. Les porteurs d\'armures de cuir raffolent de cet article !$B$BPour ce modèle, j\'ai besoin de deux tuniques de la nuit, deux bandeaux de la nuit et d\'une sauvageonne. Complétez cette commande et le patron est à vous !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2850, 'itIT', 'Wild Leather Helmet', 'Bring 2 Nightscape Tunics, 2 Nightscape Headbands, and a Wildvine to Pratt McGrubben in Feathermoon Stronghold.', 'A Wild Leather helmet offers the wearer superior protection to the vital areas of the head without a sacrifice in comfort.  As with all Wild Leather armor, the integrated wildvine yields a powerful but random enhancement to the frame of the helm.  Leather wearers will definitely want this piece!$B$BFor this pattern, I will need two Nightscape tunics, two Nightscape headbands, and a wildvine.  Complete this order and the pattern will be yours!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2850, 'ruRU', 'Дикарский кожаный шлем', 'Дикарский кожаный шлем обеспечивает лучшую защиту головы, при этом не менее удобен, чем обычные шлемы. Как и во всех дикарских кожаных доспехах, в основу шлема вплетен дикий виноград, наделяющий его мощными, но непостоянными магическими свойствами. Тем, кто ходит в кожаных доспехах, эта вещица наверняка пригодится!$b$bНо для изготовления изделия по этой выкройке мне понадобятся 2 ночных мундира, 2 ночных головных повязки и дикая лоза. Принеси это все, и выкройка твоя!', 'Принесите 2 ночных мундира, 2 ночных головных повязки и дикую лозу Пратту Макгруббену в Крепость Оперенной Луны.', '', 'Вернитесь к Пратту Макгруббену в Крепость Оперенной Луны, что в Фераласе.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2850, 'zhCN', '蛮皮头盔', '蛮皮头盔可以为你的头部提供极好的保护，与此同时，它也具有相当的舒适性。和所有的蛮皮护甲一样，制作头盔时使用的野葡萄会使它产生一种强大的随机魔法效果。对于穿着皮甲的人来说，这东西可是非常不错的！$B$B想要得到这顶头盔的图样的话，就给我带两件夜色外套、两条夜色头巾和一株野葡萄来。完成这个任务之后，图样就是你的了！', '给羽月要塞的普拉特·马克格鲁比带去2件夜色外套、2条夜色头巾和一株野葡萄。', '', '去菲拉斯找羽月要塞的普拉特·马克格鲁比。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2851, 'deDE', 'Wildlederstiefel', 'Bringt Pratt McGrubben in der Mondfederfeste 2 Hosen des Nachtschleichers, 2 Stiefel des Nachtschleichers und 2 Wildranken.', 'Ihr habt schon eine ganze Menge gelernt, $n; dennoch gibt es noch mehr für Euch zu lernen. Als Nächstes sind Wildlederstiefel an der Reihe, der Grundstein eines jeden Sets.$B$BDiese Stiefel werden so gefertigt, dass sie den Fuß in Bequemlichkeit hüllen, während sie ihn gleichzeitig gegen die Gefahren des Abenteuerlebens wappnen. Die weichen Sohlen sind ein Segen für diejenigen, die bei ihrer Fußbekleidung auf leise Bewegung Wert legen.$B$BFür dieses Muster müsst Ihr mir 2 Hosen des Nachtschleichers und 2 Stiefel des Nachtschleichers besorgen. Ich werde außerdem zwei Wildranken benötigen, nicht nur eine. Wenn Ihr all das beisammen habt, gebt mir Bescheid.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2851, 'esES', 'Botas de cuero salvaje', 'Lleva 2 pantalones Nochefugaz, 2 botas Nochefugaz y 2 atriplex salvajes a Pratt McGrubben al Bastión Plumaluna.', 'Has aprendido bastante hasta ahora, $n; pero aún tienes que aprender más. Lo siguiente serán las botas de cuero salvaje, la piedra angular de cualquier conjunto de armadura.$B$BEstas botas se crean para envolver al pie en confort a la vez que lo refuerzan para protegerlo de los peligros de una vida aventurera. Sus suelas blandas son una bendición para quienes necesitan moverse con sigilo.$B$BPara este patrón necesito 2 pantalones Nochefugaz y 2 botas Nochefugaz. Y también necesito 2 atriplex salvajes, no me basta con una. Cuando tengas todo esto, házmelo saber.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2851, 'esMX', 'Botas de cuero salvaje', 'Lleva 2 pantalones Nochefugaz, 2 botas Nochefugaz y 2 atriplex salvajes a Pratt McGrubben al Bastión Plumaluna.', 'Has aprendido bastante hasta ahora, $n; pero aún tienes que aprender más. Lo siguiente serán las botas de cuero salvaje, la piedra angular de cualquier conjunto de armadura.$B$BEstas botas se crean para envolver al pie en confort a la vez que lo refuerzan para protegerlo de los peligros de una vida aventurera. Sus suelas blandas son una bendición para quienes necesitan moverse con sigilo.$B$BPara este patrón necesito 2 pantalones Nochefugaz y 2 botas Nochefugaz. Y también necesito 2 atriplex salvajes, no me basta con una. Cuando tengas todo esto, házmelo saber.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2851, 'frFR', 'Les bottes en cuir fauve', 'Rapporter 2 Pantalons de la nuit, 2 paires de Bottes de la nuit et 2 Sauvageonnes à Pratt McGrubben au bastion de Pennelune.', 'Vous avez déjà beaucoup appris, $n. Mais ce n’est pas fini. Après viennent les bottes en cuir fauve, la pierre angulaire de tout l\'équipement.$B$BCes bottes protègent bien les pieds tout en étant très confortables. Les semelles souples sont une bénédiction pour ceux qui se déplacent discrètement.$B$BPour ces bottes, j\'ai besoin de deux pantalons de la nuit, de deux paires de bottes de la nuit, ainsi que de deux sauvageonnes. Lorsque vous aurez tout cela, faites-le-moi savoir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2851, 'itIT', 'Wild Leather Boots', 'Bring 2 Nightscape Pants, 2 Nightscape Boots, and 2 Wildvines to Pratt McGrubben in Feathermoon Stronghold.', 'You\'ve learned quite a bit now, $n; still, there is more for you to learn.  Next are Wild Leather boots, the cornerstone of any set.$B$BThese boots are crafted to cradle the foot in comfort while bracing it against the hazards of adventuring.  The soft soles are a blessing to those seeking quiet movement in footwear.$B$BFor this pattern, I need an order of two Nightscape pants and two Nightscape boots filled.  I also will require two wildvines... not just one.  When you have all this, let me know.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2851, 'ptBR', 'Botas de couro silvestre', 'Leve 2 Calças da Escapada Noturna, 2 Botas da Escapada Noturna e 2 Vinhas Selvagens para Pradal McGrubben no Domínio de Plumaluna.', 'Você já aprendeu bastante, $n, mas ainda há mais pela frente. Agora é a vez das botas de couro silvestre, o alicerce de qualquer armadura.$B$BEssas botas são feitas para deixar os pés confortáveis enquanto seguem caminhos de perigos e aventuras. Suas solas macias são uma bênção para aqueles em busca de passos tranquilos.$B$BPara este molde, preciso de duas calças da Escapada Noturna e duas botas da Escapada Noturna. Também preciso de duas – e não só uma – vinhas selvagens. Volte quando tiver tudo isso.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2851, 'ruRU', 'Дикарские кожаные сапоги', 'Принесите 2 пары ночных штанов, 2 пары ночных сапог и 2 экземпляра дикой лозы Пратту Макгруббену в Крепость Оперенной Луны.', 'Тебе известно уже немало, $n. Но многое еще остается постичь. Теперь ты узнаешь о дикарских кожаных сапогах – ведь без обуви не обходится ни один доспех.$b$bНоге в них удобно и приятно, и дальний путь в них не так тяжел. Мягкая же подошва позволяет при необходимости ступать беззвучно.$b$bДля их изготовления по выкройке мне потребуются две пары ночных штанов и две пары ночных сапог. И два экземпляра дикого винограда – запомни, два, а не один! Добудешь все это – дай мне знать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2851, 'zhCN', '蛮皮战靴', '你现在已经学到了很多东西，$n，但你仍有更多的东西要学习。现在我们来谈谈蛮皮战靴，它是这一套装备的基石。$B$B这种靴子舒适合脚，可以让你的双脚在长途跋涉之后仍然轻松自如。柔软的鞋底对于那些喜欢悄悄行动的人来说再合适不过了。$B$B如果你想要得到它的图样，就给我拿两条夜色短裤和两双夜色长靴来。另外我还需要两株野葡萄……只有一株的话可不够。拿到以上这些东西之后，就把它们交给我。', '给羽月要塞的普拉特·马克格鲁带去2条夜色裤、2双夜色靴和2株野葡萄。', '', '去菲拉斯找羽月要塞的普拉特·马克格鲁比。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2852, 'deDE', 'Wildledergamaschen', 'Bringt Pratt McGrubben in der Mondfederfeste 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken.', 'Jetzt, nachdem Ihr die Grundlagen beherrscht, seid Ihr bereit, Euch komplexeren Dingen zuzuwenden, $n. Wildledergamaschen gehören zur robustesten und stärksten Lederausrüstung überhaupt. Bauch, Hüften, Schenkel und Solarplexus werden ungeheuer gut geschützt. Und wenn dann noch die Verstärkung hinzukommt, die die Wildranke bewirkt, werden Euch die Abenteurer, die diese Rüstung besitzen, jedes Mal danken, wenn sie Euch sehen.$B$BFür dieses Muster müsst Ihr mir 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken besorgen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2852, 'esES', 'Leotardos de cuero salvaje', 'Lleva 2 yelmos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 atriplex salvajes a Pratt McGrubben al Bastión Plumaluna.', 'Una vez asentadas las bases, ya puedes aprender algo más complejo, $n. Los leotardos de cuero salvaje son uno de los artículos de cuero de mayor solidez y potencia. Ofrecen una increíble protección a la cintura, las pantorrillas y a otros órganos vitales. Si los combinas con la mejora que proporciona la atriplex salvaje, los aventureros que los vistan te lo agradecerán cada vez que te vean.$B$BPara este patrón necesito 2 cascos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 plantas de atriplex salvaje.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2852, 'esMX', 'Leotardos de cuero salvaje', 'Lleva 2 yelmos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 atriplex salvajes a Pratt McGrubben al Bastión Plumaluna.', 'Una vez asentadas las bases, ya puedes aprender algo más complejo, $n. Los leotardos de cuero salvaje son uno de los artículos de cuero de mayor solidez y potencia. Ofrecen una increíble protección a la cintura, las pantorrillas y a otros órganos vitales. Si los combinas con la mejora que proporciona la atriplex salvaje, los aventureros que los vistan te lo agradecerán cada vez que te vean.$B$BPara este patrón necesito 2 cascos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 plantas de atriplex salvaje.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2852, 'frFR', 'Les jambières en cuir fauve', 'Rapporter 2 Casques en écailles de tortue, 2 Brassards en écailles de tortue et 2 Sauvageonnes à Pratt McGrubben au bastion de Pennelune.', 'Vous pouvez maintenant apprendre des techniques plus complexes, $n. Les jambières en cuir fauve sont un des équipements en cuir les plus résistants et les plus puissants. La taille, les hanches et les parties vitales sont particulièrement bien protégées. Les aventuriers qui l\'utiliseront apprécieront grandement cette protection combinant l\'amélioration apportée par la sauvageonne.$B$BPour ce modèle, j\'ai besoin de deux casques en écailles de tortue, de deux brassards en écailles de tortue et de deux sauvageonnes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2852, 'itIT', 'Wild Leather Leggings', 'Bring 2 Turtle Scale Helms, 2 Turtle Scale Bracers, and 2 Wildvines to Pratt McGrubben in Feathermoon Stronghold.', 'With the basics down, you are ready to master more complex fare, $n.  Wild Leather leggings are some of the sturdiest and powerful leather gear around.  The waist, thighs, calves, and the vitals - all are given tremendous protection.  Coupled with the enhancement that the wildvine provides, adventurers who own it will thank you each time they see you.$B$BFor this pattern, I need an order of two Turtle Scale helms, two Turtle Scale bracers, and two wildvines filled.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2852, 'ptBR', 'Perneiras de couro silvestre', 'Leve 2 Elmos de Escama de Tartaruga, 2 Braçadeiras de Escama de Tartaruga e 2 Vinhas Selvagens para Pradal McGrubben no Domínio de Plumaluna.', 'Como você já domina o básico, está na hora de você aprender coisas mais complexas, $n. Perneiras de couro silvestre são um dos equipamentos mais robustos e poderosos. Elas oferecem extraordinária proteção à cintura, coxas, panturrilhas e órgãos vitais. Acompanhadas pelas melhorias proporcionadas pela vinha selvagem, os aventureiros que as usarem serão sempre gratos a você.$B$BPara esse pedido, preciso de dois elmos de escama de tartaruga, duas braçadeiras de escama de tartaruga e duas vinhas selvagens.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2852, 'ruRU', 'Дикарские кожаные поножи', 'Принесите два шлема из черепашьего панциря, две пары черепашьих чешуйчатых наручей и два экземпляра дикой лозы Пратту Макгруббену в Крепость Оперенной Луны.', 'Ну, $n, теперь, когда с простыми предметами покончено, мы можем перейти к более сложным вещам. Дикарские кожаные поножи – одна из наиболее прочных и надежных разновидностей кожаного доспеха. Они отлично защищают и живот, и ноги, и прочие важные части тела. А поскольку в них вшита еще и дикая лоза, дающая дополнительную защиту, любой искатель приключений скажет тебе спасибо за такие поножи.$b$bДля изготовления изделия по этой выкройке мне понадобятся два шлема из черепашьего панциря, две пары черепашьих чешуйчатых наручей, и два экземпляра дикого винограда.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2852, 'zhCN', '蛮皮护腿', '基础教程已经完成，你应做好准备掌握更为复杂的制作了，$n。蛮皮护胫是最坚硬和强大的皮制系列装备中的一样。野兽系列皮装备让你的胸部，大腿，小腿和其他致命的部位都被保护起来。再加上野葡萄所起的辅助作用，拥有这种装备的冒险者一定会永远感谢你的。$B$B如果你想要得到这个图样的话，带给我两顶龟鳞头盔，两付龟鳞护腕和两株野葡萄。', '给羽月要塞的普拉特·马克格鲁比带去2顶龟鳞头盔、2副龟鳞护腕和2株野葡萄。', '', '去菲拉斯找羽月要塞的普拉特·马克格鲁比。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2853, 'deDE', 'Meister des Wildleders', 'Nun, $n, Ihr gebt dem Wort Sorgfalt eine neue Bedeutung. Es steht nur noch ein Wildlederrüstungsmuster aus, denn Ihr habt alle anderen gemeistert. Ich werde nicht von Euch verlangen, Euch unnötig für dieses Muster abzuplagen. Ihr habt es Euch bereits mehr als verdient... Aber ich kann es Euch nicht selbst geben. Mein Lehrer, derjenige, der mich in dieses Handwerk einführte, muss Euch belohnen.$B$BBringt diesen Brief zu Telonis. Er lehrt Lederverarbeitung in Darnassus. Zeigt ihm den Brief und lasst Euch von ihm als Gleichgestellten anerkennen, so wie ich das auch tue.', 'Gebt Telonis, dem Lederverarbeitungsmeister von Darnassus, Pratts Brief.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2853, 'esES', 'Maestro de cuero salvaje', 'Vaya $n, eres la diligencia personificada. Dominas casi todos los patrones de la armadura de cuero salvaje. Solo te falta uno. Y no te haré trabajar para conseguirlo. Porque ya te lo has ganado, pero... no puedo enseñártelo yo. Mi profesor, el que me inició en la peletería, es quien debe recompensarte.$B$BLleva esta carta a Telonis; enseña peletería en Darnassus. Muéstrale la carta y te tratará como a $gun:una; igual, como hago yo.', 'Entrega la carta de Pratt a Telonis, el maestro curtidor de Darnassus.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2853, 'esMX', 'Maestro de cuero salvaje', 'Vaya $n, eres la diligencia personificada. Dominas casi todos los patrones de la armadura de cuero salvaje. Solo te falta uno. Y no te haré trabajar para conseguirlo. Porque ya te lo has ganado, pero... no puedo enseñártelo yo. Mi profesor, el que me inició en la peletería, es quien debe recompensarte.$B$BLleva esta carta a Telonis; enseña peletería en Darnassus. Muéstrale la carta y te tratará como a $gun:una; igual, como hago yo.', 'Entrega la carta de Pratt a Telonis, el maestro curtidor de Darnassus.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2853, 'frFR', 'Maître du cuir fauve', 'Eh bien, $n ! Vous êtes un modèle de rapidité. Vous connaissez tous les patrons de l\'armure en cuir fauve, sauf un. Je ne vous demanderai rien pour celui-là, vous le méritez… mais je ne peux pas vous l’apprendre moi-même. Mon maître est le seul qui puisse vous transmettre ce savoir.$B$BPortez cette lettre à Telonis, il enseigne le travail du cuir à Darnassus. Montrez-lui la lettre et laissez-le vous reconnaître comme son égal, ainsi que je l\'ai fait.', 'Donner la Lettre de Pratt à Telonis, le Maître tanneur de Darnassus.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2853, 'ruRU', 'Мастер школы Дикаря', 'Что ж, $n, ты – образец прилежания. Остался только один предмет из дикарских кожаных доспехов, который ты не умеешь изготовлять. Я не стану заставлять тебя трудиться, чтобы ее получить: ты и так сполна $gрасплатился:расплатилась; за все. Однако же сам я тебе его дать не могу. Ты получишь награду от моего наставника, того, кто посвятил в тайны нашего ремесла меня самого. $b$bОтнеси эту записку Телонису. Он обучает кожевников в Дарнасе. Покажи ему записку, и пусть он приветствует тебя как $gравного:равную;, подобно мне самому.', 'Отдайте письмо Пратта Телонису, главному кожевнику Дарнаса.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2853, 'zhCN', '蛮皮护甲大师', '好吧，$n，你就是勤奋的楷模。蛮皮护甲只剩下一种式样你还没有学会了。我不会要求你为最后的式样无谓地浪费时间和精力，你可以直接得到它，还有一些……但我不能自己传授给你。我的老师，就是带我进入这一行的人，他会教你怎么做。$B$B把这封信交给泰龙尼斯，他在达纳苏斯教授皮革制作的工艺。给他看这封信，让他像我一样认同你。', '将普拉特的信交给达纳苏斯的制皮大师泰龙尼斯。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2854, 'deDE', 'Wildlederrüstung', '$n, Euer Ruf als Lederer eilt Euch voraus. Ich besitze Wissen über die Lederverarbeitung, das zu erwerben weise von Euch wäre... Wildlederrüstung! Wildleder zieht Kraft aus den mächtigen und chaotischen Eigenschaften von Wildranken, die man in Gegenden mit üppiger Vegetation auf einigen der Kreaturen dort findet. Die daraus gefertigte Rüstung ist garantiert das Beste, was ein Handwerker Eures Geschicks herstellen kann.$B$BIch werde es Euch beibringen, aber Ihr werdet dafür eine Weile für mich arbeiten. Das Lehrgeld beträgt nur 10 Stücke dickes Leder. Danach reden wir über Details.', 'Bringt Jangdor Flinkschreiter im Camp Mojache 10 Stücke dickes Leder.', '', 'Kehrt zu Jangdor Flinkschreiter in Feralas zurück.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2854, 'esES', 'Armadura de cuero salvaje', '$n, tu habilidad como $gpeletero:peletera; te precede. Yo poseo un conocimiento de la peletería que harías bien en aprender... Por ejemplo, ¡el de la armadura de cuero salvaje! El cuero salvaje aprovecha las potentes y caóticas propiedades de la atriplex salvaje que se encuentra en áreas de exuberante vegetación y en algunas criaturas de la región. Las armaduras que se forjan con ella son las mejores para $gun artesano:una artesana; de tu habilidad, te lo garantizo.$B$BTe enseñaré a crearla, pero tendrás que trabajar para mí durante un tiempo. Para empezar, tendrás que traerme 10 piezas de cuero pesado. Ya detallaremos algo más después.', 'Lleva 10 cueros gruesos a Jangdor Zanco Veloz al Campamento Mojache.', '', 'Vuelve con: Jangdor Zanco Veloz. Zona: Feralas.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2854, 'esMX', 'Armadura de cuero salvaje', '$n, tu habilidad como $gpeletero:peletera; te precede. Yo poseo un conocimiento de la peletería que harías bien en aprender... Por ejemplo, ¡el de la armadura de cuero salvaje! El cuero salvaje aprovecha las potentes y caóticas propiedades de la atriplex salvaje que se encuentra en áreas de exuberante vegetación y en algunas criaturas de la región. Las armaduras que se forjan con ella son las mejores para $gun artesano:una artesana; de tu habilidad, te lo garantizo.$B$BTe enseñaré a crearla, pero tendrás que trabajar para mí durante un tiempo. Para empezar, tendrás que traerme 10 piezas de cuero pesado. Ya detallaremos algo más después.', 'Lleva 10 cueros gruesos a Jangdor Zanco Veloz al Campamento Mojache.', '', 'Vuelve con: Jangdor Zanco Veloz. Zona: Feralas.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2854, 'frFR', 'L\'armure en cuir fauve', '$n, la renommée de vos talents vous précède. Je connais quelque chose qui pourrait vous intéresser... l\'armure en cuir fauve ! Le cuir fauve tire ses propriétés des sauvageonnes que l\'on trouve dans la végétation luxuriante et sur quelques créatures. Cela ne devrait pas être compliqué pour quelqu\'un de votre niveau.$B$BJe vous enseignerai, mais en échange vous travaillerez pour moi pendant un moment. La première chose consiste à me rapporter dix pièces de cuir épais. Après nous discuterons technique.', 'Rapporter 10 Cuirs épais à Jangdor Rôdeur-agile au Camp Mojache.', '', 'Retournez voir Jangdor Rôdeur-agile en Féralas.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2854, 'ruRU', 'Дикарские кожаные доспехи', '$n, ты уже $gопытный кожевник:опытная кожевница;. Я владею искусством изготовления того, что весьма пригодилось бы и тебе. Это дикарские кожаные доспехи. В них используется загадочная и непостоянная сила дикой лозы, который можно отыскать в густых зарослях или получить от некоторых существ. Другого подобного доспеха мастер твоего уровня изготовить не в силах.$b$bЯ научу тебя делать эти доспехи, но для этого тебе придется потрудиться. Для начала мне нужно просто десять кусков плотной кожи. Принеси ее, потом обсудим дальнейшее.', 'Принесите 10 кусков плотной кожи Джангору Быстрому Страннику в Лагерь Мохаче.', '', 'Вернитесь к Джангору Быстрому Страннику в Фералас.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2854, 'zhCN', '蛮皮护甲', '$n，你已经是个很熟练的制皮匠了！我知道一些装备的制作方法，你可能会感兴趣……那就是蛮皮护甲！蛮皮中包含了野葡萄的混乱而强大的力量，你可以在这片茂盛的森林中找到这种植物，也可以在森林中的某些动物身上找到。用它制作的装备简直是无以伦比！$B$B我会把制作蛮皮护甲的方法教给你，条件是你要为我做点事情。首先你得给我带十块厚皮来，等你拿来十块厚皮之后我们再详谈。', '给莫沙彻营地的杉多尔·迅蹄带去10块厚皮。', '', '去找菲拉斯的杉多尔·迅蹄。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2855, 'deDE', 'Wildlederschultern', 'Bringt Jangdor Flinkschreiter im Camp Mojache 6 dicke Rüstungssets und eine Wildranke.', 'Wildlederschultern verstärken den Körper des Trägers unglaublich gut und bieten dabei besten Schutz für Oberarme und Schultern. Wie bei allen Wildlederrüstungen wird auch hier eine zufällige, aber magieerfüllte Verbesserung erzeugt, wenn die Wildranke eingearbeitet wird.$B$BFür dieses Muster werde ich 6 dicke Rüstungssets und eine Wildranke benötigen. Das sollte für Euch kein allzu schwer zu erfüllender Auftrag sein.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2855, 'esES', 'Sobrehombros de cuero salvaje', 'Lleva 6 refuerzos para armadura gruesos y una atriplex salvaje a Jangdor Zanco Veloz al Campamento Mojache.', 'Los sobrehombros de cuero salvaje ofrecen una gran protección a la parte superior de los brazos y a los hombros de quien las lleva. Como ocurre con la armadura de cuero salvaje, cuando se cose la atriplex salvaje a las hombreras, esta les imbuye una mejora potente aleatoria.$B$BPara este patrón necesitaré seis refuerzos para armadura gruesa y una atriplex salvaje. No deberías tener problemas para conseguirlos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2855, 'esMX', 'Sobrehombros de cuero salvaje', 'Los sobrehombros de cuero salvaje ofrecen una gran protección a la parte superior de los brazos y a los hombros de quien las lleva. Como ocurre con la armadura de cuero salvaje, cuando se cose la atriplex salvaje a las hombreras, esta les imbuye una mejora potente aleatoria.$B$BPara este patrón necesitaré seis refuerzos para armadura gruesa y una atriplex salvaje. No deberías tener problemas para conseguirlos.', 'Lleva 6 refuerzos para armadura gruesos y una atriplex salvaje a Jangdor Zanco Veloz al Campamento Mojache.', '', 'Vuelve con: Jangdor Zanco Veloz. Zona: Feralas.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2855, 'frFR', 'Les épaulières en cuir fauve', 'Apporter 6 Renforts d\'armure épais et 1 Sauvageonne à Jangdor Rôdeur-agile au Camp Mojache.', 'Les épaulières en cuir fauve protègent efficacement le haut des bras et les épaules. Comme toutes les pièces d\'armure en cuir fauve, elles bénéficient d\'une amélioration puissante, mais aléatoire, grâce aux sauvageonnes.$B$BPour ce modèle, je demande six renforts d\'armure épais et une sauvageonne. Cela ne devrait pas vous poser trop de problèmes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2855, 'koKR', '야생 가죽 어깨보호구', '모자케 야영지에 있는 장도르 스위프트스트라이더에게 두꺼운 방어구 강화도구 6개와 야생덩굴 1개를 가져가야 합니다.', '야생 가죽 어깨보호구는 착용하는 자의 골격을 아주 잘 받쳐 주기 때문에 팔 위쪽과 어깨를 보호하는 데 최고라고 할 수 있지. 다른 야생 가죽 방어구와 마찬가지로 야생 포도 덩굴을 바느질해 붙이면 강력한 효과가 무작위로 더해지게 되는 것이네.$B$B이 도안을 얻으려면 두꺼운 방어구 강화도구 6개와 야생덩굴 하나가 필요하오. 자네한테 그리 어려운 임무는 아닐 걸세.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2855, 'ruRU', 'Дикарские кожаные наплечники', 'Принесите 6 комплектов накладок из плотной кожи и дикую лозу Джангору Быстрому Страннику в Лагерь Мохаче.', 'Дикарские кожаные наплечники обеспечивают превосходную защиту как плеч, так и верхней части руки. Как и все дикарские кожаные доспехи, они прошиты дикиой лозой, что придает им мощные, но непостоянные магические свойства.$b$bДля изготовления изделия по этой выкройке мне потребуется шесть комплектов накладок из плотной кожи и дикий виноград. Для тебя это не слишком сложное задание...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2855, 'zhCN', '蛮皮护肩', '给莫沙彻营地的杉多尔·迅蹄带去6块厚重护甲片和1株野葡萄。', '蛮皮护肩能够很好地支撑穿戴者的身形，为其上臂和肩部提供了极好的保护。和所有的蛮皮护甲一样，制作时所使用的野葡萄会使其附带一种强大的随机魔法效果。$B$B如果你想要这种护甲的图样，就给我弄六块厚重护甲片和一株野葡萄来，这对你来说应该不算什么过分的要求。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2855, 'zhTW', '蠻皮護肩', '給莫沙徹營地的杉多爾·迅蹄帶去6塊厚重護甲片和1株野葡萄。', '蠻皮護肩能夠很好地支撐穿戴者的身形，為其上臂和肩部提供了極好的保護。和所有的蠻皮護甲一樣，製作時所使用的野葡萄會使其附帶一種強大的隨機魔法效果。$B$B如果你想要這種護甲的圖樣，就給我弄六塊厚重護甲片和一株野葡萄來，這對你來說應該不算什麼過分的要求。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2856, 'deDE', 'Wildlederweste', 'Bringt Jangdor Flinkschreiter im Camp Mojache 2 Schildkrötenschuppenbrustplatten, 2 Schildkrötenschuppenhandschuhe und eine Wildranke.', 'Wildlederwesten sind die Grundlage jeglicher Wildlederrüstungssets. Die Struktur der Weste und das Wildrankengeflecht darin erzeugen ein Produkt, das sowohl maximalen Schutz für den Torso bietet, als auch der mächtigen Magie erlaubt, sich in dem Gewand auszubreiten.$B$BFür dieses Muster werde ich 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke benötigen. Dies ist keine einfache Lektion, aber eine, für die Ihr sicherlich das nötige Geschick habt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2856, 'esES', 'El Jubón de cuero salvaje', 'Lleva 2 corazas de caparazón de tortuga, 2 guantes de caparazón de tortuga y una atriplex salvaje a Jangdor Zanco Veloz al Campamento Mojache.', 'Los jubones de cuero salvaje son la pieza principal de cualquier conjunto de armadura de cuero salvaje. La tela del jubón se amalgama con la atriplex salvaje para crear un producto que ofrece la máxima protección para el torso a la vez que permite que su potente magia se abra paso a través de ella.$B$BPara este patrón, necesitaré 2 petos de caparazón de tortuga, 2 pares de guantes de caparazón de tortuga y 1 atriplex salvaje. No es una lección fácil, pero tú tienes sin duda la habilidad necesaria para aprenderla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2856, 'esMX', 'El Jubón de cuero salvaje', 'Los jubones de cuero salvaje son la pieza principal de cualquier conjunto de armadura de cuero salvaje. La tela del jubón se amalgama con la atriplex salvaje para crear un producto que ofrece la máxima protección para el torso a la vez que permite que su potente magia se abra paso a través de ella.$B$BPara este patrón, necesitaré 2 petos de caparazón de tortuga, 2 pares de guantes de caparazón de tortuga y 1 atriplex salvaje. No es una lección fácil, pero tú tienes sin duda la habilidad necesaria para aprenderla.', 'Lleva 2 corazas de caparazón de tortuga, 2 guantes de caparazón de tortuga y una atriplex salvaje a Jangdor Zanco Veloz al Campamento Mojache.', '', 'Vuelve con: Jangdor Zanco Veloz. Zona: Feralas.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2856, 'frFR', 'Le gilet en cuir fauve', 'Rapporter 2 Cuirasses en écailles de tortue, 2 paires de Gants en écailles de tortue et une Sauvageonne à Jangdor Rôdeur-agile au Camp Mojache.', 'Le gilet est la pièce maîtresse de toute armure en cuir fauve. Il procure une protection maximale du torse tout en permettant à la puissante magie des sauvageonnes de se diffuser dans l’armure.$B$BPour ce patron, il me faut deux cuirasses et deux paires de gants en écailles de tortue, et aussi une sauvageonne. Ce n\'est pas une leçon facile, mais vous disposez clairement des talents pour la mener à bien.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2856, 'koKR', '야생 가죽 조끼', '모자케 야영지에 있는 장도르 스위프트스트라이더에게 거북 껍질 가슴보호갑 2개, 거북 껍질 장갑 2개, 야생덩굴 1개를 가져가야 합니다.', '야생 가죽 조끼는 모든 야생 가죽 방어구 세트의 기본이지. 조끼의 틀과 야생덩굴이 한데 잘 엮여 조끼 전체에 강력한 마법이 흐르게 되기 때문에 몸통을 최대한으로 보호할 수 있네.$B$B이 도안을 얻으려면 거북 껍질 가슴보호갑 2개와 거북 껍질 장갑 2개, 야생덩굴 1개가 필요하네. 쉬운 수업은 아니지만 자네 실력이라면 이 정도는 틀림없이 해낼 수 있을 걸세.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2856, 'ptBR', 'Colete de couro silvestre', 'Leve 2 Peitorais de Escama de Tartaruga, 2 Luvas de Escama de Tartaruga e uma Vinha Selvagem para Jangdor Passo Veloz na Aldeia Mojache.', 'Coletes são o carro-chefe de qualquer armadura de couro silvestre. O caimento do colete em combinação com a vinha selvagem resulta em um produto de alto grau de proteção do tronco enquanto permite a poderosa ação da magia na vestimenta.$B$BPara este molde, vou precisar de dois peitorais de escama de tartaruga, dois pares de luvas de escama de tartaruga e uma vinha selvagem. É uma tarefa árdua, mas alguém como você não terá dificuldade em fazê-la para mim.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2856, 'ruRU', 'Дикарский кожаный жилет', 'Принесите 2 черепашьих чешуйчатых кирасы, 2 пары черепашьих чешуйчатых перчаток и дикую лозу Джангору Быстрому Страннику в Лагерь Мохаче.', 'Дикарский кожаный жилет – основа любых дикарских кожаных доспехов. Основа жилета переплетена диким виноградом, что дает максимальную защиту торса и позволяет использовать мощную магию растения.$b$bДля изготовления предмета по этой выкройке мне потребуется две черепашьих чешуйчатых кирасы, две пары черепашьих чешуйчатых перчаток и дикая лоза. Изготовить это непросто, но, думаю, ты справишься.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2856, 'zhCN', '蛮皮外衣', '给莫沙彻营地的杉多尔·迅蹄带去2件龟鳞胸甲、2副龟鳞手套和1株野葡萄。', '蛮皮外衣是蛮皮护甲中最主要的部分，它的结构以及编织于其中的野葡萄能使其在具有很强的保护作用的同时，还附带着强大的魔法力量。$B$BB如果你想要这种护甲的图样，就给我弄两件龟鳞胸甲、两副龟鳞手套和一株野葡萄来。这不是件容易的活儿，但你一定可以完成它。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2856, 'zhTW', '蠻皮外衣', '給莫沙徹營地的杉多爾·迅蹄帶去2件龜鱗胸甲、2副龜鱗手套和1株野葡萄。', '蠻皮外衣是蠻皮外衣中最主要的部分，它的結構以及編織於其中的野葡萄能使其在具有很強的保護作用的同時，還附帶著強大的魔法力量。$B$BB如果你想要這種外衣的圖樣，就給我弄兩件龜鱗外衣、兩副龜鱗手套和一株野葡萄來。這不是件容易的活兒，但你一定可以完成它。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2857, 'deDE', 'Wildlederhelm', 'Bringt Jangdor Flinkschreiter im Camp Mojache 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke.', 'Ein Wildlederhelm bietet dem Träger verbesserten Kopfschutz, ohne dabei an der Bequemlichkeit zu sparen. Wie bei allen Wildlederrüstungen erzeugt die eingearbeitete Wildranke eine mächtige, aber zufällige magische Verstärkung der Struktur des Helms. Lederträger werden dieses Stück auf jeden Fall haben wollen!$B$BFür dieses Muster brauche ich 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke. Schließt diese Lektion erfolgreich ab und das Muster gehört Euch!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2857, 'esES', 'Casco de cuero salvaje', 'Lleva 2 guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y una atriplex salvaje a Jangdor Zanco Veloz al Campamento Mojache.', 'Un casco de cuero salvaje ofrece a quien lo lleva una gran protección de las áreas vitales de la cabeza sin sacrificar el confort. Como en todas las partes de las armaduras de cuero salvaje, la atriplex salvaje integrada en el casco, le imbuye una potente mejora mágica aleatoria. ¡Quienes aprecian el cuero, sin duda desearán tener esta pieza!$B$BPara este patrón necesitaré dos guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y una atriplex salvaje. ¡Si me traes eso, el patrón será tuyo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2857, 'esMX', 'Casco de cuero salvaje', 'Lleva 2 guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y una atriplex salvaje a Jangdor Zanco Veloz al Campamento Mojache.', 'Un casco de cuero salvaje ofrece a quien lo lleva una gran protección de las áreas vitales de la cabeza sin sacrificar el confort. Como en todas las partes de las armaduras de cuero salvaje, la atriplex salvaje integrada en el casco, le imbuye una potente mejora mágica aleatoria. ¡Quienes aprecian el cuero, sin duda desearán tener esta pieza!$B$BPara este patrón necesitaré dos guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y una atriplex salvaje. ¡Si me traes eso, el patrón será tuyo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2857, 'frFR', 'Le casque en cuir fauve', 'Rapporter 2 Tuniques de la nuit, 2 Bandeaux de la nuit et 1 Sauvageonne à Jangdor Rôdeur-agile au Camp Mojache.', 'Un casque en cuir fauve offre au porteur une protection optimale sur les parties vitales sans sacrifier au confort. Comme pour l\'armure en cuir fauve, les sauvageonnes lui confèrent une amélioration puissante et aléatoire. Les porteurs d\'armure en cuir fauve souhaiteront avoir cette pièce d\'équipement !$B$BPour ce modèle, j\'ai besoin de deux tuniques de la nuit, deux bandeaux de la nuit et d\'une sauvageonne. Rapportez-les et le patron est à vous !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2857, 'itIT', 'Wild Leather Helmet', 'Bring 2 Nightscape Tunics, 2 Nightscape Headbands, and a Wildvine to Jangdor Swiftstrider in Camp Mojache.', 'A Wild Leather helmet offers the wearer superior protection to the vital areas of the head without a sacrifice in comfort.  As with all Wild Leather armor, the integrated wildvine yields a powerful but random magic imbuement to the helm\'s frame.  Leather wearers will definitely want this piece!$B$BFor this pattern, I will need two Nightscape tunics, 2 Nightscape headbands, and a wildvine.  Complete this lesson and the pattern will be yours!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2857, 'koKR', '야생 가죽 보호모', '모자케 야영지에 있는 장도르 스위프트스트라이더에게 밤하늘 튜닉 2개, 밤하늘 머리띠 2개, 야생덩굴 1개를 가져가야 합니다.', '야생 가죽 보호모는 편안하면서도 머리의 중요한 부위를 아주 우수하게 보호해 주지. 모든 야생 가죽 방어구와 마찬가지로 여기에 포함된 야생덩굴이 보호모의 기본틀에 강력한 효과를 무작위로 부여하는 것이라네. 가죽 방어구를 착용하는 사람들은 틀림없이 이런 보호모를 원할 걸세!$B$B이 도안을 얻으려면 밤하늘 튜닉 2개, 밤하늘 머리띠 2개, 야생덩굴 1개가 필요하네. 이 수업을 마치면 도안은 자네 것일세!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2857, 'ptBR', 'Capacete de couro silvestre', 'Leve 2 Túnicas da Escapada Noturna, 2 Bandanas da Escapada Noturna e uma Vinha Selvagem para Jangdor Passo Veloz na Aldeia Mojache.', 'Um capacete de couro silvestre oferece a quem o veste alta proteção às áreas vitais da cabeça sem causar desconforto. Como em toda armadura de couro silvestre, a integração com vinha selvagem proporciona um poderoso, embora aleatório, incremento ao capacete. Os usuários de couro desejarão esse item!$B$BPara o molde, preciso de duas túnicas da Escapada Noturna, duas bandanas da Escapada Noturna e uma vinha selvagem. Complete a tarefa e o molde será seu!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2857, 'ruRU', 'Дикарский кожаный шлем', 'Принесите 2 ночных мундира, 2 ночных головных повязки и дикую лозу Джангору Быстрому Страннику в Лагере Мохаче.', 'Дикарский кожаный шлем обеспечивает лучшую защиту головы, при этом не менее удобен, чем обычные шлемы. Как и во всех дикарских кожаных доспехах, в основу шлема вплетен дикий виноград, наделяющий его мощными, но непостоянными магическими свойствами. Тем, кто ходит в кожаных доспехах, эта вещица наверняка пригодится!$b$bНо для изготовления изделия по этой выкройке мне понадобятся 2 ночных мундира, 2 ночных головных повязки и дикая лоза. Принеси это все, и выкройка твоя!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2857, 'zhCN', '蛮皮头盔', '给莫沙彻营地的杉多尔·迅蹄带去2件夜色外套、2条夜色头巾和1株野葡萄。', '蛮皮头盔可以为你的头部提供极好的保护，与此同时，它也具有相当的舒适性。和所有的蛮皮护甲一样，制作头盔时使用的野葡萄会使它产生一种强大的随机魔法效果。对于穿着皮甲的人来说，这东西可是非常不错的！$B$B想要得到这顶头盔的图样的话，就给我带两件夜色外套、两条夜色头巾和一株野葡萄来。完成这个任务之后，图样就是你的了！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2857, 'zhTW', '蠻皮盔帽', '給莫沙徹營地的杉多爾·迅蹄帶去2件夜景外套、2條夜景頭巾和1株野葡萄。', '蠻皮盔帽可以為你的頭部提供極好的保護，與此同時，它也具有相當的舒適性。和所有的蠻皮護甲一樣，製作盔帽時使用的野葡萄會使它產生一種強大的隨機魔法效果。對於穿著皮甲的人來說，這東西可是非常不錯的!$B$B想要得到這頂盔帽的圖樣的話，就給我帶兩件夜景外套、兩條夜景頭巾和一株野葡萄來。完成這個任務之後，圖樣就是你的了!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2858, 'deDE', 'Wildlederstiefel', 'Euer Wissen über die Herstellung von Wildlederrüstungen wächst, $n, aber trotzdem habt Ihr noch einiges zu lernen.$B$BWildlederstiefel sind die Grundlage eines jeden Sets. Diese Stiefel werden so gefertigt, dass sie den Fuß in Bequemlichkeit hüllen, während sie ihn gleichzeitig gegen die Gefahren des Abenteurerlebens wappnen. Die weichen Sohlen sind ein Segen für diejenigen, die bei ihrer Fußbekleidung auf leise Bewegung Wert legen.$B$BFür dieses Muster müsst Ihr mir 2 Hosen des Nachtschleichers und 2 Stiefel des Nachtschleichers herstellen. Ich werde außerdem zwei Wildranken benötigen, nicht nur eine. Wenn Ihr diese Dinge habt, werden wir weitermachen.', 'Bringt Jangdor Flinkschreiter im Camp Mojache 2 Hosen des Nachtschleichers, 2 Stiefel des Nachtschleichers und 2 Wildranken.', '', 'Kehrt zu Jangdor Flinkschreiter in Feralas zurück.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2858, 'esES', 'Botas de cuero salvaje', 'Lleva 2 pantalones Nochefugaz, 2 botas Nochefugaz y 2 atriplex salvajes a Jangdor Zanco Veloz al Campamento Mojache.', 'Tu conocimiento de la armadura de cuero salvaje crece, $n; pero aún te quedan cosas que aprender.$B$BLas botas de cuero salvaje son la piedra angular de cualquier conjunto de armadura. Estas botas se crean para envolver al pie en confort a la vez que lo refuerzan para protegerlo de los peligros de una vida aventurera. Sus suelas blandas son una bendición para quienes necesitan moverse con sigilo.$B$BPara este patrón necesito 2 pantalones Nochefugaz y 2 botas Nochefugaz. Y también necesito 2 atriplex salvajes, no me basta con una. Cuando tengas estos objetos, procederemos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2858, 'esMX', 'Botas de cuero salvaje', 'Lleva 2 pantalones Nochefugaz, 2 botas Nochefugaz y 2 atriplex salvajes a Jangdor Zanco Veloz al Campamento Mojache.', 'Tu conocimiento de la armadura de cuero salvaje crece, $n; pero aún te quedan cosas que aprender.$B$BLas botas de cuero salvaje son la piedra angular de cualquier conjunto de armadura. Estas botas se crean para envolver al pie en confort a la vez que lo refuerzan para protegerlo de los peligros de una vida aventurera. Sus suelas blandas son una bendición para quienes necesitan moverse con sigilo.$B$BPara este patrón necesito 2 pantalones Nochefugaz y 2 botas Nochefugaz. Y también necesito 2 atriplex salvajes, no me basta con una. Cuando tengas estos objetos, procederemos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2858, 'frFR', 'Les bottes en cuir fauve', 'Rapporter 2 Pantalons de la nuit, 2 paires de Bottes de la nuit et 2 Sauvageonnes à Jangdor Rôdeur-agile au Camp Mojache.', 'Vous avez déjà beaucoup appris, $n. Mais ce n’est pas tout. L\'étape suivante concerne les bottes en cuir fauve.$B$BElles protègent bien les pieds tout en restant confortables. Les semelles souples sont une bénédiction pour ceux qui aiment se déplacer discrètement.$B$BPour ces bottes, j\'ai besoin de deux pantalons de la nuit et deux paires de bottes de la nuit, ainsi que de deux sauvageonnes. Lorsque vous aurez tout cela, nous continuerons.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2858, 'ruRU', 'Дикарские кожаные сапоги', 'Тебе известно уже немало, $n. Но многое еще остается постичь. Теперь ты узнаешь о дикарских кожаных сапогах – ведь без обуви не обходится ни один доспех.$b$bНоге в них удобно и приятно, и дальний путь в них не так тяжел. Мягкая же подошва позволяет при необходимости ступать беззвучно.$b$bДля изготовления их по выкройке мне потребуются две пары ночных штанов и две пары ночных сапог. И два экземпляра дикого винограда – запомни, два, а не один! Добудешь все это – продолжим.', 'Принесите 2 пары ночных штанов, 2 пары ночных сапог и 2 экземпляра дикой лозы Джангору Быстрому Страннику в Лагере Мохаче.', '', 'Вернитесь к Джангору Быстрому Страннику в Фералас.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2858, 'zhCN', '蛮皮战靴', '你现在已经学到了很多东西，$N，但你仍有更多的东西要学习。现在我们来谈谈蛮皮战靴，它是这一套装备的基石。$B$B这种靴子舒适合脚，可以让你的双脚在长途跋涉之后仍然轻松自如。柔软的鞋底对于那些喜欢悄悄行动的人来说再合适不过了。$B$B如果你想要得到它的图样，就给我拿两条夜色短裤和两双夜色长靴来。另外我还需要两株野葡萄……只有一株的话可不够。拿到以上这些东西之后，就把它们交给我。', '给莫沙彻营地的杉多尔·迅蹄带去2条夜色短裤、2双夜色长靴和2株野葡萄。', '', '去找菲拉斯的杉多尔·迅蹄。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2859, 'deDE', 'Wildledergamaschen', 'Ihr seid so weit, komplexere Lektionen zu meistern, $n. Wildledergamaschen gehören zur robustesten und stärksten Lederausrüstung überhaupt. Bauch, Hüften, Schenkel und Solarplexus werden ungeheuer gut geschützt. Und wenn dann noch die Verstärkung hinzukommt, die die Wildranke bewirkt, werden Euch die Abenteurer, die diese Rüstung besitzen, jedes Mal danken, wenn sie Euch sehen.$B$BUm Euch dieses Muster zu geben, benötige ich 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken.', 'Bringt Jangdor Flinkschreiter im Camp Mojache 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken.', '', 'Kehrt zu Jangdor Flinkschreiter in Feralas zurück.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2859, 'esES', 'Leotardos de cuero salvaje', 'Lleva 2 yelmos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 atriplex salvajes a Jangdor Zanco Veloz al Campamento Mojache.', 'Ya estás $glisto:lista; para lecciones más complejas, $n. Los leotardos de cuero salvaje son uno de los artículos de cuero de mayor solidez y potencia. Ofrecen una increíble protección a la cintura, las pantorrillas y a otros órganos vitales. Si los combinas con la mejora que proporciona la atriplex salvaje, los aventureros que los vistan te lo agradecerán cada vez que te vean.$B$BPara que te enseñe este patrón, tienes que traerme 2 cascos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 plantas de atriplex salvaje.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2859, 'esMX', 'Leotardos de cuero salvaje', 'Lleva 2 yelmos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 atriplex salvajes a Jangdor Zanco Veloz al Campamento Mojache.', 'Ya estás $glisto:lista; para lecciones más complejas, $n. Los leotardos de cuero salvaje son uno de los artículos de cuero de mayor solidez y potencia. Ofrecen una increíble protección a la cintura, las pantorrillas y a otros órganos vitales. Si los combinas con la mejora que proporciona la atriplex salvaje, los aventureros que los vistan te lo agradecerán cada vez que te vean.$B$BPara que te enseñe este patrón, tienes que traerme 2 cascos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 plantas de atriplex salvaje.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2859, 'frFR', 'Les jambières en cuir fauve', 'Rapporter 2 Casques en écailles de tortue, 2 Brassards en écailles de tortue et 2 Sauvageonnes à Jangdor Rôdeur-agile au Camp Mojache.', 'Vous pouvez maintenant apprendre des techniques plus complexes, $n. Les jambières en cuir fauve sont un des équipements en cuir les plus résistants et les plus puissants. La taille, les hanches et les parties vitales sont particulièrement bien protégées. Les aventuriers qui l\'utiliseront apprécieront grandement cette protection combinant l\'amélioration apportée par la sauvageonne.$B$BPour ce modèle, j\'ai besoin de deux casques en écailles de tortue, de deux brassards en écailles de tortue et de deux sauvageonnes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2859, 'koKR', '야생 가죽 다리보호구', '모자케 야영지에 있는 장도르 스위프트스트라이더에게 거북 껍질 투구 2개, 거북 껍질 팔보호구 2개, 야생덩굴 2개를 가져가야 합니다.', '이제 좀 더 복잡한 기술을 익힐 때가 되었군. 야생 가죽 다리보호구는 가죽 장비 중에서도 가장 튼튼하고 강력한 장비에 속한다네. 허리, 허벅지, 종아리에 이르기까지 모두 강력한 보호할 수 있지. 거기에 야생덩굴에서 얻을 수 있는 강력한 효과까지 합치면 이걸 가진 모험가들이 자네를 볼 때마다 감사하게 될 것일세.$B$B이 도안을 얻으려면 거북 껍질 투구 2개, 거북 껍질 팔보호구 2개, 야생덩굴 2개가 필요하네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2859, 'ptBR', 'Perneiras de couro silvestre', 'Leve 2 Elmos de Escama de Tartaruga, 2 Braçadeiras de Escama de Tartaruga e 2 Vinhas Selvagens para Jangdor Passo Veloz na Aldeia Mojache.', 'Você já está $gpreparado:preparada; para aprender lições mais complexas, $n. Perneiras de couro silvestre são um dos equipamentos mais robustos e poderosos. Elas dão enorme proteção à cintura, coxas, panturrilhas e órgãos vitais. Acompanhadas pelas melhorias proporcionadas pela vinha selvagem, os aventureiros que as usarem serão sempre gratos a você.$B$BPara adquirir este molde, preciso de dois elmos de escama de tartaruga, duas braçadeiras de escama de tartaruga e duas vinhas selvagens.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2859, 'ruRU', 'Дикарские кожаные поножи', 'Принесите два шлема из черепашьего панциря, две пары черепашьих чешуйчатых наручей и два экземпляра дикой лозы Джангору Быстрому Страннику в Лагерь Мохаче.', 'Ну, $n, теперь мы можем перейти к более сложным вещам. Дикарские кожаные поножи – одна из наиболее прочных и надежных разновидностей кожаного доспеха. Они отлично защищают ноги. А поскольку в них вшита еще и дикая лоза, дающая дополнительную защиту, любой искатель приключений скажет тебе спасибо за такие поножи.$b$bДля изготовления изделия по этой выкройке мне понадобятся два шлема из черепашьего панциря, две пары черепашьих чешуйчатых наручей, и два экземпляра дикого винограда.', '', '', '', '', '', 'Вернитесь к Джангору Быстрому Страннику в Фералас.', 0);
INSERT INTO `quest_template_locale` VALUES (2859, 'zhCN', '蛮皮护腿', '给莫沙彻营地的杉多尔·迅蹄带去2顶龟鳞头盔、2副龟鳞护腕和2株野葡萄。', '看来你已经准备好学习更加复杂的课程了，$n。蛮皮护胫是最坚硬和强大的皮制系列装备中的一样。野兽系列皮装备让你的胸部，大腿，小腿和其他致命的部位都被保护起来。再加上野葡萄所起的辅助作用，拥有这种装备的冒险者一定会永远感谢你的。$B$B如果你想要得到这个图样的话，带给我两顶龟鳞头盔，两付龟鳞护腕和两株野葡萄。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2859, 'zhTW', '蠻皮護腿', '給莫沙徹營地的杉多爾·迅蹄帶去2頂龜鱗頭盔、2副龜鱗護腕和2株野葡萄。', '看來你已經準備好學習更加複雜的課程了，$n。蠻皮護脛是最堅硬和強大的皮制系列裝備中的一樣。野獸系列皮裝備讓你的胸部，大腿，小腿和其他致命的部位都被保護起來。再加上野葡萄所起的輔助作用，擁有這種裝備的冒險者一定會永遠感謝你的。$B$B如果你想要得到這個圖樣的話，帶給我兩頂龜鱗頭盔，兩付龜鱗護腕和兩株野葡萄。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2860, 'deDE', 'Meister des Wildleders', '$n, Ihr habt alles erlernt, was ich Euch lehren kann. Es gibt allerdings noch ein letztes Wildlederrüstungsmuster. Ich werde nicht von Euch verlangen, Euch unnötig dafür abzuplagen. Ihr habt es Euch bereits mehr als verdient. Jedoch kann ich dieses Muster nicht einfach mit Euch teilen.$B$BMein Lehrer, derjenige, der mich in dieses Handwerk einführte, muss Euch belohnen. Bringt diese Notiz zu Una. Sie lehrt Lederverarbeitung in Donnerfels. Zeigt ihr den Brief und lasst Euch von ihr als Gleichgestellten anerkennen, so wie ich das auch tue.', 'Gebt Una, der Lederverarbeitungsmeisterin von Donnerfels, Jangdors Schreiben.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2860, 'esES', 'Maestro de cuero salvaje', '$n, ya has aprendido todo lo que yo puedo enseñarte. Pero te queda por aprender un último patrón de la armadura de cuero salvaje. No te pediré que trabajes más, pues ya te has ganado el derecho de aprender este patrón. Pero no es un patrón que sea libre de compartir contigo.$B$BMi profesor, el que me inició en la peletería, es quien debe enseñártelo. Lleva esta nota a Una. Enseña peletería en Cima del Trueno. Muéstrale la carta y te tratará como a $gun:una; igual, como hago yo.', 'Entrega la carta de Jangdor a Una, el maestro curtidor de Cima del Trueno.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2860, 'esMX', 'Maestro de cuero salvaje', '$n, ya has aprendido todo lo que yo puedo enseñarte. Pero te queda por aprender un último patrón de la armadura de cuero salvaje. No te pediré que trabajes más, pues ya te has ganado el derecho de aprender este patrón. Pero no es un patrón que sea libre de compartir contigo.$B$BMi profesor, el que me inició en la peletería, es quien debe enseñártelo. Lleva esta nota a Una. Enseña peletería en Cima del Trueno. Muéstrale la carta y te tratará como a $gun:una; igual, como hago yo.', 'Entrega la carta de Jangdor a Una, el maestro curtidor de Cima del Trueno.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2860, 'frFR', 'Maître du cuir fauve', '$n, vous avez appris tout ce que je pouvais vous enseigner. Il reste néanmoins un dernier élément de l’armure en cuir fauve à apprendre. Je ne vous demanderai rien pour celui-là, vous l\'avez mérité. Mais je ne peux vous le donner.$B$BMon maître est la seule personne à pouvoir vous l’enseigner. Remettez cette lettre à Una, qui enseigne le travail du cuir aux Pitons-du-Tonnerre. Montrez-lui la lettre et laissez-la vous reconnaître comme son égal, ainsi que je l\'ai fait.', 'Donner la Lettre de Jangdor à Una, le Maître tanneur des Pitons-du-Tonnerre.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2860, 'ruRU', 'Мастер школы Дикаря', 'Что ж, $n, теперь ты знаешь все, чему я мог тебя научить. Однако остался еще один предмет из дикарских кожаных доспехов, выкройки которого у тебя нет. Я не стану заставлять тебя трудиться, чтобы ее получить: ты и так сполна $gрасплатился:расплатилась; за все. Однако же отдать тебе его сам я не могу.$b$bТы получишь награду от моей наставницы, той, которая посвятила в тайны нашего ремесла меня самого. Отнеси эту записку Уне. Она обучает кожевников в Громовом Утесе. Покажи ей записку, и пусть она приветствует тебя как $gравного:равную;, подобно мне самому.', 'Отдайте письмо Джангора Уне, главному кожевнику Громового Утеса.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2860, 'zhCN', '蛮皮护甲大师', '$N，我能教你的你已经全部都学会了。蛮皮护甲只剩下一种式样你还没有学会了。我不会要求你为最后的式样无谓地浪费时间和精力，你可以直接得到它，还有一些……但我不能自己传授给你。我的老师，就是带我进入这一行的人，她会教你该怎么做的。$B$B带这封信给犹纳；她在雷霆崖教授皮革制作。给她看这封信，让她像我一样地认同你。', '将杉多尔的信交给雷霆崖的制皮大师犹纳。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2861, 'deDE', 'Tabethas Aufgabe', 'Sprecht mit Tabetha in den Düstermarschen.', 'Gerüchte besagen, dass Tabetha, eine Magierkollegin, die tief in den Düstermarschen lebt, ein Hühnchen mit einem gewissen Sandtroll in Zul\'Farrak zu rupfen hat. Wenn Ihr mit Tabetha sprecht, kann sie Euch sicher mehr dazu sagen.$B$BUnd nehmt ein paar Freunde mit, $n. Falls Tabetha Euch nach Zul\'Farrak schickt, wollt Ihr sicher nicht allein hingehen.$B$BIhr findet Tabethas Hütte westlich von Theramore und direkt nördlich der Ruinen der Steinbrecher.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2861, 'esES', 'La tarea de Tabetha', 'Habla con Tabetha en el Marjal Revolcafango.', 'Se rumorea que Tabetha, una compañera maga que vive en las profundidades del Marjal Revolcafango, tiene que arreglar cuentas con un trol de arena de Zul\'Farrak. Si hablas con Tabetha te dirá más.$B$BY lleva contigo algunos amigos, $n. Si Tabetha quiere que vayas a Zul\'Farrak no querrás ir $gsolo:sola;.$B$BEncontrarás la cabaña de Tabetha al oeste de Theramore, al norte de las Ruinas Quebrantarrocas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2861, 'esMX', 'La tarea de Tabetha', 'Habla con Tabetha en el Marjal Revolcafango.', 'Se rumorea que Tabetha, una compañera maga que vive en las profundidades del Marjal Revolcafango, tiene que arreglar cuentas con un trol de arena de Zul\'Farrak. Si hablas con Tabetha te dirá más.$B$BY lleva contigo algunos amigos, $n. Si Tabetha quiere que vayas a Zul\'Farrak no querrás ir $gsolo:sola;.$B$BEncontrarás la cabaña de Tabetha al oeste de Theramore, al norte de las Ruinas Quebrantarrocas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2861, 'frFR', 'La mission de Tabetha', 'Parler à Tabetha dans le marécage d\'Âprefange.', 'La rumeur dit que Tabetha, une collègue mage qui vit loin dans le marécage d\'Âprefange, a un compte à régler avec une certaine trolle des sables à Zul\'Farrak. Si vous parlez à Tabetha, elle pourra vous en dire davantage.$B$BEt invitez quelques amis, $n. Si Tabetha vous envoie à Zul\'Farrak, il ne faut pas y aller $gtout seul:toute seule;.$B$BVous trouverez le cottage de Tabetha à l\'ouest de Theramore, juste au nord des ruines Cognepierres.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2861, 'itIT', 'Tabetha\'s Task', 'Speak with Tabetha in Dustwallow Marsh.', 'Rumor is that Tabetha, a mage colleague who lives deep in Dustwallow Marsh, has a bone to pick with a certain sand troll in Zul\'Farrak.  If you speak with Tabetha she can tell you more.$B$BAnd bring some friends with you, $n.  If Tabetha has you go to Zul\'Farrak then you\'re not going to want to go alone.$B$BYou will find Tabetha\'s cottage west of Theramore, and just north of the Stonemaul Ruin.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2861, 'koKR', '타베사의 임무', '먼지진흙 습지대에 있는 타베사와 대화해야 합니다.', '듣자 하니, 먼지진흙 습지대 깊은 곳에 사는 동료 마법사 타베사가 줄파락에 사는 어떤 트롤과 해결할 문제가 있다고 해요. 타베사에게 가면 더 자세히 얘기해 줄 거예요.$B$B참, 친구도 몇 명 데리고 가세요, $n. 타베사가 당신을 줄파락으로 보낼지도 몰라요. 그렇다면 혼자 가는 것은 바람직하지 않아요.$B$B타베사의 오두막은 테라모어 서쪽, 돌망치 폐허의 바로 북쪽에 있어요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2861, 'ptBR', 'Tabetha\'s Task', 'Speak with Tabetha in Dustwallow Marsh.', 'Rumor is that Tabetha, a mage colleague who lives deep in Dustwallow Marsh, has a bone to pick with a certain sand troll in Zul\'Farrak. If you speak with Tabetha she can tell you more.$B$BAnd bring some friends with you, $n. If Tabetha has you go to Zul\'Farrak then you\'re not going to want to go alone.$B$BYou will find Tabetha\'s cottage west of Theramore, and just north of the Stonemaul Ruin.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2861, 'ruRU', 'Миссия Табеты', 'Поговорите с Табетой в Пылевых топях.', 'Ходят слухи, что у Табеты, мага, живущего в Пылевых топях, серьезный зуб на одну песчаную троллиху из Зул\'Фаррака. Если ты поговоришь с Табетой, она, конечно, расскажет тебе больше.$B$BИ захвати с собой друзей, $n. Если она пошлет тебя в Зул\'Фаррак, а скорее всего так и будет, то лучше тебе там в одиночку не появляться.$B$BТы найдешь дом Табеты к западу от Терамора и к северу от руин Каменного Молота.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2861, 'zhCN', '塔贝萨的任务', '与尘泥沼泽的塔贝萨谈一谈。', '据说有个名叫塔贝萨的法师想要某个祖尔法拉克巨魔的命。如果你去找塔贝萨的话，她会让你知道详情的。$B$B别忘了带上几个伙伴，$n。如果塔贝萨真的让你去祖尔法拉克的话，你最好不要独自前往。$B$B塔贝萨的小屋就在尘泥沼泽里，准确地说，是在塞拉摩的西边，石槌废墟的北边。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2861, 'zhTW', '塔貝薩的任務', '與塵泥沼澤的塔貝薩談話。', '據說有個名叫塔貝薩的法師想要某個祖爾法拉克食人妖的命。如果你去找塔貝薩的話，她會讓你知道詳情的。$B$B別忘了帶幾個夥伴，$n。如果塔貝薩真的讓你去祖爾法拉克的話，你最好不要獨自前往。$B$B塔貝薩的小屋就在塵泥沼澤裡，準確地說，是在塞拉摩的西邊，石槌廢墟的北邊。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2862, 'deDE', 'Krieg gegen die Waldpfoten', 'Bringt Hadoken Flinkschreiter im Camp Mojache 10 Mähnen eines Waldpfotengnolls.', 'Die Gnolle... sie sind mehr als nur ein Ärgernis. Sie sind eine konstante Bedrohung für die Existenz dieses gesamten Lagers! Es ist unwichtig, ob sie Eingeborene des Feralas sind oder nicht. Wenn wir uns nicht gegen sie wappnen, werden wir uns eines Tages aus dem gesamten Gebiet vertrieben sehen.$B$BDie Gnollangriffe auf das Lager wurden in letzter Zeit heftiger. Wir brauchen Eure Hilfe dabei, ihre Anzahl zu reduzieren. Bringt mir zehn ihrer Mähnen und ich werde Euch stattlich für Eure Mühe entlohnen.$B$BLos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2862, 'esES', 'En guerra con los Zarpaleña', 'Lleva 10 cabelleras de gnolls Zarpaleña a Hadoken Zanco Veloz al Campamento Mojache.', 'Los gnolls... son algo más que una molestia. ¡Son una amenaza constante para este campamento! Que sean o no de Feralas es irrelevante. Si no redoblamos nuestros esfuerzos por controlarlos, acabarán por expulsarnos de la región entera.$B$BLos ataques de los gnolls al campamento se han intensificado últimamente. Necesitamos que nos ayudes a reducir su número. Tráeme 10 de sus cabelleras. Recompensaré generosamente tu esfuerzo.$B$B¡Ve!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2862, 'esMX', 'En guerra con los Zarpaleña', 'Lleva 10 cabelleras de gnolls Zarpaleña a Hadoken Zanco Veloz al Campamento Mojache.', 'Los gnolls... son algo más que una molestia. ¡Son una amenaza constante para este campamento! Que sean o no de Feralas es irrelevante. Si no redoblamos nuestros esfuerzos por controlarlos, acabarán por expulsarnos de la región entera.$B$BLos ataques de los gnolls al campamento se han intensificado últimamente. Necesitamos que nos ayudes a reducir su número. Tráeme 10 de sus cabelleras. Recompensaré generosamente tu esfuerzo.$B$B¡Ve!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2862, 'frFR', 'La guerre avec les Griffebois', 'Rapportez 10 Crinières de gnolls griffebois à Hadoken Rôdeur-agile, au Camp Mojache.', 'Les gnolls… Ils sont plus qu\'une nuisance. Ils représentent une menace constante pour ce campement ! Qu\'ils soient natifs de Féralas ou non n\'a aucune importance. Si nous ne prenons pas rapidement des mesures appropriées, nous serons balayés de la région.$B$BLes attaques contre le camp sont de pire en pire. Nous avons besoin de votre aide pour réduire leur nombre. Rapportez-moi dix crinières et je vous récompenserai de vos efforts.$B$BAllez !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2862, 'itIT', 'War on the Woodpaw', 'Bring 10 Woodpaw Gnoll Manes to Hadoken Swiftstrider in Camp Mojache.', 'The gnolls... they are more than a nuisance.  They are a constant threat to the existence of this very camp!  It is irrelevant whether they are indigenous to Feralas or not; if we do not strengthen our resolve against them, we will find ourselves driven out of the entire region.$B$BThe gnoll attacks against the camp have become more fevered as of late.  We need your aid in thinning their numbers; bring to me ten of their manes, and I will reward you handsomely for your effort.$B$BGo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2862, 'ptBR', 'War on the Woodpaw', 'Bring 10 Woodpaw Gnoll Manes to Hadoken Swiftstrider in Camp Mojache.', 'The gnolls... they are more than a nuisance. They are a constant threat to the existence of this very camp! It is irrelevant whether they are indigenous to Feralas or not; if we do not strengthen our resolve against them, we will find ourselves driven out of the entire region.$B$BThe gnoll attacks against the camp have become more fevered as of late. We need your aid in thinning their numbers; bring to me ten of their manes, and I will reward you handsomely for your effort.$B$BGo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2862, 'ruRU', 'Война с племенем Древолапов', 'Принесите 10 грив гноллов-древолапов Хадокену Быстрому Страннику в Лагере Мохаче.', 'О, эти гноллы... Сказать, что они отравляют нам жизнь – значит ничего не сказать! Мы здесь постоянно живем под угрозой нападения! Я не знаю, всегда ли они жили в Фераласе, или пришли сюда откуда-то еще, но если мы не будем противостоять им достаточно решительно, нам здесь не жить.$b$bА в последнее время гноллы совершенно озверели! Нам необходима твоя помощь, чтобы сократить их численность. Принеси мне десяток их грив, и я щедро вознагражу тебя за труды.$b$bСтупай!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2862, 'zhCN', '与豺狼人开战', '豺狼人……它们现在不再只是一个小麻烦了，它们已经对我们形成了巨大的威胁！这些豺狼人是不是菲拉斯的原住民已经不重要了；如果我们无法有效地对抗它们，那我们早晚都会被赶出这个地区的。$B$B豺狼人最近对营地的攻势愈演愈烈，因此我们需要你的帮助来削弱它们的数量。带给我十份豺狼人的鬃毛，我会为你付出的努力而给予优厚的报酬。$B$B赶快出发吧！', '将10份木爪豺狼人的鬃毛交给莫沙彻营地的哈杜肯·迅蹄。', '', '去菲拉斯找莫沙彻营地的哈杜肯·迅蹄。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2863, 'deDE', 'Schlag gegen die Alphas', 'Tötet 5 Alphas der Waldpfoten und kehrt innerhalb einer Stunde zu Hadoken Flinkschreiter zurück.', 'Der Plan besteht darin, verschiedene Anführer der Gnolle - Alphas, wie sie sich selbst nennen - in schneller Folge auszuschalten. Wenn wir einen solch schnellen und präzisen Schlag gegen sie erzielen können, würde es in ihren Rängen zu Chaos führen. Vielleicht würde es dann endlich auch in ihren dicken, von Flöhen zerbissenen Köpfen klar, dass wir sie vernichten können und werden!$B$BIch möchte, dass Ihr der Ausführende meines Willens seid. Eliminiert innerhalb einer Stunde fünf Alphas, wenn das klappen soll, $n, sonst bleiben wir in dieser Pattsituation!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2863, 'esES', 'Ataca a los Alfas', 'Mata a 5 Alfas Zarpaleña y vuelve con Hadoken Zanco Veloz en 1 hora.', 'El plan es para varios líderes de los gnolls, esos que se hacen llamar Alfas. Para acabar con ellos en rápida sucesión. Si logramos ejecutarlo con la rapidez y precisión necesarias, haríamos que cundiera el caos entre sus filas. Y quizás conseguiríamos que en esas estúpidas cabezas piojosas entrara el mensaje de que podemos destruirlos ¡y lo haremos!$B$BQuiero que seas tú $gel mensajero:la mensajera; de mi voluntad, $n. Elimina a 5 de sus Alfas en una hora. ¡Si tardas más, el plan no funcionará y empeorarás nuestra situación!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2863, 'esMX', 'Ataca a los Alfas', 'Mata a 5 Alfas Zarpaleña y vuelve con Hadoken Zanco Veloz en 1 hora.', 'El plan es para varios líderes de los gnolls, esos que se hacen llamar Alfas. Para acabar con ellos en rápida sucesión. Si logramos ejecutarlo con la rapidez y precisión necesarias, haríamos que cundiera el caos entre sus filas. Y quizás conseguiríamos que en esas estúpidas cabezas piojosas entrara el mensaje de que podemos destruirlos ¡y lo haremos!$B$BQuiero que seas tú $gel mensajero:la mensajera; de mi voluntad, $n. Elimina a 5 de sus Alfas en una hora. ¡Si tardas más, el plan no funcionará y empeorarás nuestra situación!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2863, 'frFR', 'Frappe sur les Alphas', 'Tuez 5 Alphas griffebois et retournez voir Hadoken Rôdeur-agile en moins d\'une heure.', 'Le plan concerne divers chefs gnolls - les Alphas pour reprendre leur propre appellation - et leur élimination successive en un minimum de temps. Si nous pouvions exécuter ce genre d\'éradication rapide et précise contre eux, cela amènerait le chaos dans leurs rangs. Et peut-être que le message passerait même à l\'intérieur de leurs têtes épaisses et infestées de puces que nous pouvons et allons les détruire !$B$BJe veux que vous soyez $gle messager:la messagère; de ma volonté. Éliminez cinq de leurs Alphas en une heure pour cette mission, $n, ou nous resterons dans l\'impasse.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2863, 'itIT', 'Alpha Strike', 'Kill 5 Woodpaw Alphas and return to Hadoken Swiftstrider within one hour.', 'The plan is for various leaders of the gnolls - Alphas as they call themselves - to be taken down within rapid succession.  If we\'re able to execute this sort of swift and precise devastation against them, it would cause chaos in their ranks.  Perhaps it would also drive a message through their thick flea-bitten heads that we can and will destroy them!$B$BI want you to be the messenger of my will; eliminate five of their Alphas within one hour for this to work $n, or we\'ll remain at this impasse!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2863, 'ptBR', 'Alpha Strike', 'Kill 5 Woodpaw Alphas and return to Hadoken Swiftstrider within one hour.', 'The plan is for various leaders of the gnolls - Alphas as they call themselves - to be taken down within rapid succession. If we\'re able to execute this sort of swift and precise devastation against them, it would cause chaos in their ranks. Perhaps it would also drive a message through their thick flea-bitten heads that we can and will destroy them!$B$BI want you to be the messenger of my will; eliminate five of their Alphas within one hour for this to work $n, or we\'ll remain at this impasse!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2863, 'ruRU', 'Истребление вожаков', 'План состоит в том, чтобы одним махом истребить всех предводителей гноллов. Если нам удастся это сделать, в рядах гноллов воцарится хаос. А возможно, до этих блохастых тварей даже дойдет, что с нами лучше не связываться!$b$bТебе, $n, я поручаю истребить пятерых вожаков гноллов. Только сделать это нужно в течение часа, иначе весь план пойдет насмарку!', 'Убейте в течение часа 5 вожаков стаи Древолапов и вернитесь к Хадокену Быстрому Страннику.', '', 'Вернитесь к Хадокену Быстрому Страннику в лагерь Мохаче, что в Фераласе.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2863, 'zhCN', '突然袭击', '我们计划在很短的时间里拿下豺狼人的领导者——按他们自己的说法是突击队员。如果我们顺利地执行了这个计划……不仅他们的内部会出现混乱，而且这也会向他们愚蠢的脑袋传达一个信息，那就是我们有能力而且必将消灭他们！$B$B我想让你成为传达这个信息的使者，你必须在一小时内消灭五个突击队员，否则我们还会处于这种僵局中。', '在1个小时内杀死5个木爪突击队员，并向哈杜肯·迅蹄汇报。', '', '去菲拉斯找莫沙彻营地的哈杜肯·迅蹄。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2864, 'deDE', 'Tran\'rek', 'Sprecht mit Tran\'rek in Gadgetzan.', 'Mein Vetter Tran\'rek ist in Schwierigkeiten. Er versprach mir vor einer Woche eine Fuhre Skarabäuspanzerschalen und hat bisher nicht geliefert. Er ist mein Vetter, also würde ich es wirklich hassen, wenn ich jemanden nach Gadgetzan schicken müsste, um ihm etwas zu brechen.$B$BKönnt Ihr für mich mit Tran\'rek sprechen? Lasst ihn wissen, dass er diese Lieferung zusammenbekommen muss, bevor ich irgendetwas Unüberlegtes mache.$B$BGadgetzan liegt im nördlichen Tanaris, in Kalimdor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2864, 'esES', 'Tran\'rek', 'Habla con Tran\'rek en Gadgetzan.', 'Mi primo Tran\'rek está en un aprieto. Me prometió un cargamento de caparazones de escarabajo hace semanas y todavía no lo ha traído. Es mi primo, así que odio tener que enviar a alguien a Gadgetzan para que le eche la bronca.$B$B¿Puedes hablar con Tran\'rek por mí? Hazle saber que tiene que conseguir ese cargamento antes de que haga algo malo.$B$BGadgetzan está en el norte de Tanaris, en Kalimdor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2864, 'esMX', 'Tran\'rek', 'Habla con Tran\'rek en Gadgetzan.', 'Mi primo Tran\'rek está en un aprieto. Me prometió un cargamento de caparazones de escarabajo hace semanas y todavía no lo ha traído. Es mi primo, así que odio tener que enviar a alguien a Gadgetzan para que le eche la bronca.$B$B¿Puedes hablar con Tran\'rek por mí? Hazle saber que tiene que conseguir ese cargamento antes de que haga algo malo.$B$BGadgetzan está en el norte de Tanaris, en Kalimdor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2864, 'frFR', 'Tran\'rek', 'Parler à Tran\'rek à Gadgetzan.', 'Mon cousin Tran\'rek me pose un problème. Il m\'a promis une cargaison de carapaces de scarabées il y a quelques semaines, et je n\'ai toujours rien reçu. C’est mon cousin, je n\'aimerais donc pas le rappeler méchamment à l’ordre.$B$BPouvez-vous lui parler avant que je ne commette un acte regrettable ? Dites-lui que j\'ai vraiment besoin de cette cargaison.$B$BGadgetzan est dans le nord de Tanaris, en Kalimdor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2864, 'itIT', 'Tran\'rek', 'Speak with Tran\'rek in Gadgetzan.', 'My cousin Tran\'rek is in a tight spot.  He promised me a load of scarab shells weeks ago and he hasn\'t delivered.  He\'s my cousin, so I\'d hate to have to send someone to Gadgetzan to break something on him.$B$BCan you speak to Tran\'rek for me?  Let him know he needs to get that delivery together before I do something rash?$B$BGadgetzan is in northern Tanaris, in Kalimdor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2864, 'koKR', '트란렉', '가젯잔에 있는 트란렉과 대화해야 합니다.', '내 사촌, 트란렉이 곤경에 빠졌어요. 왕쇠똥구리 껍질을 한 짐 보내겠다고 약속한 지가 벌써 몇 주나 됐는데 아직 도착하지 않았거든요. 사촌이라 가젯잔으로 누구를 보내서 독촉을 하기가 좀 그런데...$B$B나 대신 트란렉과 얘기해 볼래요? 내가 허튼짓을 하기 전에 빨리 보내라고 했다고 말에요.$B$B가젯잔은 칼림도어의 타나리스 북부에 있어요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2864, 'ptBR', 'Tran\'rek', 'Speak with Tran\'rek in Gadgetzan.', 'My cousin Tran\'rek is in a tight spot. He promised me a load of scarab shells weeks ago and he hasn\'t delivered. He\'s my cousin, so I\'d hate to have to send someone to Gadgetzan to break something on him.$B$BCan you speak to Tran\'rek for me? Let him know he needs to get that delivery together before I do something rash?$B$BGadgetzan is in northern Tanaris, in Kalimdor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2864, 'ruRU', 'Тран\'рек', 'Поговорите с Тран\'реком в Прибамбасске.', 'У моего кузена Тран\'река сейчас большие проблемы. Он обещал мне партию панцирей скарабея еще неделю назад, но так и не поставил. Он мой кузен, так что я не хотел бы посылать кого-то в Прибамбасск выколачивать из него свои деньги или груз.$B$BМожешь поговорить с ним за меня? Скажи ему, что он должен доставить мне этот груз – иначе придется отвечать, как и всем остальным... $B$BПрибамбасск находится на севере пустыни Танарис в Калимдоре.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2864, 'zhCN', '特兰雷克', '与加基森的特兰雷克谈一谈。', '我的兄弟特兰雷克现在显然处于困境中。几个星期之前他曾经向我承诺会给我一批圣甲虫的壳，但是至今为止我还没有收到。他是我的兄弟，所以我可不想派人去加基森砸他的场子。$B$B你能帮我去问问特兰雷克到底是怎么回事吗？在我真的发飙之前他最好快点把货给我送来。$B$B加基森就在卡利姆多大陆上的塔纳利斯沙漠北部。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2864, 'zhTW', '特蘭雷克', '與加基森的特蘭雷克談話。', '我的兄弟特蘭雷克現在顯然處於困境中。幾個星期之前他曾經向我承諾會給我一批聖甲蟲的殼，但是至今為止我還沒有收到。他是我的兄弟，所以我可不想派人去加基森砸他的場子。$B$B你能幫我去問問特蘭雷克到底是怎麼回事嗎?在我真的發飆之前他最好快點把貨給我送來。$B$B加基森就在卡林多大陸上的塔納利斯沙漠北部。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2865, 'deDE', 'Skarabäuspanzerschalen', 'Bringt Tran\'rek in Gadgetzan 5 unbeschädigte Skarabäuspanzerschalen.', 'Die Skarabäen von Tanaris haben sehr harte Panzerschalen! Hart genug, um sie als Baumaterial für eine Menge Dinge zu verwenden. So viele Dinge!$B$BTatsächlich sind diese Panzer so nützlich... dass die Skarabäen beinahe bis zur Ausrottung gejagt wurden.$B$BIch weiß, wo es noch mehr Skarabäen gibt, und wenn Ihr mir versprecht, mir ihre Panzerschalen zu bringen, sage ich Euch, wo sie sind. Versprochen?$B$BO.k., die Skarabäen haben eine Kolonie in Zul\'Farrak. Ich schätze, die Trolle jagen sie nicht wegen ihrer Panzerschalen.$B$BAber Ihr könnt das tun! Geht nach Zul\'Farrak und besorgt mir unbeschädigte Skarabäuspanzerschalen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2865, 'esES', 'Caparazones de escarabajo', 'Lleva 5 caparazones de escarabajo sin rajar a Tran\'rek en Gadgetzan.', '¡Los escarabajos de Tanaris tienen unos caparazones muy duros! Tan duros que se pueden utilizar para construir muchas cosas. ¡Muchísimas!$B$BDe hecho, esos caparazones son tan útiles y cazamos tantos escarabajos... ¡que casi se han extinguido!$B$BSé dónde hay más escarabajos, y si me prometes traerme sus caparazones te diré dónde están. ¿Prometido?$B$BMuy bien, hay una colonia de escarabajos en Zul\'Farrak. Supongo que los trols no los cazan en busca de caparazones.$B$B¡Pero tú sí! ¡Ve a Zul\'Farrak y consígueme caparazones sin rajar!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2865, 'esMX', 'Caparazones de escarabajo', 'Lleva 5 caparazones de escarabajo sin rajar a Tran\'rek en Gadgetzan.', '¡Los escarabajos de Tanaris tienen unos caparazones muy duros! Tan duros que se pueden utilizar para construir muchas cosas. ¡Muchísimas!$B$BDe hecho, esos caparazones son tan útiles y cazamos tantos escarabajos... ¡que casi se han extinguido!$B$BSé dónde hay más escarabajos, y si me prometes traerme sus caparazones te diré dónde están. ¿Prometido?$B$BMuy bien, hay una colonia de escarabajos en Zul\'Farrak. Supongo que los trols no los cazan en busca de caparazones.$B$B¡Pero tú sí! ¡Ve a Zul\'Farrak y consígueme caparazones sin rajar!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2865, 'frFR', 'Les carapaces de scarabées', 'Rapporter 5 Carapaces de scarabée intactes à Tran\'rek à Gadgetzan.', 'Les scarabées de Tanaris ont une carapace très dure ! Si dure qu’elle est utilisée dans de nombreux domaines.$B$BEn fait ces carapaces sont si utiles que les scarabées ont presque disparu !$B$BJe sais où trouver des scarabées, mais vous devez me promettre de ramener leurs carapaces. Promis ?$B$BOK, les scarabées ont une colonie à Zul\'Farrak. Je pense que les trolls ne les chassent pas pour leurs carapaces.$B$BMais vous si ! Allez à Zul\'Farrak et trouvez-moi ces carapaces intactes !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2865, 'itIT', 'Scarab Shells', 'Bring 5 Uncracked Scarab Shells to Tran\'rek in Gadgetzan.', 'The scarabs of Tanaris have very hard shells!  Hard enough to use as a building material for lots of things.  So many things!$B$BIn fact, those shells are so useful... the scarabs were hunted all to near extinction!$B$BI know where there are more scarabs, and if you promise to bring me their shells then I\'ll tell you where they are.  Promise?$B$BOk, the scarabs have a colony in Zul\'Farrak.  I guess the trolls don\'t hunt them for their shells.$B$BBut you can!  Go to Zul\'Farrak and get me uncracked shells!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2865, 'koKR', '왕쇠똥구리 껍질', '가젯잔에 있는 트란렉에게 온전한 왕쇠똥구리 껍질 5개를 가져가야 합니다.', '타나리스의 왕쇠똥구리들은 단단한 껍질을 갖고 있지요! 건축 자재로 쓸 수 있을 만큼 아주 단단해요. 그 외에도 정말 여러 가지로 쓰이지요!$B$B사실, 그 껍질이 얼마나 유용한지, 왕쇠똥구리가 거의 멸종될 정도로 사냥을 해댔답니다!$B$B저는 그 귀한 왕쇠똥구리의 서식지를 알죠. 껍질을 제게 가져오겠다고만 약속하신다면 어딘지 가르쳐 드리지요. 약속하시겠어요?$B$B그럼, 좋아요. 왕쇠똥구리들은 줄파락에 무리를 지어서 살아요. 다행히 그곳 트롤들이 껍질을 노리고 녀석들을 사냥하지는 않는 것 같아요.$B$B당신이라면 할 수 있죠! 줄파락으로 가서 온전한 껍질을 가져오세요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2865, 'ptBR', 'Scarab Shells', 'Bring 5 Uncracked Scarab Shells to Tran\'rek in Gadgetzan.', 'The scarabs of Tanaris have very hard shells! Hard enough to use as a building material for lots of things. So many things!$B$BIn fact, those shells are so useful... the scarabs were hunted all to near extinction!$B$BI know where there are more scarabs, and if you promise to bring me their shells then I\'ll tell you where they are. Promise?$B$BOk, the scarabs have a colony in Zul\'Farrak. I guess the trolls don\'t hunt them for their shells.$B$BBut you can! Go to Zul\'Farrak and get me uncracked shells!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2865, 'ruRU', 'Панцири скарабеев', 'Принести 5 целых панцирей скарабея Тран\'реку в Прибамбасск.', 'У скарабеев Танариса очень твердый панцирь, достаточно твердый, чтобы использовать их как строительный материал много для чего. Очень много!$B$BЭти панцири, настолько полезная вещь, что ради них скарабеев извели практически под корень.$B$BЯ знаю, где они еще водятся, и я поделюсь с тобой, если ты пообещаешь принести мне их панцири, обещаешь?$B$BТак вот. Колония скарабеев находится в Зул\'Фарраке. Видимо до троллей еще не дошло, насколько полезны их панцири.$B$BТак что все твои! Отправляйся в Зул\'Фаррак и принеси мне целых панцирей скарабеев.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2865, 'zhCN', '圣甲虫的壳', '给加基森的特兰雷克带去5个完整的圣甲虫壳。', '塔纳利斯这里的圣甲虫的壳都很硬！硬得足可以给好多东西当建筑材料，好多东西！$B$B事实上，因为那些壳都太有用了……圣甲虫已经被猎杀得近乎灭绝了！$B$B我知道哪里有更多的圣甲虫，如果你保证会给我弄些壳来，我就告诉你它们在哪里。你保证吗？$B$B好吧，祖尔法拉克里面有许多圣甲虫，我想巨魔们并不因为想要那些壳而猎杀它们。$B$B但你可以！到祖尔法拉克去给我弄些完好无损的壳回来！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2865, 'zhTW', '聖甲蟲的殼', '帶5個完整的聖甲蟲殼給加基森的特蘭雷克。', '塔納利斯這裡的聖甲蟲的殼都很硬!硬得足可以給好多東西當建築材料，好多東西!$B$B事實上，因為那些殼都太有用了...聖甲蟲已經被獵殺得近乎滅絕了!$B$B我知道哪裡有更多的聖甲蟲，如果你保證會給我弄些殼來，我就告訴你它們在哪裡。你保證嗎?$B$B好吧，祖爾法拉克裡面有許多聖甲蟲，我想食人妖們並不因為想要那些殼而獵殺它們。$B$B但你可以!到祖爾法拉克去給我弄些完好無損的殼回來!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2866, 'deDE', 'Die Ruinen von Solarsal', 'Erforscht die Ruinen von Solarsal und findet etwas über die Anwesenheit der Naga dort heraus.', 'Südlich von uns befinden sich die Ruinen von Solarsal. Dies ist eine Gegend aus längst vergangener Zeit. Wir haben die Ruinen ursprünglich erforscht und nichts Besonderes darin gefunden. Daher sind wir friedlich wieder abgezogen.$B$BVor Kurzem haben die Hasskammnaga die Ruinen in alarmierender Anzahl besetzt. Zuerst hielten wir das für die Vorbereitungen auf einen Angriff. Obwohl diese Bedrohung immer noch gegeben ist, glauben wir, dass dort etwas anderes vorgeht.$B$BGeht dorthin, erforscht die Ruinen und achtet auf alles, was Euch ungewöhnlich vorkommt. Viel Glück, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2866, 'esES', 'Las Ruinas de Solarsal', 'Explora las Ruinas de Solarsal e investiga la presencia de los nagas allí.', 'Al sur de aquí están las Ruinas de Solarsal, una zona que ya era antigua antes de nuestro tiempo. Exploramos las ruinas en su momento y no encontramos nada relevante, así que no volvimos a enturbiar la paz del lugar.$B$BHace poco, los nagas Crestafuria han ocupado las ruinas en número alarmante. Al principio, lo interpretamos como el anuncio de un ataque. Aún consideramos esa posibilidad, pero además, creemos que podría estar ocurriendo alguna otra cosa.$B$BVe a explorar las ruinas y busca cualquier cosa que te llame la atención. Buena suerte, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2866, 'esMX', 'Las Ruinas de Solarsal', 'Explora las Ruinas de Solarsal e investiga la presencia de los nagas allí.', 'Al sur de aquí están las Ruinas de Solarsal, una zona que ya era antigua antes de nuestro tiempo. Exploramos las ruinas en su momento y no encontramos nada relevante, así que no volvimos a enturbiar la paz del lugar.$B$BHace poco, los nagas Crestafuria han ocupado las ruinas en número alarmante. Al principio, lo interpretamos como el anuncio de un ataque. Aún consideramos esa posibilidad, pero además, creemos que podría estar ocurriendo alguna otra cosa.$B$BVe a explorar las ruinas y busca cualquier cosa que te llame la atención. Buena suerte, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2866, 'frFR', 'Les ruines de Solarsal', 'Explorer les ruines de Solarsal et enquêter sur la présence des nagas dans le secteur.', 'Au sud se trouvent les ruines de Solarsal, un site bien antérieur à l\'ère moderne. Nous avons exploré ces ruines et rien trouvé, aussi les avons-nous délaissées.$B$BTrès récemment, les nagas hainecrêtes se sont installés en nombre dans les ruines. Au début, nous pensions à une attaque. Bien que cela soit toujours possible, nous croyons qu\'il s\'agit d\'autre chose.$B$BAllez là-bas, explorez les ruines et rendez compte de tout ce qui vous paraîtra inhabituel. Bonne chance, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2866, 'itIT', 'The Ruins of Solarsal', 'Explore the Ruins of Solarsal and investigate the presence of the naga there.', 'To our south are the Ruins of Solarsal; this is an area that far pre-dates modern times.  We explored the ruins initially and found nothing of note; as such, we left it in peace.$B$BVery recently, the Hatecrest naga have occupied these ruins in alarming numbers.  At first, we considered it as the precursor for an attack.  Though that threat is still possible, we believe there might be something else going on.$B$BGo there, explore the ruins, and look for anything that stands out to you. Good luck, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2866, 'koKR', '솔라살 폐허', '솔라살 폐허를 탐사하고 그곳에 있는 나가의 실태를 조사해야 합니다.', '여기서 남쪽으로 가면 현시대가 되기 훨씬 이전부터 있었던 솔라살 폐허가 있습니다. 처음 그 폐허를 조사했을 때는 특별히 눈에 띄는 것이 없기에 건드리지 않고 그대로 두었지요.$B$B그런데 최근 들어 엄청난 수의 증오갈기 나가들이 그 폐허를 점령했습니다. 처음에는 녀석들이 공격 준비를 하는 것으로 생각했지요. 지금도 그럴 가능성이 있긴 하지만 뭔가 다른 일을 벌이는 게 아닌가 싶군요.$B$B가서 그 폐허를 탐사하고 눈에 띄는 것이 있으면 뭐든지 가져 와 주십시오. 엘룬께서 행운을 가져다주시길.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2866, 'ptBR', 'The Ruins of Solarsal', 'Explore the Ruins of Solarsal and investigate the presence of the naga there.', 'To our south are the Ruins of Solarsal; this is an area that far pre-dates modern times. We explored the ruins initially and found nothing of note; as such, we left it in peace.$B$BVery recently, the Hatecrest naga have occupied these ruins in alarming numbers. At first, we considered it as the precursor for an attack. Though that threat is still possible, we believe there might be something else going on.$B$BGo there, explore the ruins, and look for anything that stands out to you. Good luck, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2866, 'ruRU', 'Руины Соларсаля', 'Обследуйте руины Соларсаля на предмет присутствия наг.', 'К югу от нас находятся руины Соларсаля. Это развалины очень древнего города. Мы уже обследовали руины, но пока не нашли там ничего примечательного и оставили их в покое.$b$bОднако не так давно в этих развалинах поселились наги из клана Гребня Ненависти, причем в угрожающих количествах. Поначалу мы решили, что они готовят нападение. Возможно, они его действительно готовят, но нам кажется, что там происходит что-то еще.$b$bСтупай туда, осмотри развалины и обрати внимание на все, что покажется подозрительным. Удачи, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2866, 'zhCN', '索兰萨尔废墟', '探索索兰萨尔废墟，并调查纳迦在那个地区的情况。', '在我们的南边就是索兰萨尔废墟，那个地区的历史相当悠久。我们探索过那个遗迹，没有发现什么特别值得注意的东西，所以我们就离开了。$B$B最近，憎世纳迦占据了那片遗迹，而且他们的数量已经多到了让我们不得不警惕的程度。一开始我们认为那些纳迦是准备对我们发起攻击，虽然这种可能性现在依然存在，但我们觉得他们可能是有什么别的目的。$B$B现在就到那片废墟去查看一番吧，祝你好运，$n。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2866, 'zhTW', '索蘭薩爾廢墟', '探索索蘭薩爾廢墟，並調查納迦在那個地區的情況。', '在我們的南邊就是索蘭薩爾廢墟，那個地區的歷史相當悠久。我們探索過那個遺跡，沒有發現什麼特別值得注意的東西，所以我們就離開了。$B$B最近，憎世納迦佔據了那片遺跡，而且他們的數量已經多到了讓我們不得不警惕的程度。一開始我們認為那些納迦是準備對我們發起攻擊，雖然這種可能性現在依然存在，但我們覺得他們可能是有什麼別的目的。$B$B現在就到那片廢墟去查看一番吧，祝你好運，$n。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2867, 'deDE', 'Kehrt zur Mondfederfeste zurück', 'Kehrt zu Shandris Mondfeder in der Mondfederfeste zurück und berichtet von Euren Entdeckungen.', 'Mit der Entdeckung des seltsamen Pavillons im Herzen der Ruinen von Solarsal ist es an der Zeit, zur Mondfederfeste zurückzukehren und General Shandris Mondfeder zu berichten, was hier entdeckt wurde.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2867, 'esES', 'Regresa al Bastión Plumaluna', 'Vuelve al Bastión Plumaluna e informa a Shandris Plumaluna del resultado de tu exploración.', 'Tras descubrir la extraña glorieta en el corazón de las Ruinas de Solarsal, sabes que ha llegado el momento de regresar al Bastión Plumaluna e informar a la general Shandris Plumaluna del resultado de tu exploración.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2867, 'esMX', 'Regresa al Bastión Plumaluna', 'Vuelve al Bastión Plumaluna e informa a Shandris Plumaluna del resultado de tu exploración.', 'Tras descubrir la extraña glorieta en el corazón de las Ruinas de Solarsal, sabes que ha llegado el momento de regresar al Bastión Plumaluna e informar a la general Shandris Plumaluna del resultado de tu exploración.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2867, 'frFR', 'Retourner au bastion de Pennelune', 'Retourner voir Shandris Pennelune au bastion de Pennelune et lui rendre compte de vos découvertes.', 'Après la découverte du belvédère étrange au cœur des ruines de Solarsal, il est temps de retourner au bastion de Pennelune et faire votre rapport au général Shandris Pennelune.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2867, 'itIT', 'Return to Feathermoon Stronghold', 'Return to Shandris Feathermoon in the Feathermoon Stronghold and report your findings.', 'With the discovery of the strange gazebo in the heart of the Ruins of Solarsal, the time has come to return to Feathermoon Stronghold and report to General Shandris Feathermoon what has been discovered here.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2867, 'ptBR', 'Return to Feathermoon Stronghold', 'Return to Shandris Feathermoon in the Feathermoon Stronghold and report your findings.', 'With the discovery of the strange gazebo in the heart of the Ruins of Solarsal, the time has come to return to Feathermoon Stronghold and report to General Shandris Feathermoon what has been discovered here.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2867, 'ruRU', 'Возвращение в Крепость Оперенной Луны', 'Теперь, когда вы обнаружили странную беседку в глубине руин Соларсаля, настало время вернуться в Крепость Оперенной Луны и доложить генералу Шандрисе Оперенной Луне о своем открытии.', 'Вернитесь в Крепость Оперенной Луны к Шандрисе Оперенной Луне и доложите о находке.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2867, 'zhCN', '返回羽月要塞', '在索兰萨尔废墟的心脏地带发现了奇怪的瞭望台之后，你觉得应该马上回到羽月要塞去向珊蒂斯·羽月汇报这个发现。', '向羽月要塞的珊蒂斯·羽月汇报你的发现。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2868, 'deDE', '<TXT> The Shriveled Heart', 'Packt diesen Bösen in ein Trollgrab im Versunkenen Tempel und besorgt Euch was Cooles.', '', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2868, 'esES', 'Corazón marchito', 'Entrega a este chico malo en una tumba trol del Templo Sumergido ¡y consigue algo fantástico!', '', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2868, 'esMX', '<TXT> Corazón marchito', 'Entrega a este chico malo en una tumba trol del Templo Sumergido ¡y consigue algo fantástico!', '', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2868, 'frFR', '<TXT> The Shriveled Heart', 'Faites de ce mauvais garçon une tombe trolle dans le Temple englouti et récupérez quelque chose de sympathique !', '', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2868, 'ruRU', '<TXT> Сушеное сердце', 'Засунь этого урода в какую-нибудь троллью могилу в затонувшем храме и будет тебе счастье!', '', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2868, 'zhCN', '暂无内容', '把这个坏小子塞到Sunken Temple的巨魔墓里，让他吃吃苦头。', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2869, 'deDE', 'Gegen die Hasskämme', 'Bringt Latronicus Mondspeer in der Mondfederfeste 10 Nagaschuppen der Hasskämme.', 'Es ist derzeit die beste Handlungsweise, davon auszugehen, dass die Naga, was auch immer sie vorhaben, langfristig versuchen werden, die Insel für sich zu beanspruchen. Wir müssen aktive Schritte unternehmen, um diese Bedrohung auszuschalten, bevor es zu spät ist.$B$BBringt mir 10 Schuppen der Hasskämme in gutem Zustand. Es dürfte offensichtlich sein, wie man an diese herankommt. Ihr werdet Euch an den Naga das erste Mal Eure Sporen verdienen und wir haben dann eine geschwächte Nagapräsenz auf der Sardor-Insel - beides sind Aussichten, die uns zum Vorteil gereichen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2869, 'esES', 'En contra de los Crestafuria', 'Consigue 10 escamas de naga Crestafuria para Latronicus Lanzaluna en el Bastión Plumaluna.', 'En este momento, lo mejor que podemos hacer es asumir que, independientemente de lo que estén haciendo los nagas, antes o después se moverán para asegurar la isla para los suyos. Necesitamos tomar medidas agresivas para combatir esa amenaza antes de que sea demasiado tarde.$B$BTráeme 10 escamas de Crestafuria que estén en buenas condiciones. No hará falta decirte cómo conseguirlas, supongo. Tú adquirirás experiencia con los nagas y nosotros conseguiremos reducir su presencia en Isla de Sardor. Ambas cosas serán beneficiosas para nosotros.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2869, 'esMX', 'En contra de los Crestafuria', 'Consigue 10 escamas de naga Crestafuria para Latronicus Lanzaluna en el Bastión Plumaluna.', 'En este momento, lo mejor que podemos hacer es asumir que, independientemente de lo que estén haciendo los nagas, antes o después se moverán para asegurar la isla para los suyos. Necesitamos tomar medidas agresivas para combatir esa amenaza antes de que sea demasiado tarde.$B$BTráeme 10 escamas de Crestafuria que estén en buenas condiciones. No hará falta decirte cómo conseguirlas, supongo. Tú adquirirás experiencia con los nagas y nosotros conseguiremos reducir su presencia en Isla de Sardor. Ambas cosas serán beneficiosas para nosotros.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2869, 'frFR', 'Contre les Hainecrêtes', 'Rapporter 10 Écailles de naga hainecrête à Latronicus Lancelune au bastion de Pennelune.', 'La meilleure approche est de penser, indépendamment du projet réel des nagas, qu\'ils tentent de prendre possession de l\'île. Nous devons réagir énergiquement avant qu\'il ne soit trop tard.$B$BRapportez-moi dix écailles de Hainecrête en bon état ; pas besoin de vous expliquer comment vous les procurer. Vous diminuerez leur puissance et affaiblirez leur présence sur l\'île de Sardor, ce qui joue en notre faveur.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2869, 'itIT', 'Against the Hatecrest', 'Bring 10 Hatecrest Naga Scales to Latronicus Moonspear in Feathermoon Stronghold.', 'The best course of action at this point is to assume that, regardless of what the naga are up to, they will eventually move to secure the island for their own.  We need to take aggressive steps to counter that threat before it is too late.$B$BBring me ten Hatecrest Scales that are in good condition; it should be obvious to you how to acquire them.  You\'ll cut your teeth on the naga for the first time, and we\'ll have a weakened naga presence on Sardor Isle - both are prospects that play to our advantage.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2869, 'ptBR', 'Against the Hatecrest', 'Bring 10 Hatecrest Naga Scales to Latronicus Moonspear in Feathermoon Stronghold.', 'The best course of action at this point is to assume that, regardless of what the naga are up to, they will eventually move to secure the island for their own. We need to take aggressive steps to counter that threat before it is too late.$B$BBring me ten Hatecrest Scales that are in good condition; it should be obvious to you how to acquire them. You\'ll cut your teeth on the naga for the first time, and we\'ll have a weakened naga presence on Sardor Isle - both are prospects that play to our advantage.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2869, 'ruRU', 'Борьба с Гребнем Ненависти', 'Принесите 10 штук чешуи наг из клана Гребня Ненависти Латроникусу Лунному Копью в Крепость Оперенной Луны.', 'На данный момент самое разумное – рассчитывать на то, что наги в любом случае попытаются захватить остров. И нам нужно предотвратить эту угрозу, пока не стало поздно.$b$bПринеси мне десять штук чешуи Гребня Ненависти. Как их добыть – думаю, ясно? У тебя будет случай поохотиться на наг, и количество их воинов на острове Сардор уменьшится. И то, и другое нам на пользу!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2869, 'zhCN', '憎世纳迦之战', '我们认为，无论纳迦想要干什么，他们最终的目的无非是完全占据这座岛屿，由此我们制定了相应的行动计划。我们要尽早采取攻击行动来消除这种威胁，否则就可能全盘皆输。$B$B给我带来十块完整的憎世纳迦的鳞片——你应该知道如何获得这东西。这样一来，纳迦在萨尔多岛上的力量就被大大削弱了，这对我们是非常有利的。', '收集10块憎世纳迦的鳞片，把它们交给羽月要塞的拉托尼库斯·月矛。', '', '去菲拉斯找羽月要塞的拉托尼库斯·月矛。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2870, 'deDE', 'Gegen Lord Shalzaru', 'Vernichtet Lord Shalzaru und bringt dann Latronicus Mondspeer in der Mondfederfeste das geheimnisvolle Relikt, das er entdeckt hat.', '$n, unsere neuesten Spähberichte haben uns einen guten Einblick in die Kommandokette der Naga erlaubt. In ihren Truppen gibt es viele Adlige und die sind es, die den Ton angeben.$B$BUm genau zu sein, wir haben einen der grausameren Adligen als Lord Shalzaru identifiziert. Späher berichten, dass er höchstpersönlich bei einer kürzlich durchgeführten Nagaausgrabung in einer Höhle südlich der eigentlichen Ruinen, jenseits des Wassers, auf der Insel des Schreckens, ein seltsames Relikt gefunden hat.$B$BMacht dem ein Ende, indem Ihr ihm ein Ende macht, und bringt mir dieses Relikt, damit wir es untersuchen können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2870, 'esES', 'En contra de Lord Shalzaru', 'Elimina a Lord Shalzaru y luego lleva la reliquia misteriosa que desenterró a Latronicus Lanzaluna al Bastión Plumaluna.', '$n, nos han llegado informes de los exploradores que nos han dado una buena idea de cómo es la cadena de mando de los nagas. Entre sus filas hay Regios, que son los que mandan.$B$BEn concreto, hemos identificado a uno de los regios más crueles: Lord Shalzaru. Según mis exploradores, él en persona ha desenterrado una extraña reliquia en una excavación naga de una cueva del sur de las ruinas principales, en la Isla del Terror.$B$BAcaba con él y tráeme esa reliquia para que podamos estudiarla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2870, 'esMX', 'En contra de Lord Shalzaru', 'Elimina a Lord Shalzaru y luego lleva la reliquia misteriosa que desenterró a Latronicus Lanzaluna al Bastión Plumaluna.', '$n, nos han llegado informes de los exploradores que nos han dado una buena idea de cómo es la cadena de mando de los nagas. Entre sus filas hay Regios, que son los que mandan.$B$BEn concreto, hemos identificado a uno de los regios más crueles: Lord Shalzaru. Según mis exploradores, él en persona ha desenterrado una extraña reliquia en una excavación naga de una cueva del sur de las ruinas principales, en la Isla del Terror.$B$BAcaba con él y tráeme esa reliquia para que podamos estudiarla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2870, 'frFR', 'Contre le seigneur Shalzaru', 'Éliminez le seigneur Shalzaru et rapportez la Mystérieuse relique qu\'il a découverte à Latronicus Lancelune, au bastion de Pennelune.', '$n, de nouveaux rapports nous donnent une bonne idée de la chaîne de commandement des nagas. Parmi le tout-venant se trouvent des individus de sang royal, et ce sont eux qui prennent toutes les décisions.$B$BNous avons identifié l\'un de ces nobles, le seigneur Shalzaru. Nos agents disent qu\'il a découvert une relique étrange au cours de fouilles récentes qu’il a menées dans une caverne au sud des ruines principales, sur l\'île de l\'Effroi.$B$BArrêtez les autres en neutralisant Shalzaru et rapportez-moi sa relique afin que nous puissions l\'étudier.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2870, 'itIT', 'Against Lord Shalzaru', 'Eliminate Lord Shalzaru, and then bring the Mysterious Relic he uncovered to Latronicus Moonspear in Feathermoon Stronghold.', '$n, new scouting reports have given us a good idea on the naga chain of command.  Royalty is present amongst their rank and file, and they\'re the ones calling all the shots.$B$BSpecifically, we\'ve identified one of the crueler royals as Lord Shalzaru.  Scouts report that he himself has uncovered an odd relic during a recent naga excavation in a cave south of the main ruins, across the water on the Isle of Dread.$B$BPut a stop to it by putting a stop to him, and bring me his relic so we can study it.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2870, 'koKR', '군주 샬자루와의 전투', '군주 샬자루를 처치하고 그가 찾아낸 신비한 유물을 페더문 요새에 있는 라트로니쿠스 문스피어에게 가져가야 합니다.', '새로운 정찰 보고를 받고 나가의 명령 체계를 더 자세히 알 수 있게 되었소. 듣자하니 나가 종족 중에는 왕족이 있는데 이 왕족이 모든 일을 결정하고 있다고 하는군.$B$B그중에서도 유독 잔인한 군주 샬자루라는 자가 있다는 것을 확인하였소. 정찰병의 보고에 의하면 샬자루는 최근 나가들이 발굴하고 있는 공포의 섬에 수로를 건너 주 유적지의 남쪽 동굴에서 특이한 유물을 찾았다고 하오.$B$B그를 처치해 발굴을 저지하고 그 유물을 연구할 수 있게 내게 가져오시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2870, 'ptBR', 'Against Lord Shalzaru', 'Eliminate Lord Shalzaru, and then bring the Mysterious Relic he uncovered to Latronicus Moonspear in Feathermoon Stronghold.', '$n, new scouting reports have given us a good idea on the naga chain of command. Royalty is present amongst their rank and file, and they\'re the ones calling all the shots.$B$BSpecifically, we\'ve identified one of the crueler royals as Lord Shalzaru. Scouts report that he himself has uncovered an odd relic during a recent naga excavation in a cave south of the main ruins, across the water on the Isle of Dread.$B$BPut a stop to it by putting a stop to him, and bring me his relic so we can study it.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2870, 'ruRU', 'Борьба с лордом Шалзару', 'Убейте лорда Шалзару и принесите обнаруженную им таинственную реликвию Латроникусу Лунному Копью в Крепость Оперенной Луны.', '$n, мы получили новые донесения разведчиков, и теперь неплохо представляем себе, как устроено войско наг. Высшее командование состоит из особ королевской крови. Именно они-то всем и заправляют.$b$bНам стало известно имя одного из самых жестоких наг, лорда Шалзару. Разведка докладывает, что во время недавних раскопок, которые наги проводили в пещере к югу от основных руин на острове Ужаса, он лично обнаружил некую загадочную реликвию.$b$bЕго необходимо остановить. Убей его, и принеси мне этот предмет, чтобы мы могли его изучить.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2870, 'zhCN', '击败沙尔扎鲁', '消灭沙尔扎鲁，然后把神秘遗物交给羽月要塞的拉托尼库斯·月矛。', '$n，新的侦查报告让我们对纳迦部队的组成有了一个更清晰的概念。纳迦的皇室成员在他们的部队中具有最高的权威性。$B$B皇室成员中的沙尔扎鲁是个极端残酷的纳迦，侦查报告表明最近他在南部的遗迹中发现了一块奇怪的古代遗物，那个地方就在恐怖之岛那里。$B$B你的任务就是结果他，然后把那块遗物给我带回来进行研究。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2870, 'zhTW', '擊敗沙爾札魯領主', '消滅沙爾札魯領主，然後把神秘聖物交給羽月要塞的拉托尼庫斯·月矛。', '$n，新的偵察報告讓我們對納迦部隊的組成有了一個更清晰的概念。納迦的皇室成員在他們的部隊中具有最高的權威性。$B$B皇室成員中的沙爾札魯領主是個極端殘酷的納迦，偵察報告表明最近他在南部的遺跡中發現了一塊奇怪的古代聖物，那個地方就在恐怖之島那裡。$B$B你的任務就是結束他，然後把那塊聖物給我帶回來進行研究。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2871, 'deDE', 'Überbringung des Relikts', 'Bringt das geheimnisvolle Relikt zu Vestia Mondspeer in der Mondfederfeste.', 'Wenn Ihr so nett wärt, das Relikt zu meiner Frau Vestia zu bringen, wüsste ich das zu schätzen. Sie ist nicht nur eine gute Priesterin für unsere Gemeinde, sondern auch eine der versiertesten Gelehrten, die es gibt. Ich könnte wahrscheinlich nicht einmal dann herausfinden, wo an diesem Ding oben oder unten ist, wenn mein Leben davon abhinge. Meine Frau wiederum... Also wenn irgendwer herausfinden kann, was das ist, dann sie... Und wir müssen es wissen!$B$BSie befindet sich gleich um die Ecke, ohne Zweifel am Studieren. Danke, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2871, 'esES', 'Entrega la reliquia', 'Entrega la reliquia misteriosa a Vestia Lanzaluna que está en el Bastión Plumaluna.', 'Si fueras tan amable de entregar la reliquia a mi esposa Vestia, te lo agradecería. No es solo una de las mejores sacerdotisas de nuestra comunidad, además es una de las estudiosas más eruditas que conocerás en tu vida. Yo no podría ni dibujar este instrumento, ¡ni aunque mi vida dependiera de ello! Pero mi mujer... Si alguien puede averiguar qué es esta cosa, esa es ella. ¡Y necesitamos saber qué es!$B$BEstá al otro lado de la curva. Estará estudiando, seguro. Gracias, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2871, 'esMX', 'Entrega la reliquia', 'Entrega la reliquia misteriosa a Vestia Lanzaluna que está en el Bastión Plumaluna.', 'Si fueras tan amable de entregar la reliquia a mi esposa Vestia, te lo agradecería. No es solo una de las mejores sacerdotisas de nuestra comunidad, además es una de las estudiosas más eruditas que conocerás en tu vida. Yo no podría ni dibujar este instrumento, ¡ni aunque mi vida dependiera de ello! Pero mi mujer... Si alguien puede averiguar qué es esta cosa, esa es ella. ¡Y necesitamos saber qué es!$B$BEstá al otro lado de la curva. Estará estudiando, seguro. Gracias, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2871, 'frFR', 'Livraison de la relique', 'Porter la Relique mystérieuse à Vestia Lancelune au bastion de Pennelune.', 'Si vous aviez l\'amabilité de porter la relique à ma femme Vestia, je vous en serais très reconnaissant. Elle n\'est pas seulement une excellente prêtresse, elle fait également partie des meilleurs érudits que vous puissiez trouver. Je n\'arrive pas à comprendre l’usage de cet objet. Si quelqu\'un peut le savoir, c\'est ma femme... et nous avons besoin de savoir de quoi il s\'agit !$B$BElle est juste derrière le virage, sûrement en train d\'étudier. Merci, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2871, 'itIT', 'Delivering the Relic', 'Deliver the Mysterious Relic to Vestia Moonspear in Feathermoon Stronghold.', 'If you would be so kind as to deliver the relic to my wife Vestia, I would appreciate it.  She\'s not only a fine priestess for our community, but she is one of the most well-versed scholars you\'ll find anywhere.  I couldn\'t make heads or tails of this device even if there was a wager involved.  My wife though, if anyone can figure out what this thing is, she could... and we need to know!$B$BShe\'s just around the bend, no doubt studying.  Thanks, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2871, 'ptBR', 'Delivering the Relic', 'Deliver the Mysterious Relic to Vestia Moonspear in Feathermoon Stronghold.', 'If you would be so kind as to deliver the relic to my wife Vestia, I would appreciate it. She\'s not only a fine priestess for our community, but she is one of the most well-versed scholars you\'ll find anywhere. I couldn\'t make heads or tails of this device even if there was a wager involved. My wife though, if anyone can figure out what this thing is, she could... and we need to know!$B$BShe\'s just around the bend, no doubt studying. Thanks, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2871, 'ruRU', 'Доставка реликвии', 'Не возьмешься ли ты доставить эту реликвию моей жене Вестии? Я был бы тебе очень признателен. Она не только почтенная жрица, но и одна из наиболее сведущих ученых. Я лично не пойму, что это за штука, хоть меня озолоти. Но уж если моя жена в ней не разберется, значит, не разберется никто.$b$bОна вон там, неподалеку. Занимается, небось. Спасибо, $n!', 'Доставьте таинственную реликвию Вестии Лунное Копье в Крепость Оперенной Луны.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2871, 'zhCN', '递送遗物', '如果你能帮我将这个东西送给我的妻子维斯提亚，那我会非常感激的。她不仅是我们这里的女祭司，也会是你所见过的最博学的学者之一。我即使抓破头皮也搞不清楚这个东西到底是什么，可我的妻子不同，如果世界上有人能知道这东西是什么，那无疑就是她了……我们也迫切地需要知道真相！$B$B拜托了，$n。', '将神秘遗物交给羽月要塞的维斯提亚·月矛。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2872, 'deDE', 'Stoleys Schuld', 'Sprecht mit Stoley beim Dampfdruckpier.', 'Mein alter Kumpel Stoley schuldet mir eine ganz bestimmte Flasche Rum. Er schmeißt die Bar am Dampfdruckpier in Tanaris, Kalimdor, und er versprach mir, dass der Rum dort stärker ist als alles, was man in Beutebucht finden kann. Er ist so davon überzeugt, dass er sagt, wenn ich diesen Rum probiere und ihm nicht zustimme, würde er das Geld, das er mir schuldet, verdoppeln!$B$BNun, ich glaube, er ist nicht mehr so zuversichtlich wie damals, als wir zuletzt miteinander geredet haben, und hat jetzt wohl Angst, mir diesen Rum zu geben. Ich lasse eine Wette aber nicht auf sich beruhen, also wie wäre es, wenn Ihr für mich mit ihm redet?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2872, 'esES', 'La deuda de Stoley', 'Habla con Stoley en el Puerto Bonvapor.', 'Mi viejo amigo Stoley me debe una botella muy especial de ron. Él atiende el bar del Puerto Bonvapor en Tanaris, Kalimdor, y me aseguró que el ron allí es más fuerte que cualquier cosa que podamos encontrar en Bahía del Botín. Está tan seguro de lo que dice que prometió darme el doble de dinero del que me debe si después de probarlo no opino lo mismo.$B$BBueno, yo creo que ahora ya no está tan seguro como en su día y no quiere que pruebe el ron. Sin embargo, yo nunca me echo atrás ante una apuesta, así que por qué no vas a hablar con él...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2872, 'esMX', 'La deuda de Stoley', 'Habla con Stoley en el Puerto Bonvapor.', 'Mi viejo amigo Stoley me debe una botella muy especial de ron. Él atiende el bar del Puerto Bonvapor en Tanaris, Kalimdor, y me aseguró que el ron allí es más fuerte que cualquier cosa que podamos encontrar en Bahía del Botín. Está tan seguro de lo que dice que prometió darme el doble de dinero del que me debe si después de probarlo no opino lo mismo.$B$BBueno, yo creo que ahora ya no está tan seguro como en su día y no quiere que pruebe el ron. Sin embargo, yo nunca me echo atrás ante una apuesta, así que por qué no vas a hablar con él...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2872, 'frFR', 'Les dettes de Stoley', 'Parler à Stoley, à Port Gentepression.', 'Mon vieux copain Stoley me doit une bouteille d’un rhum très spécial. Il tient un bar à Port Gentepression en Tanaris. C\'est en Kalimdor. Il soutient que ce rhum est plus fort que tout ce que l\'on peut trouver à Baie-du-Butin. Il y croit dur comme fer. À tel point que si je ne suis pas de son avis après avoir goûté cet alcool, je double ce qu\'il me doit !$B$BJe pense que depuis la dernière fois, il a perdu confiance, car il ne m’a toujours pas envoyé son fameux rhum à goûter. Un pari est un pari, je ne reviendrai pas dessus. Pourquoi n\'iriez-vous pas lui parler un peu ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2872, 'itIT', 'Stoley\'s Debt', 'Speak with Stoley in Steamwheedle Port.', 'My old buddy Stoley owes me a very special bottle of rum.  He tends the bar at Steamwheedle Port in Tanaris, Kalimdor,  and he promised me that the rum there is stronger than anything we can find in Booty Bay.  He\'s so confident that he says if I try that rum and don\'t agree with him, then he\'ll double the money he owes me!$B$BWell, I think he\'s not as confident as he was when last we spoke, and now he\'s afraid to give me that rum.  But I don\'t back down from wagers, so why don\'t you talk to him for me...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2872, 'koKR', '스톨리의 빚', '스팀휘들 항구에 있는 스톨리와 대화해야 합니다.', '내 오랜 친구 스톨리는 나에게 아주 특별한 럼주 한 병을 빚졌다오. 그 친구는 칼림도어의 타나리스에 있는 스팀휘들 항구에서 술집을 운영하는데 그곳의 럼주는 무법항에서 찾을 수 있는 다른 어떤 것보다 더 독하다고 하더군. 얼마나 자신 있어 하는지, 내가 그 술을 마셔 보고 그의 말에 동의하지 않는다면 내게 빚진 돈을 두 배로 갚겠다고 하는 거요!$B$B뭐, 지금은 우리가 지난번 얘기를 나눌 때만큼 그렇게 자신 있어 하는 것 같진 않지만 말이야. 이제는 내게 그 럼주를 주는 것을 꺼려하고 있다니까. 그렇지만 나는 내기에서 물러서는 성격이 못되니 나 대신 그 친구와 얘기를 나누어 보지 않겠소?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2872, 'ptBR', 'Stoley\'s Debt', 'Speak with Stoley in Steamwheedle Port.', 'My old buddy Stoley owes me a very special bottle of rum. He tends the bar at Steamwheedle Port in Tanaris, Kalimdor, and he promised me that the rum there is stronger than anything we can find in Booty Bay. He\'s so confident that he says if I try that rum and don\'t agree with him, then he\'ll double the money he owes me!$B$BWell, I think he\'s not as confident as he was when last we spoke, and now he\'s afraid to give me that rum. But I don\'t back down from wagers, so why don\'t you talk to him for me...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2872, 'ruRU', 'Долг Столи', 'Поговорите со Столи из Порта Хитрой Шестеренки.', 'Мой давний приятель Столи должен мне особую бутылку рома. Он держит бар в Порту Хитрой Шестеренки в Танарисе, и клялся, что ром у него крепче, чем во всей Пиратской Бухте. Он даже заявил, что удвоит свой долг мне, если я попробую его напиток и не соглашусь с ним!$b$bДумаю, он потом пожалел о своих словах, потому и не шлет мне ром. Но я ничего не забываю, поэтому поговори с ним вместо меня...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2872, 'zhCN', '斯杜雷的债务', '与热砂港的斯杜雷谈一谈。', '我的老朋友欠我一瓶特殊的朗姆酒。他在塔纳利斯沙漠的热砂港里经营着一家酒吧，他向我保证说，那里的朗姆酒绝对比藏宝海湾的任何酒都要香醇，还说如果我喝了他的酒不满意的话，那他就会双倍奉还他欠我的钱！$B$B好吧，我想他现在已经不像他上次说这话的时候那么自信了，他害怕给我尝他的朗姆酒了。但是我还是想继续这个赌注，那么你就替我去和他谈谈吧……', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2872, 'zhTW', '斯杜雷的債務', '與熱砂港的斯杜雷談話。', '我的老朋友欠我一瓶特殊的朗姆酒。他在塔納利斯沙漠的熱砂港裡經營著一家酒吧，他向我保證說，那裡的朗姆酒絕對比藏寶海灣的任何酒都要香醇，還說如果我喝了他的酒不滿意的話，那他就會雙倍奉還他欠我的錢!$B$B好吧，我想他現在已經不像他上次說這話的時候那麼自信了，他害怕給我嘗他的朗姆酒了。但是我還是想繼續這個賭注，那麼你就替我去和他談談吧...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2873, 'deDE', 'Stoleys Lieferung', 'Bringt Stoleys Lieferung zu Stoley beim Dampfdruckpier.', 'Ich schulde \"Seewolf\" MacKinley in Beutebucht etwas Rum, um eine Wette zu begleichen, aber mir ist der Rum ausgegangen! Kapitän Cuergo hat meine letzte Lieferung gestohlen und lagert sie in dieser ummauerten Piratenstadt im Süden.$B$BWenn Ihr meine Rumlieferung finden und zurückbringen könnt, dann kann ich MacKinley beweisen, dass Kalimdors Drinks die aus Beutebucht um Längen schlagen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2873, 'esES', 'El envío de Stoley', 'Llévale el envío de Stoley a Stoley, que está en el Puerto Bonvapor.', 'Le debo algo de ron a \"Lobo de Mar\" MacKinley de Bahía del Botín por una apuesta, ¡pero no me queda ni gota! El capitán Cuergo robó mi último cargamento y lo escondió en esa ciudad pirata amurallada situada al sur.$B$BSi encuentras mi cargamento de ron y me lo devuelves, quizás pueda demostrarle a MacKinley que el ron de Bahía del Botín no le llega a este ni a la suela del zapato.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2873, 'esMX', 'El envío de Stoley', 'Llévale el envío de Stoley a Stoley, que está en el Puerto Bonvapor.', 'Le debo algo de ron a \"Lobo de Mar\" MacKinley de Bahía del Botín por una apuesta, ¡pero no me queda ni gota! El capitán Cuergo robó mi último cargamento y lo escondió en esa ciudad pirata amurallada situada al sur.$B$BSi encuentras mi cargamento de ron y me lo devuelves, quizás pueda demostrarle a MacKinley que el ron de Bahía del Botín no le llega a este ni a la suela del zapato.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2873, 'frFR', 'La cargaison de Stoley', 'Apportez la Cargaison de Stoley à Port Gentepression et remettez-la à Stoley.', 'Je dois du rhum à MacKinley \"Loup des mers\" pour payer une dette de jeu, mais je n\'en ai plus ! Le capitaine Cuergo a volé ma dernière caisse et il l\'a entreposée dans cette ville de pirates fortifiée, au sud.$B$BSi vous pouviez rapatrier ma cargaison de rhum, alors je pourrais prouver à MacKinley que la gnôle de Kalimdor est autrement plus musclée que le petit-lait qu\'ils boivent à Baie-du-Butin.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2873, 'itIT', 'Stoley\'s Shipment', 'Bring Stoley\'s Shipment to Stoley in Steamwheedle Port.', 'I owe \"Sea Wolf\" MacKinley in Booty Bay some rum, to settle a bet, but I\'m all out of rum!  Captain Cuergo stole my last shipment, and he has it stowed in that walled pirate town to the south.$B$BIf you can find my shipment of rum and return it to me, then I can prove to MacKinley that Kalimdor\'s booze beats the dirt out of Booty Bay!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2873, 'koKR', '스톨리의 뱃짐', '스팀휘들 항구에 있는 스톨리에게 뱃짐을 가져가야 합니다.', '지금 럼주가 다 떨어지고 없소! 무법항의 바다 늑대, 맥킨리랑 건 내기에서 이기려면 럼주를 좀 줘야 하는데 말이오. 최근에 쿠에르고 선장이 내 뱃짐을 훔쳐서 해적 마을에 숨겨 두고 있다오. 성벽으로 둘러싸인 그 마을은 여기에서 남쪽에 있소.$B$B럼주가 들어 있는 내 뱃짐을 찾아와 준다면 칼림도어의 술이 무법항 것보다 훨씬 낫다는 걸 맥킨리에게 확실히 보여줄 수 있소이다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2873, 'ptBR', 'Stoley\'s Shipment', 'Bring Stoley\'s Shipment to Stoley in Steamwheedle Port.', 'I owe \"Sea Wolf\" MacKinley in Booty Bay some rum, to settle a bet, but I\'m all out of rum! Captain Cuergo stole my last shipment, and he has it stowed in that walled pirate town to the south.$B$BIf you can find my shipment of rum and return it to me, then I can prove to MacKinley that Kalimdor\'s booze beats the dirt out of Booty Bay!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2873, 'ruRU', 'Посылка Столи', 'Отыщите партию товара Столи и возвратите ее хозяину в Порт Хитрой Шестеренки.', 'Я должен старине Маккинли из Пиратской Бухты немного рома. Проблема в том, что ром у меня закончился! Капитан Куэрго украл последнюю партию и прячет в стенах огороженного пиратского поселения на юге.$b$bЕсли ты отыщешь мою партию рома, я смогу доказать Маккинли, что калимдорская выпивка в сто раз лучше пойла из Пиратской Бухты!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2873, 'zhCN', '斯杜雷的货物', '将斯杜雷的货物交给热砂港的斯杜雷。', '我欠藏宝海湾的“海狼”马克基雷一些朗姆酒，那是打赌输的，但我现在一瓶朗姆酒都没有了！科尔戈船长偷了我最后一船货，他把它们藏在南边的那个戒备森严的海盗镇里。$B$B如果你能找到我的那批朗姆酒并把它们给我拿回来的话，那我就可以向马克基雷证明，藏宝海湾的那些酒跟我的朗姆酒比起来只能被称作垃圾！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2873, 'zhTW', '斯杜雷的貨物', '將斯杜雷的貨物交給熱砂港的斯杜雷。', '我欠藏寶海灣的『海狼』麥克基雷一些朗姆酒，那是打賭輸的，但我現在一瓶朗姆酒都沒有了!科爾戈船長偷了我最後一船貨，他把它們藏在南邊的那個戒備森嚴的海賊鎮裡。$B$B如果你能找到我的那批朗姆酒並把它們給我拿回來的話，那我就可以向麥克基雷證明，藏寶海灣的那些酒跟我的朗姆酒比起來只能被稱作垃圾!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2874, 'deDE', 'Lieferung für MacKinley', 'Bringt Stoleys Flasche zu \"Seewolf\" MacKinley in Beutebucht.', 'Danke nochmals für Eure Hilfe, $n. Hier habt Ihr eine Flasche Dampfdruckrum. Bringt sie zu MacKinley und er wird endlich seine verrückten Ansichten über Drinks aus Beutebucht über Bord werfen können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2874, 'esES', 'Un envío para MacKinley', 'Llévale la botella de Stoley a \"Lobo de Mar\" MacKinley, en Bahía del Botín.', 'Gracias de nuevo por tu ayuda, $n. Aquí tienes una botella de ron de la sentina Bonvapor. Llévasela a MacKinley para que pueda comprobar por fin lo que se dice del ron de Bahía del Botín.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2874, 'esMX', 'Un envío para MacKinley', 'Llévale la botella de Stoley a \"Lobo de Mar\" MacKinley, en Bahía del Botín.', 'Gracias de nuevo por tu ayuda, $n. Aquí tienes una botella de ron de la sentina Bonvapor. Llévasela a MacKinley para que pueda comprobar por fin lo que se dice del ron de Bahía del Botín.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2874, 'frFR', 'Livraison à MacKinley', 'Apportez la Bouteille de Stoley à MacKinley « Loup des mers » à Baie-du-Butin.', 'Merci encore pour votre aide, $n. Voici une bouteille de rhum de cale Gentepression. Portez-la à MacKinley et il pourra remballer ses préjugés à propos des alcools de Baie-du-Butin.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2874, 'itIT', 'Deliver to MacKinley', 'Bring Stoley\'s Bottle to \"Sea Wolf\" MacKinley in Booty Bay.', 'Thanks again for your help, $n.  Here\'s a bottle of Steamwheedle bilge rum.  Take it to MacKinley and he can finally put to rest his crazy beliefs about Booty Bay booze.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2874, 'koKR', '맥킨리에게 배달', '무법항에 있는 \"바다늑대\" 맥킨리에게 칼림도어산 럼주가 든 스톨리의 병을 가져가야 합니다.', '다시 한번 도와줘서 고맙다는 말을 하고 싶소. 여기 스팀휘들 럼주가 한 병 있소. 이걸 맥킨리에게 갖다주면 그 친구도 마침내 무법항 술에 대한 그 맹목적인 믿음을 버릴 수 있을 거요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2874, 'ptBR', 'Deliver to MacKinley', 'Bring Stoley\'s Bottle to \"Sea Wolf\" MacKinley in Booty Bay.', 'Thanks again for your help, $n. Here\'s a bottle of Steamwheedle bilge rum. Take it to MacKinley and he can finally put to rest his crazy beliefs about Booty Bay booze.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2874, 'ruRU', 'Ром для Маккинли', 'Отнесите бутылку Столи \"Морскому волку\" Маккинли в Пиратскую Бухту.', 'Еще раз спасибо за помощь, $n. Вот тебе бутылка картельского рома. Отнеси ее Маккинли, пора развеять его заблуждения относительно рома из Пиратской Бухты.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2874, 'zhCN', '给马克基雷的货物', '将斯杜雷的瓶子交给藏宝海湾的“海狼”马克基雷。', '再次感谢你的帮助，$n。这里有一瓶热砂港朗姆酒。把它交给马克基雷，这样他就可以抛弃藏宝海湾烈酒，改喝热砂港朗姆酒了！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2874, 'zhTW', '給麥克基雷的貨物', '將斯杜雷的瓶子交給藏寶海灣的『海狼』麥克基雷。', '再次感謝你的幫助，$n。這裡有一瓶熱砂港朗姆酒。把它交給麥克基雷，這樣他就可以拋棄藏寶海灣烈酒，改喝熱砂港朗姆酒了!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2875, 'deDE', 'GESUCHT: Andre Feuerbart', 'Bringt Sicherheitschef Bilgenritzel beim Dampfdruckpier Andres Kopf.', 'Der niederträchtige Pirat Andre Feuerbart wird aufgrund seiner gegen die Einwohner des Dampfdruckpiers und Gadgetzan gerichteten Verbrechen gesucht.$B$BDurch Erlass der Ratsregierung ist hiermit jeder, der dies liest, zu tödlicher Gewaltanwendung bei der Festnahme von Andre und seinen Spießgesellen autorisiert.$B$BDes Weiteren gilt durch zusätzlichen Erlass, dass, sollte Andre verstorben oder anderweitig während seiner Anhörung unpässlich sein, sein Kopf als Ersatz gilt und die Aushändigung dieses Kopfes an Sicherheitschef Bilgenritzel als vertragsbindend angesehen wird.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2875, 'esES', 'SE BUSCA: Andre Barbafuego', 'Llévale la cabeza de Andre al jefe de seguridad Pasaquillas, que está en el Puerto Bonvapor.', 'Se busca al malvado pirata Andre Barbafuego por crímenes contra Bonvapor y Gadgetzan.$B$BPor decreto del consejo de gobierno, todo el que lea esto tiene autorización expresa para utilizar fuerza letal durante la ejecución del arresto de Andre y sus cómplices.$B$BPor nuevo decreto, de producirse el fallecimiento de Andre o de sufrir este algún tipo de perjuicio durante la sesión de interrogatorio posterior a su detención, su cabeza actuará como prueba irrefutable de este hecho y su entrega ante el jefe de seguridad Pasaquillas constituye contrato vinculante.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2875, 'esMX', 'SE BUSCA: Andre Barbafuego', 'Llévale la cabeza de Andre al jefe de seguridad Pasaquillas, que está en el Puerto Bonvapor.', 'Se busca al malvado pirata Andre Barbafuego por crímenes contra Bonvapor y Gadgetzan.$B$BPor decreto del consejo de gobierno, todo el que lea esto tiene autorización expresa para utilizar fuerza letal durante la ejecución del arresto de Andre y sus cómplices.$B$BPor nuevo decreto, de producirse el fallecimiento de Andre o de sufrir este algún tipo de perjuicio durante la sesión de interrogatorio posterior a su detención, su cabeza actuará como prueba irrefutable de este hecho y su entrega ante el jefe de seguridad Pasaquillas constituye contrato vinculante.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2875, 'frFR', 'ON RECHERCHE : André Barbe-en-feu', 'Rapportez la tête d\'André au Chef de la sécurité Vizisanie, à Port Gentepression.', 'L\'ignoble pirate André Barbe-en-feu est recherché pour crimes contre Gentepression et Gadgetzan.$B$BPar décret du conseil gouvernemental, toute personne lisant ce texte est autorisée à utiliser la force, et à tuer si nécessaire, pour procéder à l\'arrestation d\'André et de ses complices.$B$BSi André était décédé ou indisposé pendant son interrogatoire, un autre décret précise que sa tête servira de preuve. La présentation de ladite tête au Chef de la sécurité Vizisanie constitue un contrat valable et inaliénable.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2875, 'itIT', 'WANTED: Andre Firebeard', 'Bring Andre\'s Head to Security Chief Bilgewhizzle in Steamwheedle Port.', 'The vile pirate Andre Firebeard is wanted for crimes against Steamwheedle and Gadgetzan.$B$BBy decree of governing counsel, any who read this are hereby authorized to use lethal force during the apprehension of Andre and his abettors.$B$BBy further decree, should Andre be deceased, or otherwise indisposed, during the time of his hearing, his head may act as proxy, and deliverance of such head to Security Chief Bilgewhizzle constitutes a binding, and tenderable, contract.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2875, 'koKR', '현상 수배: 앙드레 파이어비어드', '앙드레를 처치하고 그 증거로 그의 머리카락을 스팀휘들 항구에 있는 보안과장 빌지위즐에게 가져가야 합니다.', '스팀휘들 항구와 가젯잔을 휩쓸고 다니는 사악한 해적 앙드레 파이어비어드를 현상 수배한다.$B$B이 글을 읽는 모든 이에게 허가한다! 의회의 판결에 따라 앙드레와 공범들을 체포할 때 죽음을 불러올 정도의 힘을 사용해도 좋다.$B$B앙드레가 죽거나, 심하게 다쳐서 보안과장 빌지위즐에게 호송하지 못할 경우 그의 머리카락을 잘라서 증거로 가져가도 현상금을 받을 수 있다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2875, 'ptBR', 'WANTED: Andre Firebeard', 'Bring Andre\'s Head to Security Chief Bilgewhizzle in Steamwheedle Port.', 'The vile pirate Andre Firebeard is wanted for crimes against Steamwheedle and Gadgetzan.$B$BBy decree of governing counsel, any who read this are hereby authorized to use lethal force during the apprehension of Andre and his abettors.$B$BBy further decree, should Andre be deceased, or otherwise indisposed, during the time of his hearing, his head may act as proxy, and deliverance of such head to Security Chief Bilgewhizzle constitutes a binding, and tenderable, contract.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2875, 'ruRU', 'РАЗЫСКИВАЕТСЯ: Андре Огнебородый', 'Принесите голову Андре Огнебородого начальнику отдела безопасности Чепухастеру в Порт Хитрой Шестеренки', 'Злобный пират Андре Огнебородый разыскивается за преступления против картеля Хитрой Шестеренки и Прибамбасска.$B$BСогласно указу правящего совета, все лица, прочитавшие данный документ, уполномочены применять любые меры при задержании Андре и его пособников.$b$bСогласно следующему указу, если Андре скончается или по иным причинам не сможет предстать перед судом, его может представлять его голова. Предоставление упомянутой головы начальнику отдела безопасности Чепухастеру повлечет за собой заключение весьма выгодного и ответственного контракта.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2875, 'zhCN', '通缉：安德雷·费尔比德', '将安德雷的徽记交给热砂港的安全主管吉罗姆·比格维兹。', '可恶的海盗安德雷·费尔比德因对热砂港和加基森所犯的罪行正被通缉。$B$B根据政府讨论所发布的决议，任何阅读这则通缉令的人有权在逮捕安德雷和他的同党过程中采取可能致其死亡的手段。$B$B进一步的决议表明，如在审讯期间，安德雷死了或病了，他的徽记可以作为代替品，将它交给安全主管吉罗姆·比格维兹仍将使这份通缉令生效。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2875, 'zhTW', '懸賞:安德雷·火鬍', '將安德雷的頭顱交給熱砂港的安全主管膨嘯。', '可惡的海賊安德雷·火鬍因對熱砂港和加基森所犯的罪行正被通緝。$B$B根據政府討論所發佈的決議，任何閱讀這則通緝令的人有權在逮捕安德雷和他的同黨過程中採取可能致其死亡的手段。$B$B進一步的決議表明，如在審訊期間，安德雷死了或病了，他的人頭可以作為代替品，將它交給安全主管膨嘯仍將使這份通緝令生效。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2876, 'deDE', 'Schiffzeitpläne', 'Berichtet den Behörden des Dampfdruckpiers von den Schiffzeitplänen.', 'Auf dieser Schriftrolle sind die Zeitpläne für Lieferungen zwischen dem Dampfdruckpier und Beutebucht verzeichnet. Sowohl vergangene als auch zukünftige Lieferungen sind aufgeführt sowie Schiffsnamen. Große rote Kreuze befinden sich neben einigen der Schiffsreisen und Anmerkungen wie \"Große Beute\" oder \"Harter Kampf\" neben anderen.$B$BJemand beim Dampfdruckpier wird dieses Dokument sicher sehen wollen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2876, 'esES', 'El registro náutico', 'Informa del registro de envíos a alguna autoridad del Puerto Bonvapor.', 'Este pergamino contiene el registro de envíos realizados entre el Puerto Bonvapor y Bahía del Botín. Están incluidos tanto los envíos ya hechos como los pendientes, así como los nombres de los barcos, algunos con una X al lado y otros con anotaciones del tipo \"GRAN BOTÍN\" y \"DURA LUCHA\".$B$BSeguro que hay alguien en el Puerto Bonvapor interesado en echarle un vistazo a este documento.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2876, 'esMX', 'El registro náutico', 'Informa del registro de envíos a alguna autoridad del Puerto Bonvapor.', 'Este pergamino contiene el registro de envíos realizados entre el Puerto Bonvapor y Bahía del Botín. Están incluidos tanto los envíos ya hechos como los pendientes, así como los nombres de los barcos, algunos con una X al lado y otros con anotaciones del tipo \"GRAN BOTÍN\" y \"DURA LUCHA\".$B$BSeguro que hay alguien en el Puerto Bonvapor interesado en echarle un vistazo a este documento.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2876, 'frFR', 'Horaires des bateaux', 'Présenter les Horaires des bateaux aux autorités de Port Gentepression.', 'Ce parchemin détaille le calendrier des livraisons entre Port Gentepression et Baie-du-Butin. La liste contient le nom des navires et des dates, passées ou à venir. Certaines lignes sont cochées d’un grand X. D\'autres sont annotées avec \"Bon butin\" ou \"Dur combat\".$B$BQuelqu\'un à Port Gentepression apprécierait certainement de voir ce document.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2876, 'itIT', 'Ship Schedules', 'Report the Ship Schedules to an authority in Steamwheedle Port.', 'This scroll contains the schedules of shipments between Steamwheedle Port and Booty Bay. Both past and future shipments are included, along with ship names, with large \'X\'s next to some ships\' journeys, and annotations like \"GREAT LOOT\" and \"HARD FIGHT\" next to others.$B$BSomeone at Steamwheedle Port would want to see this document.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2876, 'ptBR', 'Ship Schedules', 'Report the Ship Schedules to an authority in Steamwheedle Port.', 'This scroll contains the schedules of shipments between Steamwheedle Port and Booty Bay. Both past and future shipments are included, along with ship names, with large \'X\'s next to some ships\' journeys, and annotations like \"GREAT LOOT\" and \"HARD FIGHT\" next to others.$B$BSomeone at Steamwheedle Port would want to see this document.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2876, 'ruRU', 'Расписание кораблей', 'Сообщите о корабельном расписании руководству Порта Хитрой Шестеренки.', 'В свитке приводится список рейсов между Портом Хитрой Шестеренки и Пиратской Бухтой. Там перечислены все прошлые и будущие грузы с указанием названий кораблей, везущих их. Напротив некоторых стоит большой крестик, напротив других заметки \"ОТЛИЧНАЯ ДОБЫЧА\" и \"СИЛЬНАЯ ОХРАНА\".$b$bКое-кто из Порта Хитрой Шестеренки очень заинтересуется этим документом.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2876, 'zhCN', '船运时刻表', '这卷轴上面记载着热砂港和藏宝湾之间的船运时刻表，包括过去和未来计划中的船运批次和船名，一些船的旅程旁有个大大的红叉记号，剩下的旁边有类似“华丽的战利品”和“坚固的战舰”之类的字样。$B$B热砂港那里的人一定非常想看看这个文件。', '把船运时刻表交给热砂港的某个官员。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2877, 'deDE', 'Den Lauerfels aufräumen', 'Tötet 10 grüne Schlicke sowie 10 jadegrüne Schlamme und meldet Euch dann wieder bei Fraggar Donnermantel beim Nistgipfel.', 'Wir Wildhämmer haben die Vergangenheit besser in Erinnerung als unsere Brüder in Eisenschmiede. Wenn Ihr Euch hier beliebt machen wollt, dann müsst Ihr Euch schon Eure Hände für uns schmutzig machen!$B$BDie Horde hat ihre Stellungen im Hinterland beim Lauerfels aufgegeben. Jetzt wimmelt es dort von üblen Schlicken und Schlammen. Wir haben Pläne für dieses Gebiet, aber zuerst muss uns eine Aufräummannschaft dort eine Bresche hineinschlagen.$B$BUnd hier kommt Ihr ins Spiel, Sonnenschein! Geht da runter und erledigt 10 grüne Schlicke und 10 jadegrüne Schlamme und meldet Euch dann wieder bei mir. Zieht los!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2877, 'esES', 'La limpieza de Roca Oculta', 'Mata 10 lodos verdes y 10 mocos de jade e informa a Fraggar Mantrueno en Pico Nidal.', 'Los Martillo Salvaje recordamos el pasado mejor que nuestros hermanos de Forjaz. ¡Si quieres hacer amigos aquí, tendrás que ensuciarte las manos!$B$BLa Horda ha abandonado sus puestos de Roca Oculta en las Tierras del Interior; ahora todo está infestado de babosas y mocos. Tenemos planes para la región, pero antes hay que enviar una brigada de limpieza.$B$B¡Ese es tu trabajo, hermosura! Ve allí y elimina 10 lodos verdes y 10 mocos de jade. ¡Andando!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2877, 'esMX', 'La limpieza de Roca Oculta', 'Mata 10 lodos verdes y 10 mocos de jade e informa a Fraggar Mantrueno en Pico Nidal.', 'Los Martillo Salvaje recordamos el pasado mejor que nuestros hermanos de Forjaz. ¡Si quieres hacer amigos aquí, tendrás que ensuciarte las manos!$B$BLa Horda ha abandonado sus puestos de Roca Oculta en las Tierras del Interior; ahora todo está infestado de babosas y mocos. Tenemos planes para la región, pero antes hay que enviar una brigada de limpieza.$B$B¡Ese es tu trabajo, hermosura! Ve allí y elimina 10 lodos verdes y 10 mocos de jade. ¡Andando!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2877, 'frFR', 'Nettoyer le rocher de l\'Affût', 'Tuer 10 Vases vertes et 10 Limons de jade puis faire votre rapport à Fraggar Tonnemante au Nid-de-l\'Aigle.', 'Nous, les Marteaux-Hardis, nous souvenons du passé, mieux que nos frères de Forgefer. Si vous désirez vous faire des amis, alors travaillez pour nous !$B$BLa Horde a abandonné ses positions dans les Hinterlands, autour du rocher de l\'Affût qui est maintenant envahi par des vases et des limons. Nous avons des plans pour ce secteur, mais nous avons besoin de quelqu\'un pour faire du nettoyage.$B$BC\'est là que vous intervenez ! Allez là-bas et liquidez dix vases vertes et dix limons de jade puis revenez me faire votre rapport ici. Exécution !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2877, 'itIT', 'Skulk Rock Clean-up', 'Kill 10 Green Sludges and 10 Jade Oozes, and then report back to Fraggar Thundermantle in Aerie Peak.', 'We Wildhammers remember the past, more than our brothers in Ironforge.  If you want to make friends here, then you get your hands dirty for us!$B$BThe Horde abandoned their positions in the Hinterlands around Skulk Rock; it\'s now overrun with nasty sludges and oozes.  We\'ve got plans on that area, but first we need a clean-up crew to make a hole for us.$B$BThat\'s where you come in, Sunshine!  Head down there and take out ten green sludges and ten jade oozes, then report back to me here.  Move out!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2877, 'koKR', '굼벵이 바위굴 청소', '녹색 녹괴물 10마리와 비취 수액괴물 10마리를 처치한 후 맹금의 봉우리에 있는 프라가르 썬더맨틀에게 돌아와 보고해야 합니다.', '우리 와일드해머는 아이언포지의 형제들보다 과거를 더 잘 기억한다네. 여기서 친구를 사귀고 싶다면 우리 대신 지저분한 일을 좀 맡아 줘야겠어!$B$B동부 내륙지에 있는 굼벵이 바위굴에서 호드가 물러났네. 이제 그곳은 고약한 녹괴물과 수액괴물이 득실거리지. 우리에게 그 지역에 대한 계획이 있네. 우리가 그곳으로 가기 전에 일꾼을 시켜 먼저 청소를 해놓는 거지.$B$B바로 자네가 적임자일 것 같군! 그곳으로 가서 녹색 녹괴물 10마리와 비취 수액괴물 10마리를 제거한 후에 돌아와 내게 보고하게. 출발!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2877, 'ptBR', 'Skulk Rock Clean-up', 'Kill 10 Green Sludges and 10 Jade Oozes, and then report back to Fraggar Thundermantle in Aerie Peak.', 'We Wildhammers remember the past, more than our brothers in Ironforge. If you want to make friends here, then you get your hands dirty for us!$B$BThe Horde abandoned their positions in the Hinterlands around Skulk Rock; it\'s now overrun with nasty sludges and oozes. We\'ve got plans on that area, but first we need a clean-up crew to make a hole for us.$B$BThat\'s where you come in, Sunshine! Head down there and take out ten green sludges and ten jade oozes, then report back to me here. Move out!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (2877, 'ruRU', 'Большая уборка на Осклизлой скале', 'Мы, клан Громового Молота, помним прошлое куда лучше, чем наши братья из Стальгорна. Если хочешь, чтобы к тебе тут хорошо относились, не бойся грязной работы!$B$BОрда забросила одну из своих баз во Внутренних землях, она находится на Осклизлой скале. Сейчас эту базу заполонили слизнюки и пиявки, у нас были на нее планы, но пока там кишат эти твари, мы не можем занять эту позицию.$B$BВот тебе, собственно, и работка! Отправляйся туда и убей по 10 зеленых слякочей и нефритовых слизнюков, а потом доложи мне, как все прошло. Бегом!', 'Убейте 10 зеленых слякочей и 10 нефритовых слизнюков, после чего доложитесь Фраггару Громовой Мантии, который ждет вас на Заоблачном пике.', '', 'Вернитесь к Фраггару Громовой Мантии на Заоблачный пик, что во Внутренних землях.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (2877, 'zhCN', '清理隐匿石', '比起那些铁炉堡的兄弟们来，我们蛮锤部族对于过去曾发生过的事情有着更加深刻的记忆。如果你想在这里交朋友的话，那么你就要证明给我们看！$B$B部落的蛮子们从隐匿石一带撤了出去，现在那里满地都是淤泥怪。我们想要占领那个地区，不过首先我们要找些人来帮我们清理一下恼人的淤泥怪。$B$B这就是你要做的事情了！到那边去干掉十个绿色淤泥怪和十个玉石软泥怪，然后回到这里来向我报告。快出发吧！', '杀掉10个绿色淤泥怪和10个玉石软泥怪，然后回到鹰巢山向弗拉加尔报告。', '', '去辛特兰找鹰巢山的弗拉加尔。', '', '', '', '', 0);
