INSERT INTO `quest_template_locale` VALUES (3082, 'esES', 'Tablilla con grabados', 'Habla con Jen\'shan en el Valle de los Retos.', 'Ah, mientras estabas fuera llegó una tablilla para ti, $n.$B$BLéela cuando tengas tiempo, si no me equivoco viene de parte de la instructora de cazadores, Jen\'shan. Querrá tener unas palabras contigo cuando estés $glisto:lista;.', '', '', '', '', '', '', 0);
