INSERT INTO `quest_template_locale` VALUES (3095, 'frFR', 'Parchemin simple', 'Lire le Parchemin simple, et parler à Dannal Stern, au Glas.', 'Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu’il est de quelque importance, car il porte le sceau du Maître des guerriers, Dannal. À votre place, je prendrais le temps de le lire avant de repartir.', '', '', '', '', '', '', 0);
