INSERT INTO `quest_template_locale` VALUES (3402, 'deDE', 'Der Untermarkt', 'Ich will, dass Ihr Vizzklick in Gadgetzan persönlich die Nachricht von Kovics Niederlage überbringt. Ich bin ziemlich sicher, Kovics Tod wird ihn so erfreuen, dass er Euch eine großartige Belohnung geben wird.', 'Sprecht mit Vizzklick in Gadgetzan.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (3402, 'esES', 'El Mercado Negro', 'Quiero que tú des la noticia de la derrota de Kovic a Vizzklick, en Gadgetzan. Se alegrará de saber que Kovic ha muerto; seguro que su recompensa será generosa.', 'Habla con Vizzklick en Gadgetzan.|n', '', '', '', '', '', '', 18019);
