INSERT INTO `quest_template_locale` VALUES (3402, 'frFR', 'Le Sous-marché', 'Je veux que vous informiez personnellement Vizzklick à Gadgetzan de la défaite de Kovic. Il sera si heureux d\'apprendre sa mort qu\'il vous remettra sûrement une récompense.', 'Parler à Vizzklick à Gadgetzan.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (3402, 'ruRU', 'Черный рынок', 'Поговорите с Визгликом в Прибамбасске.', 'Мне нужно, чтобы ты лично $gдонес:донесла; весть о кончине Ковиса Визглику в Прибамбасск. Он будет так рад, что непременно щедро наградит тебя.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (3402, 'zhCN', '黑市交易', '我要你把考维克被干掉的消息亲自带给加基森的维兹格里克，他知道考维克死亡的消息一定会很高兴的，我敢说他肯定会为此给你丰厚的奖励。', '和加基森的维兹格里克谈一谈。', '', '', '', '', '', '', 0);
