INSERT INTO `quest_template_locale` VALUES (3441, 'frFR', 'Châtiment divin', 'Écouter le récit de Kalaran Lamevent.', 'Écoutez mon récit, $gaventurier:aventurière;.', 'Histoire de Kalaran', '', '', '', '', 'Retournez voir Kalaran Lamevent au Chaudron, dans la Gorge des Vents brûlants.', 0);
INSERT INTO `quest_template_locale` VALUES (3441, 'itIT', 'Divine Retribution', 'Listen as Kalaran Windblade tells his story.', 'Hear my tale, adventurer.', 'Kalaran Story', '', '', '', '', 'Return to Kalaran Windblade at The Cauldron in Searing Gorge.', 0);
