INSERT INTO `quest_template_locale` VALUES (3482, 'zhCN', '布满孔洞的黑匣子*', '这个盒子的表面已经非常老旧了,因为它他酸中泡了很久了--它一定在那只巨大的秃鹫胃里呆了很久,$B$B把盒子翻过来,在它底部有个符记,在这符记下面写的是:\"Kr..el Ko.l..ard \"', '把这个盒子带给克拉维尔', '', '', '', '', '', '', 0);
