INSERT INTO `quest_template_locale` VALUES (3567, 'esMX', 'A la cima', '', '', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (3567, 'frFR', 'Au sommet', '', '', '', '', '', '', '', '', 18019);
