INSERT INTO `quest_template_locale` VALUES (3702, 'frFR', 'Les ruines fumantes de Thaurissan', 'Écouter l\'historienne royale Archesonus raconter l\'histoire de Thaurissan.', 'Est-ce que vous aimeriez entendre une petite histoire sur les nains sombrefers ?', 'Histoire de Thaurissan', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (3702, 'itIT', 'Le Rovine fumanti di Thaurissan', 'Ascolta la Storica Reale Archesonus rievocare la storia di Thaurissan.', 'Vuoi ascoltare una breve storia dei Nani Ferroscuro?', 'Ascolta la storia di Thaurissan.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (3702, 'koKR', '불타는 타우릿산의 폐허', '왕실사학자 아케소누스가 들려 주는 타우릿산의 역사를 들어야 합니다.', '검은무쇠 드워프의 역사를 간단히 들어 보지 않으시겠습니까?', '타우릿산의 이야기', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (3702, 'ptBR', 'As fumegantes Ruínas de Thaurissan', 'Escute a Historiadora Real Arquefilis recontar a história de Thaurissan.', 'Gostaria de escutar uma breve história sobre os anões Ferro Negro?', 'História de Thaurissan', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (3702, 'ruRU', 'Дымящиеся руины Тауриссана', 'Узнайте от королевского историка Аркессон историю Тауриссана.', 'Не желаете ли услышать краткий обзор истории дворфов Черного Железа?', 'Выслушайте историю о Тауриссане.', 'Выслушайте историю о Тауриссане.', '', '', '', 'Вернитесь к королевскому историку Аркессон в Дун Морог.', 0);
INSERT INTO `quest_template_locale` VALUES (3702, 'zhCN', '索瑞森废墟', '听皇家历史学家阿克瑟努斯讲述索瑞森的历史。', '你愿意听听黑铁矮人的简要历史吗？', '索瑞森的故事', '', '', '', '', '', 0);
