INSERT INTO `quest_template_locale` VALUES (3788, 'ruRU', 'Просьба друида', 'Простите, друг мой...$b$bОдин мой коллега хотел бы побеседовать с вами об исследованиях, которые вы ведете в сотрудничестве с Кругом Кенария. Это друид по имени Квинтис Ночной Костер. Он проживает здесь же, в Крепости Оперенной Луны. Он хотел бы поговорить с вами насчет рассветницы... а главное, очень просит вас, чтобы эта беседа не дошла до ушей верховного друида.$b$bЕсли вас это интересует, поговорите с ним, и вам все станет ясно.', 'Поговорите с друидом по имени Квинтис Ночной Костер в Крепости Оперенной Луны.', '', '', '', '', '', '', 18019);
