INSERT INTO `quest_template_locale` VALUES (3901, 'deDE', 'Rattling the Rattlecages', 'Kill 8 Rattlecage Skeletons.', 'You\'ve shown your potential to the Forsaken under normal circumstances $n - now let\'s see you under pressure.$B$BThe Rattlecage skeletons, more mindless minions of menace, are a tougher foe than the zombies you have faced thus far.  Again, thin their numbers and prove yourself to the Forsaken.  Do not tarry - when you are done, speak to me again.', '', '', '', '', '', '', 0);
