INSERT INTO `quest_template_locale` VALUES (3903, 'deDE', 'Milly Geltmark', 'Sprecht mit Milly Geltmark.', 'Ihr habt Euch als verlässlich erwiesen, $n. Verlässlich und ohne Angst, Euch auch mal die Hände schmutzig zu machen, wie?$B$BTja, eine Freundin von mir, Milly Geltmark, hat ein paar Probleme. Sie ist mit ihrem Wagen auf der anderen Seite der Abtei, in der Nähe des Stalls. Ich bin mir sicher, sie könnte Eure Hilfe gut gebrauchen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (3903, 'esES', 'Milly Osworth', 'Habla con Milly Osworth.', '$n, has demostrado que eres $Gun:una; $c de confianza y que no temes al trabajo duro.$b$bMi amiga Milly Osworth está en apuros. Tiene la carreta al otro lado de la abadía, cerca del establo. Seguro que le va bien que le eches una mano.', '', '', '', '', '', '', 0);
