INSERT INTO `quest_template_locale` VALUES (4223, 'deDE', 'Die wahren Meister', 'Sprecht mit Marschall Maxwell in der Brennenden Steppe.', '<Magistrat Solomon seufzt.>$B$BIhr müsst in die Brennende Steppe zurückkehren, $n. Als Stellvertreter von Sturmwind erwartet man von Euch, dass Ihr Euch bei Marschall Maxwell meldet. Viel Glück.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (4223, 'esES', 'Los verdaderos maestros', 'Habla con el mariscal Maxwell en Las Estepas Ardientes.', '<El magistrado Solomon suspira.> $b$b$n, tienes que volver a Las Estepas Ardientes. Como ayudante del alguacil de Ventormenta, estarás a las órdenes del mariscal Maxwell. Suerte.', '', '', '', '', '', '', 0);
