INSERT INTO `quest_template_locale` VALUES (4361, 'frFR', 'Le porteur des mauvaises nouvelles', 'Retourner à Forgefer et apporter la mauvaise nouvelle au Roi Magni Barbe-de-bronze.', 'Vous devez délivrer cette information au roi Magni, $n. Il saura quoi faire !', '', '', '', '', '', '', 0);
