INSERT INTO `quest_template_locale` VALUES (5226, 'frFR', 'Retour au camp du Noroît', 'Apportez la Bouteille de la Flétrissure de Gahrron remplie à la grande prêtresse MacDonnell au camp du Noroît, dans les Maleterres de l’Ouest.', 'Vous fermez avec précaution le robinet sur le panneau de contrôle tout en regardant le liquide putride du chaudron couler dans la bouteille vide.$B$BVous voilà en possession du poison et $gprêt:prête; à retourner auprès de la grande prêtresse MacDonnell au camp du Noroît.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (5226, 'itIT', 'Return to Chillwind Camp', 'Bring the Filled Gahrron\'s Withering Bottle to High Priestess MacDonnell at Chillwind Camp, Western Plaguelands.', 'You carefully close the spigot in the control panel, watching the cauldron\'s putrid ichors drip into the empty bottle.$B$BWith the poison in hand, it is time to return to High Priestess MacDonnell at Chillwind Camp to complete the mission.', '', '', '', '', '', '', 0);
