INSERT INTO `quest_template_locale` VALUES (5236, 'ruRU', 'Возвращение в Бастион', 'Вы тщательно перекрываете кран на панели управления, и остатки гнусной жижи медленно стекают в пустую бутылку.$b$bЗаполучив яд, пора возвращаться к жрице Тени Вандис в Бастион.', 'Отнесите полную бутылку с пустоши Гаррона жрице Тени Вандис в Бастион в Западных Чумных землях.', '', '', '', '', '', '', 18019);
