INSERT INTO `quest_template_locale` VALUES (5842, 'deDE', 'Willkommen!', 'Bringt Orenthil Wisperwind in Dolanaar den Geschenkgutschein von Dolanaar.', 'Willkommen bei World of Warcraft!$B$BAls besonderes Dankeschön dafür, dass Ihr die World of Warcraft Collector\'s Edition erworben habt, erhaltet Ihr diesen Geschenkgutschein, den Ihr bei Orenthil Wisperwind in Dolanaar einlösen könnt. Dort erhaltet Ihr dann ein Geschenk: einen kleinen Gefährten, der Euch bei Eurer Suche nach Abenteuer und Ruhm begleiten wird.$B$BNochmals vielen Dank und genießt Euren Aufenthalt in World of Warcraft!', '', '', '', '', '', '', 0);
