INSERT INTO `quest_template_locale` VALUES (6065, 'ruRU', 'Путь охотника', 'Привет тебе, $gюный охотник:юная охотница;. Ты кажешься мне достаточно $gопытным:опытной;, чтобы научиться обращаться с животным и обрести верного друга.$B$BТебе надо повидаться с Йо Колкой Гривой в деревне Кровавого Копыта. Он научит тебя, как обрести собственного питомца и спутника.', 'Поговорите с Йо Колкой Гривой в деревне Кровавого Копыта, что в Мулгоре.', '', '', '', '', '', '', 18019);
