INSERT INTO `quest_template_locale` VALUES (6070, 'zhCN', '猎人之道', '你好，年轻的猎人。看起来你已经准备好要驯服一头野兽，并将其训练成自己的同伴了。$B$B你必须去见一见多兰纳尔的达扎拉，他可以帮助你驯服自己的第一头宠物。', '与多兰纳尔的达扎拉谈一谈。', '', '', '', '', '', '', 0);
