INSERT INTO `quest_template_locale` VALUES (6721, 'deDE', 'Der Weg des Jägers', 'Sprecht mit Dazalar in Dolanaar in Teldrassil.', 'Hallo, so jung und schon auf der Jagd. Ihr seht mir talentiert genug aus, um ein Tier zu führen und es zu Eurem Gefährten abzurichten.$B$BIhr müsst Dazalar in Dolanaar aufsuchen. Er kann Euch auf den Weg zu einem eigenen Tier führen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (6721, 'esES', 'El camino del cazador', 'Habla con Dazalar en Dolanaar, en Teldrassil.', 'Hola, joven $gcazador:cazadora;. Tienes cara de ser capaz de domar a una bestia.$B$BVe a ver a Dazalar a Dolanaar. Él puede enseñarte a conseguir tu propia mascota.|n', '', '', '', '', '', '', 0);
