INSERT INTO `quest_template_locale` VALUES (6721, 'frFR', 'La voie du chasseur', 'Parler à Dazalar, à Dolanaar, en Teldrassil.', 'Salut, jeune adepte de la chasse. Vous me semblez assez habile pour apprivoiser une bête et en faire votre compagnon.$B$BVous devez aller voir Dazalar, à Dolanaar. Il vous mettra sur la voie, et vous montrera comment avoir votre propre familier.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (6721, 'itIT', 'The Hunter\'s Path', 'Speak with Dazalar at Dolanaar in Teldrassil.', 'Hello, young hunter. You look to me like you are skilled enough to handle a beast and train it as your companion.$B$BYou must see Dazalar in Dolanaar. He can put you on the path to earning a pet of your own.', '', '', '', '', '', '', 0);
