INSERT INTO `quest_template_locale` VALUES (7507, 'deDE', 'Forors Kompendium', 'Ihr habt etwas von größter Wichtigkeit entdeckt. Dieses Buch ist anders, als alle anderen die Ihr bisher gesehen habt.$B$BEs scheint, als hätte jemand oder etwas es geschafft, das Siegel des Athenaeums aufzubrechen. Der Text in seinem Inneren ist beschädigt und schwer lesbar.', 'Bringt Forors Kompendium des Drachentötens zurück in das Athenaeum.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (7507, 'esES', 'Compendio de Foror', 'Has descubierto algo de gran importancia. Este libro no se parece a nada que hayas visto antes.$B$BParece que alguien o algo ha logrado romper el sello de El Athenaeum. El texto es confuso, apenas se puede leer.', 'Devuelve el compendio de matar dragones de Foror a El Athenaeum.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (7507, 'esMX', 'Compendio de Foror', 'Has descubierto algo de gran importancia. Este libro no se parece a nada que hayas visto antes.$B$BParece que alguien o algo ha logrado romper el sello de El Athenaeum. El texto es confuso, apenas se puede leer.', 'Devuelve el compendio de matar dragones de Foror a El Athenaeum.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (7507, 'frFR', 'Le guide de Dagharn', 'Vous avez découvert quelque chose de très important. Ce livre ne ressemble à rien de ce que vous avez déjà vu.$B$BIl semble que quelque chose ou quelqu’un soit parvenu à briser le sceau de l’Athenaeum. A l’intérieur, le texte est à peine lisible.', 'Rendez “Le guide de Dagharn du tueur de dragons“ à l’Athenaeum.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (7507, 'ruRU', 'Справочник Форора', 'Вы обнаружили нечто очень важное. Эта книга не похожа ни на одну из тех, которые вам доводилось видеть раньше.$b$bТакое впечатление, как будто кому-то удалось испортить печать Читальни. Текст искажен и читается с трудом.', 'Верните \"Справочник Форора по убийству драконов\" в Читальню.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (7507, 'zhCN', '弗洛尔的屠龙技术纲要', '你找到了一些非常重要的东西。这本书和你看到过的其它的书不太一样。$B$B某些人或者某些东西将图书馆的封印揭掉了，这本书中的文字十分混乱，根本无法辨识。', '将《弗洛尔的屠龙技术纲要》还回图书馆。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (7508, 'deDE', 'Das Schmieden von Quel\'Serrar', 'Wenn Ihr diese Aufgabe annehmen wollt, zeigt Lydros die glanzlose Klinge.', 'Bringt dem Wissenshüter Lydros die stumpfe und glanzlose Elfenklinge.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (7508, 'esES', 'Forjar Quel\'Serrar', 'Si quieres encargarte de esta tarea, entrega la espada roma a Lydros.', 'Entrega la hoja élfica roma Tradicionalista Lydros.', '', '', '', '', '', '', 18019);
