INSERT INTO `quest_template_locale` VALUES (7522, 'frFR', 'Examiner le Calice', 'Examinez le Calice de la renaissance, $gmortel:mortelle;. C\'est grâce à lui que le Cherchevent reviendra !', 'Examiner le Calice de la renaissance et le rapporter au généralissime Demitrian.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (7522, 'ruRU', 'Сосуд Возрождения', 'Изучи Сосуд Возрождения, смертный. Из него возродится Искатель Ветра!', 'Изучите Сосуд Возрождения и вернитесь к верховному лорду Демитриану.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (7522, 'zhCN', '复生之瓶', '去查看一下他的复生之瓶，凡人。逐风者会在那儿重生！', '查看复生之瓶，然后回到德米提恩那里去。', '', '', '', '', '', '', 0);
