INSERT INTO `quest_template_locale` VALUES (7670, 'frFR', 'Le seigneur Grayson Brisombre', 'Parler au seigneur Grayson Brisombre dans le Quartier de la Cathédrale de Hurlevent.', '$n, le temps est venu d’apprendre ce que vous devrez faire pour vous procurer quelque chose que vous attendez sans doute depuis longtemps… votre destrier !$B$BAllez parler au seigneur Grayson Brisombre, dans le Quartier de la Cathédrale, à Hurlevent. Il vous apprendra à maîtriser la monture favorite des paladins. Et auparavant, il vous chargera de toutes les tâches que vous devrez accomplir pour vous en procurer un.$B$BJ’espère que votre épreuve se passera bien. Pour la Lumière, noble $gfrère:sœur; !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (7670, 'itIT', 'Lord Grayson Shadowbreaker', 'Speak with Lord Grayson Shadowbreaker in Stormwind\'s Cathedral District.', 'The time has come, $n, for you to learn about what it will take for you to acquire something you have no doubt waited on for some time... your charger.$B$BSpeak with Lord Grayson Shadowbreaker in Stormwind\'s Cathedral District.  It is he who will guide you not only though the legerdemain of a Paladin\'s most trusted mount, but the tasks before you to acquire one of your own.$B$BI wish you the best in this trial.  For the Light, noble $G brother : sister;!', '', '', '', '', '', '', 0);
