INSERT INTO `quest_template_locale` VALUES (7738, 'ruRU', 'Безупречная шкура йети', 'Вы добыли безупречную шкуру йети, особенно толстую и прочную – даже прочнее шкур йети из Пещеры Бешеного оврага.$b$bВам известно, что Джангору Быстрому Страннику из Лагеря Мохаче всегда нужны шкуры йети, а уж эта его тем более заинтересует!', 'Отнесите безупречную шкуру йети Джангору Быстрому Страннику в Лагерь Мохаче в Фераласе.', '', '', '', '', '', '', 18019);
