INSERT INTO `quest_template_locale` VALUES (7783, 'koKR', '검은바위의 군주', '오그리마에 있는 아이트리그에게 네파리안의 머리를 가져가야 합니다.', '치열한 전투를 마친 후의 익숙한 느낌이 되살아나며 반갑기까지 한 승리의 고통이 온몸에 퍼집니다. 지금 당신의 앞에 전투로 인해 상처 입은 거대한 네파리안의 머리가 놓여 있습니다. 용사여, 이 머리를 대족장의 조언자 아이트리그에게 가져가십시오.', '', '', '', '', '', '', 0);
