INSERT INTO `quest_template_locale` VALUES (7783, 'ruRU', 'Владыка Черной горы', 'Отнесите голову Нефариана Эйтриггу в Оргриммар.', 'Прежнее чувство возвращается. Радостный трепет от победы разливается по твоим жилам. Перед тобой лежит огромная, покрытая боевыми шрамами голова Нефариана. Отнеси ее советнику Эйтриггу, $gвоин:воительница;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (7783, 'zhCN', '黑石之王', '将奈法利安的徽记交给奥格瑞玛的伊崔格。', '以前的感觉又回来了，胜利的喜悦和伤痛的折磨流遍你的身体。在你面前的是奈法利安那枚巨大而布满创痕的徽记。勇士，将这枚徽记交给大酋长的顾问伊崔格吧。', '', '', '', '', '', '', 0);
