INSERT INTO `quest_template_locale` VALUES (7784, 'koKR', '검은바위의 군주', '오그리마에 있는 대군주 나토즈와 대화해야 합니다. 힘의 골짜기에서 당신을 기다리고 있습니다.', '이제 힘의 골짜기로 가서 대군주 나토즈를 만나보게나.', '', '', '', '', '', '', 0);
