INSERT INTO `quest_template_locale` VALUES (7785, 'deDE', 'Untersuchung des Gefäßes', 'Untersucht das Gefäß der Wiedergeburt und bringt es zu Hochlord Demitrian.', 'Untersucht das Gefäß der Wiedergeburt, Sterblicher. Mit seiner Hilfe soll der Windsucher wieder auferstehen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (7785, 'esES', 'Examina la vasija', 'Examina la vasija de renacer y devuélvesela al alto señor Demitrian.', 'Examina la vasija de su renacimiento, mortal. ¡De aquí renacerá el Hijo del Viento!', '', '', '', '', '', '', 0);
