INSERT INTO `quest_template_locale` VALUES (7785, 'koKR', '결정 조사', '환생의 관을 조사한 후, 대영주 데미트리안에게 가져가야 합니다.', '필멸의 생명체여, 환생의 관을 조사하십시오. 이 결정에서 바람추적자가 새로이 태어날 것입니다!', '', '', '', '', '', '', 0);
