INSERT INTO `quest_template_locale` VALUES (7785, 'ruRU', 'Сосуд Возрождения', 'Изучите Сосуд Возрождения и вернитесь к верховному лорду Демитриану.', 'Изучи Сосуд Возрождения, смертный. Из него возродится Искатель Ветра!', '', '', '', '', '', '', 0);
