INSERT INTO `quest_template_locale` VALUES (7908, 'frFR', 'Le maître de l\'arène', 'Parler à Court John Mithril dans l’arène de Strangleronce.', 'Être le maître d’une arène, ça ne doit pas se limiter à ça ! Le pirate qui anime celle des Gurubashi aura peut-être des conseils à me donner.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (7908, 'itIT', 'Arena Master', 'Speak with Short John Mithril in the Stranglethorn Arena.', 'There must be more to being a master of the arena than this!   Perhaps the Pirate who runs the event at Gurubashi will know!', '', '', '', '', '', '', 0);
