INSERT INTO `quest_template_locale` VALUES (7908, 'ruRU', 'Повелитель арены', 'Поговорите с Коротышкой Джоном Мифрилом на арене Тернистой долины.', 'Должно быть, стать повелителем арены не так просто! Быть может, пират-распорядитель на Арене Гурубаши, знает, в чем тут штука!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (7908, 'zhCN', '竞技场高手', '要成为竞技场高手肯定需要付出更多的代价！也许管理古拉巴什竞技场的海盗知道到底要多少！', '与荆棘竞技场的小个子约翰·米斯瑞尔谈一谈。', '', '', '', '', '', '', 0);
