INSERT INTO `quest_template_locale` VALUES (8300, 'frFR', 'Plus de caisses de ressources', '', '', '', 'Retournez voir le Nécrotraqueur Mortis dans les Hautes-terres Arathies.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (8300, 'ruRU', 'Новые ящики с ресурсами', '', '', '', 'Вернитесь к ловчему смерти Мортису на Нагорье Арати.', '', '', '', '', 18019);
