INSERT INTO `quest_template_locale` VALUES (8339, 'ruRU', 'Обмануть Совет<NYI> <TXT> UNUSED', 'Чтобы сделать перстень-печатку сумеречного сектанта, мне понадобится куча предметов.', 'Принесите предметы Аурель Золотой Лист в Крепость Кенария.', '', '', '', '', '', '', 18019);
