INSERT INTO `quest_template_locale` VALUES (8430, 'frFR', 'La bataille du goulet des Chanteguerres', 'Apportez 3 Marques d’honneur du goulet des Chanteguerres à l’un des porteguerre de la Horde, devant les champs de bataille.', 'La bataille dans le goulet des Chanteguerres contre les Sentinelles d’Aile-argent est d’une grande importance. Sous le prétexte de protéger une forêt qui ne lui appartient pas, l’Alliance cherche à refuser à la Horde l’une de ses plus grandes ressources en bois.$B$BNe la laissez pas faire, $n ! Revenez me voir quand vous aurez la preuve que vous avez bien servi la Horde !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8430, 'itIT', 'Battaglia della Forra dei Cantaguerra', 'Porta 3 Contrassegni d\'Onore della Forra dei Cantaguerra a un Araldo della Guerra dell\'Orda fuori dai campi di battaglia.', 'La Battaglia della Forra dei Cantaguerra contro le Sentinelle Alargentea è importantissima. Con la scusa di proteggere una foresta che non gli appartiene, l\'Alleanza sta cercando di privare l\'Orda di una grossa risorsa di legname.$b$bNon permettere che ci riesca, $n! Torna da me con la prova del tuo servigio all\'Orda!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8430, 'ptBR', 'Batalha da Ravina Brado Guerreiro', 'Leve 3 Marcas de Honra da Ravina Brado Guerreiro a um Armipotente da Horda do lado de fora do campo de batalha.', 'A batalha na Ravina Brado Guerreiro contra as Sentinelas da Asa de Prata é de grande importância. Usando como desculpa a defesa de uma floresta que não os pertence, a Aliança tenta impedir a Horda de utilizar uma das nossas maiores fontes de madeira.$B$BNão permita que isso aconteça, $n! Volte e me traga provas de que você foi útil à Horda de alguma forma!', '', '', '', '', '', '', 0);
