INSERT INTO `quest_template_locale` VALUES (8601, 'ruRU', 'Орде все еще не хватает грубой кожи!', '', '', '', 'Вернитесь к доктору Серратусу в Оргриммар.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (8601, 'zhCN', '部落需要更多硬甲皮！', '', '', '', '去找奥格瑞玛的塞拉图斯。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8602, 'deDE', 'Schulterstücke des Sturmrufers', 'Bringt die Dominanzbindungen der Qiraji, 2 Götzen des Lebens, 5 Goldskarabäen und 5 Kristallskarabäen zu Andorgos in Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen neutralen Ruf bei der Brut Nozdormus.', 'Man sagt unter uns, dass die kunstvoll verzierten Schulterpanzerungen, die von den sterblichen Völkern getragen werden, zu Beginn eine Nachahmung von Drachenflügeln werden sollten.$B$BBringt mir die Bindungen, die von den höchsten Anführern der Qiraji getragen werden und ich werde sie in ein Paar Schulterstücke verarbeiten, die furchteinflößender als die Flügel Nefarians selbst sind!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8602, 'esES', 'Los espaldares de clamatormentas', 'Llévale las ataduras de dominancia qiraji, 2 ídolos de la vida, 5 escarabajos de oro y 5 escarabajos de cristal a Andorgos en Ahn\'Qiraj. También debes alcanzar la reputación neutral ante el Linaje de Nozdormu para completar esta misión.', 'Se cuenta entre nosotros que el adorno de la armadura de los hombros que llevan las razas mortales se inspiró en un intento de emular las alas de un dragón.$B$BTráeme las esclavas que llevan los más importantes líderes qiraji y con ellas te forjaré unas hombreras ¡más temidas que las alas del mismísimo Nefarian!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8602, 'esMX', 'Los espaldares de clamatormentas', 'Llévale las ataduras de dominancia qiraji, 2 ídolos de la vida, 5 escarabajos de oro y 5 escarabajos de cristal a Andorgos en Ahn\'Qiraj. También debes alcanzar la reputación neutral ante el Linaje de Nozdormu para completar esta misión.', 'Se cuenta entre nosotros que el adorno de la armadura de los hombros que llevan las razas mortales se inspiró en un intento de emular las alas de un dragón.$B$BTráeme las esclavas que llevan los más importantes líderes qiraji y con ellas te forjaré unas hombreras ¡más temidas que las alas del mismísimo Nefarian!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8602, 'frFR', 'Les espauliers d’implorateur de tempête', 'Apportez des Manchettes de domination qiraji, 2 Idoles de la vie, 5 Scarabées d\'or et 5 Scarabées de cristal à Andorgos, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard.', 'Nous pensons qu’à l’origine, les épaulières ornées que portent les races mortelles étaient une tentative pour imiter les ailes des dragons.$B$BRapportez-moi les manchettes portées par les plus hauts dignitaires qiraji, et je les façonnerai pour en faire des espauliers plus redoutables que les ailes de Nefarian lui-même !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8602, 'itIT', 'Paraspalle dell\'Invocatore delle Tempeste', 'Porta le Manette del Dominio Qiraji, 2 Idoli della Vita, 5 Scarabei d\'Oro e 5 Scarabei di Cristallo a Andorgos ad Ahn\'qiraj. Per completare questa missione devi anche guadagnarti la reputazione Neutrale con la Stirpe di Nozdormu.', 'Si narra che le protezioni alle spalle ornate delle razze mortali costituissero originariamente un tentativo di emulare le ali dei draghi.$B$BPortami le manette indossate dai capi Qiraji più importanti e io le trasformerò in un paio di spallacci più terribili delle ali dello stesso Nefarian!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8602, 'koKR', '폭풍소환사의 견갑', '안퀴라즈에 있는 안도르고스에게 지배의 퀴라지 팔보호구, 생명의 우상 2개, 황금 스카라베 5개, 수정 스카라베 5개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 노즈도르무 혈족으로부터 중립적 이상의 평판을 얻어야 합니다.', '필멸의 종족들이 착용하고 있는 화려한 어깨갑옷이 용의 날개를 본뜨려고 한 시도에서 유래했다는 소문이 돌더군요.$B$B퀴라지 최고 지휘관들이 차고 있는 팔보호구를 가지고 오세요. 그럼 네파리안의 날개보다도 무시무시한 견갑으로 만들어 드릴게요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8602, 'ptBR', 'Brafoneiras do Tempestário', 'Leve os Braceletes do Domínio Qiraji, 2 Ídolos da Vida, 5 Escaravelhos de Ouro e 5 Escaravelhos de Cristal para Andorgos em Ahn\'Qiraj. Também é necessário ser $gTolerado:Tolerada; pela Prole de Nozdormu para concluir a missão.', 'Dizem que as exuberantes ombreiras usadas pelas raças mortais foram uma tentativa de imitar asas de dragão.$B$BTraga-me os braceletes utilizados pelos líderes supremos dos qirajis, e eu os transformarei em proteções de ombros mais terríveis que as asas do próprio Nefarian!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8602, 'ruRU', 'Наплечье Зовущего бурю', 'Принесите киражские наручники господства, 2 идола Жизни, 5 золотых и 5 хрустальных скарабеев Андоргос в Ан\'Кираж. Кроме того, необходимо заслужить как минимум равнодушное отношение рода Ноздорму.', 'У нас говорят, что вычурные наплечные доспехи, которые носят смертные, были изначально созданы в подражание крыльям драконов.$B$BПринеси мне наручники, принадлежащие киражским правителям, и я сделаю из них наплечье еще более устрашающее, чем крылья самого Нефариана!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8602, 'zhCN', '风暴召唤者的肩甲', '把其拉统御腕轮、2个生命塑像、5个黄金甲虫和5个水晶甲虫交给安其拉的安多葛斯。你必须首先在诺兹多姆的子嗣中达到中立声望才可以完成这个任务。', '据说凡人所穿着的绚丽护肩是他们用来模仿龙翼的。$B$B把其拉虫人的最高领袖所佩戴的腕轮交给我，我会用它来制作一副比奈法利安的双翼还要可怕的护肩！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8602, 'zhTW', '風暴召喚者肩鎧', '將其拉統御束腕、2個生命塑像、5隻金色甲蟲和5隻水晶甲蟲帶給安其拉的安多葛斯。你還必須在諾茲多姆的子嗣達到中立的聲望值才能完成這個任務。', '據說我們之中那個被人類種族穿戴的華麗肩鎧已成為與龍翼競爭的工具了。$B$B幫我把最高等其拉領導者穿戴的護甲弄來，然後我就能將它們做成一套比奈法利安翅膀更可怕的肩鎧了!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8603, 'deDE', 'Gewänder des Orakels', 'Bringt die Hülle des Alten Gottes, 2 Götzen des Todes, 5 Steinskarabäen und 5 Kristallskarabäen zu Vethsera innerhalb von Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf bei der Brut Nozdormus.', 'Der unaussprechliche Alte Gott erwacht langsam aus seinem Schlummer unterhalb Ahn\'Qirajs. Das schreckliche Schicksal, das Azeroth erwartet, könnte in Tagen oder auch Jahren Wirklichkeit werden, Zeit spielt für Wesen seiner Macht keine Rolle.$B$BStehen die Sterne günstig, wird er furchtbaren Zorn gegen alle Lebewesen walten lassen. Ihr müsst dem zuvorkommen und einen Weg finden, um seiner Existenz ein Ende zu bereiten.$B$BTut dies und bringt mir ein Stück seiner Hülle. Ich werde Euch eine Rüstung gewähren, die noch kein Sterblicher getragen hat... Rüstung für einen Helden, der einen Gott bezwungen hat!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8603, 'esES', 'Las vestimentas del Oráculo', 'Llévale la coraza del dios antiguo, 2 ídolos de muerte, 5 escarabajos de piedra y 5 escarabajos de cristal a Vethsera en Ahn\'Qiraj. También debes alcanzar la reputación de honorable ante el Linaje de Nozdormu para completar esta misión.', 'El inmencionable dios antiguo se despierta despacio de su sueño bajo Ahn\'Qiraj. El terrible sino que aguarda a Azeroth podría llegar dentro de unos días o unos años; el tiempo nada significa para seres con su poder.$B$BCuando los astros sean favorables, desatará su ira contra todo ser viviente. Debes hallarlo antes de que ocurra y encontrar la forma de poner fin a su existencia.$B$BHazlo y tráeme un pedazo de sus restos. Te concederé una armadura como ningún mortal ha llevado... ¡una armadura hecha para quien ha acabado con un Dios!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8603, 'esMX', 'Las vestimentas del Oráculo', 'Llévale la coraza del dios antiguo, 2 ídolos de muerte, 5 escarabajos de piedra y 5 escarabajos de cristal a Vethsera en Ahn\'Qiraj. También debes alcanzar la reputación de honorable ante el Linaje de Nozdormu para completar esta misión.', 'El inmencionable dios antiguo se despierta despacio de su sueño bajo Ahn\'Qiraj. El terrible sino que aguarda a Azeroth podría llegar dentro de unos días o unos años; el tiempo nada significa para seres con su poder.$B$BCuando los astros sean favorables, desatará su ira contra todo ser viviente. Debes hallarlo antes de que ocurra y encontrar la forma de poner fin a su existencia.$B$BHazlo y tráeme un pedazo de sus restos. Te concederé una armadura como ningún mortal ha llevado... ¡una armadura hecha para quien ha acabado con un Dios!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8603, 'frFR', 'L’habit de l’oracle', 'Apportez la Carcasse du Dieu très ancien, 2 Idoles de la mort, 5 Scarabées de pierre et 5 Scarabées de cristal à Vethsera, à l’intérieur d’Ahn’Qiraj. Pour terminer cette quête, vous devez être $gHonoré:Honorée; par la Progéniture de Nozdormu.', 'L’indicible Dieu très ancien se réveille lentement de son sommeil sous Ahn’Qiraj. Le destin atroce qui attend Azeroth peut s’abattre sur nous dans des jours, ou des années, car le temps n’a pas vraiment de sens pour lui.$B$BMais lorsque les étoiles seront propices, il se déchaînera, et son effroyable courroux s’abattra sur toutes les créatures vivantes. Trouvez-le avant qu’il ne soit trop tard, et mettez un terme à son existence, d’une manière ou d’une autre.$B$BSi vous y parvenez, ramenez-moi un morceau de sa carcasse, et je vous en ferai une armure qui n’a encore jamais été portée par aucun mortel… une armure digne $gd’un tueur:d’une tueuse; de dieu !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8603, 'itIT', 'Paramento dell\'Oracolo', 'Porta il Guscio del Dio Antico, 2 Idoli di Pronto Ritorno, 5 Scarabei di Pietra e 5 Scarabei di Cristallo a Vethsera ad Ahn\'qiraj. Per completare questa missione devi anche guadagnarti la reputazione Onorato con la Stirpe di Nozdormu.', 'L\'innominabile Dio Antico si sta lentamente risvegliando dal suo sonno sotto Ahn\'qiraj. Il terribile fato che attende Azeroth potrebbe realizzarsi tra pochi anni come tra pochi giorni: il tempo non ha significato per esseri tanto potenti.$B$BQuando le stelle saranno favorevoli, si scaglierà contro tutti gli esseri viventi con furia terribile. Devi trovarlo prima di allora e scoprire il modo di porre fine alla sua esistenza.$B$BFallo e portami un pezzo del suo guscio. Realizzerò per te un\'armatura mai indossata da alcun mortale... un\'armatura degna di chi ha ucciso un dio!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8603, 'koKR', '신탁의 의복', '안퀴라즈 안에 있는 베스세라에게 고대 신의 허물, 죽음의 우상 2개, 돌 스카라베 5개, 수정 스카라베 5개를 가져가야 합니다. 이 퀘스트를 완료하려면 노즈도르무 혈족에게 우호적 평판을 얻어야 합니다.', '안퀴라즈 지하에는 입에 담기조차 두려운 고대 신이 잠들어 있어요. 지금 그가 서서히 깨어나고 있습니다. 이제 아제로스를 기다리는 끔찍한 운명이 찾아올 겁니다. 며칠이 됐든 몇 년이 됐든 고대의 힘을 지닌 존재들에게 그 시간은 아무런 의미가 없습니다.$B$B별들의 항로가 고대 신의 운명과 함께하는 날, 그는 살아 있는 모든 존재를 향해 무시무시한 분노를 터뜨릴 겁니다. 그날이 오기 전에 그를 제거할 방법을 모색해야 합니다.$B$B고대 신을 없애고 그 허물 한 조각을 제게 가져다주세요. 지금까지 그 어떤 필멸의 존재도 입어 보지 못한... 신을 처단한 이에게 걸맞은 그런 갑옷을 드리겠어요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8603, 'ptBR', 'Vestimenta do Oráculo', 'Entregue a Carapaça do Deus Antigo, 2 Ídolos da Morte, 5 Escaravelhos de Pedra e 5 Escaravelhos de Cristal para Vethsera em Ahn\'Qiraj. Também é necessário ser $gHonrado:Honrada; pela Prole de Nozdormu para concluir a missão.', 'O Deus Antigo, cujo nome não ouso proferir, desperta lentamente de seu sono nos subterrâneos de Ahn\'Qiraj. O terrível destino de Azeroth pode se concretizar em questão de dias ou anos; o tempo nada significa para criatura tão poderosa.$B$BQuando as estrelas se mostrarem favoráveis, ele se lançará contra todos os seres vivos com terrível ira. Você precisa encontrá-lo antes disso e descobrir um modo de dar fim à sua existência.$B$BFaça isso e me traga um pedaço da carapaça dele. Eu lhe agraciarei com uma armadura que nenhum mortal jamais vestiu... digna de quem derrotou um deus!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8603, 'ruRU', 'Одежды оракула', 'Принесите чешую древнего бога, 2 идола Смерти, 5 каменных скарабеев и 5 хрустальных скарабеев Ветсере в Ан\'Кираже. Кроме того, необходимо заслужить как минимум уважительное отношение рода Ноздорму.', 'Древний бог, о котором лучше вслух не упоминать, медленно пробуждается от своего сна в Ан\'Кираже. Ужасная судьба, которая ждет Азерот в случае его пробуждения, – вопрос лишь нескольких дней... или нескольких лет. Для существа, обладающего подобной мощью, время не имеет значения.$B$BВстанут звезды подходящим образом – и ярость его обрушится на живущих, подобно плети. Тебе следует найти его и положить конец его существованию, пока он не пробудился.$B$BКогда ты сделаешь это, принеси мне кусок его панциря. Взамен я дам тебе броню, подобной которой не владел еще никто из смертных. Броню, достойную убийцы бога.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8603, 'zhCN', '神谕者的外套', '把上古之神的外鞘、2个死亡塑像、5个岩石甲虫和5个水晶甲虫交给安其拉的温瑟拉。你必须在诺兹多姆的子嗣中达到尊敬声望才能完成这个任务。', '最令人恐怖的上古之神正在从安其拉的休眠之中慢慢醒来。艾泽拉斯世界不久之后即将面对这股恐怖的力量，时间对于如此强大的存在来说毫无意义。$B$B当星辰的位置合适的时候，他将让恐怖的力量席卷所有的一切。你必须在此之前找到他，消灭他。$B$B完成这个任务，把他的外壳交给我。我会奖励你一件凡人从未见到过的护甲……一件只有弑神者才有资格获得的护甲！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8603, 'zhTW', '神諭者的法衣', '將上古之神的外殼、2個死亡塑像、5隻石甲蟲和5隻水晶甲蟲帶給安其拉的溫瑟拉。你還必須在諾茲多姆的子嗣達到尊敬聲望值才能完成這個任務。', '最令人恐懼的上古之神正在從安其拉的休眠之中慢慢醒來。艾澤拉斯世界不久之後即將面對這股恐怖的力量，時間對於如此強大的存在來說毫無意義。$B$B當星辰的位置合適的時候，他將讓恐怖的力量席捲所有的一切。你必須在此之前找到他，消滅他。$B$B完成這個任務，把他的外殼交給我。我會獎勵你一件凡人從未見到過的護甲...一件只有弒神者才有資格獲得的護甲!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8604, 'deDE', 'Die Horde braucht Wollverbände!', 'Bringt 20 Wollverbände zu Heiler Fernläufer im Tal der Geister in Orgrimmar.', 'Seid gegrüßt, $C. Während der Vorbereitungen für den Krieg in Ahn\'Qiraj, bin ich hier, um die Bemühungen der Horde beim Zusammentragen von Wollverbänden zu koordinieren. Auf dem Schlachtfeld können sich unsere Soldaten nicht auf die Gegenwart eines Heilers verlassen, der sie am Leben hält. Glaubt mir, ich weiß das. Daher stellen wir all diese Sanitätskästen für sie zusammen.$B$BSolltet Ihr Zeit haben: Ich brauche sehr viele Wollverbände. Bringt sie hierher zu mir und ich werde sehen, dass Ihr dafür ordnungsgemäß entlohnt werdet.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8604, 'esES', '¡La Horda necesita vendas de lana!', 'Llévale 20 vendas de lana al sanador Largo Camino en el Valle de los Espíritus de Orgrimmar.', 'Saludos, $c. Para los preparativos de la guerra de Ahn\'Qiraj, estoy coordinando las tareas de la Horda para reunir vendas de lana. Por desgracia, cuando estén ahí fuera, nuestros soldados no pueden depender de la presencia de un sanador para mantenerse vivos. Créeme, he pasado por ahí. Por lo tanto, estamos confeccionando todos estos botiquines de primeros auxilios para ellos.$B$BSi tienes tiempo, necesito muchas vendas de lana. Tráemelas aquí y me ocuparé personalmente de que recibes tu merecida recompensa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8604, 'esMX', '¡La Horda necesita vendas de lana!', 'Llévale 20 vendas de lana al sanador Largo Camino en el Valle de los Espíritus de Orgrimmar.', 'Saludos, $c. Para los preparativos de la guerra de Ahn\'Qiraj, estoy coordinando las tareas de la Horda para reunir vendas de lana. Por desgracia, cuando estén ahí fuera, nuestros soldados no pueden depender de la presencia de un sanador para mantenerse vivos. Créeme, he pasado por ahí. Por lo tanto, estamos confeccionando todos estos botiquines de primeros auxilios para ellos.$B$BSi tienes tiempo, necesito muchas vendas de lana. Tráemelas aquí y me ocuparé personalmente de que recibes tu merecida recompensa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8604, 'frFR', 'La Horde a besoin de bandages en laine !', 'Apportez 20 Bandages en laine au Guérisseur Long-coureur dans la Vallée des Esprits d\'Orgrimmar.', 'Je vous salue, $c. Je coordonne les efforts de la Horde pour collecter des bandages en laine, en préparation de la toute prochaine guerre à Ahn’Qiraj. Une fois sur le terrain, nos soldats n’ont pas toujours le luxe d’attendre l’intervention d’un soigneur pour rester en vie. Croyez-moi, je sais de quoi je parle. Nous leur préparons donc toutes ces trousses de premiers soins.$B$BSi vous avez le temps, sachez que j’ai besoin de nombreux bandages en laine. Si vous m’en ramenez, je veillerai à ce que vous soyez $grécompensé:récompensée;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8604, 'itIT', 'L\'Orda ha bisogno di Bende di Lana!', 'Porta 20 Bende di Lana al Guaritore Passo Lungo della Valle degli Spiriti a Orgrimmar.', 'Salve, $C. Io sono qui per coordinare le operazioni di raccolta di Bende di Lana in vista della guerra di Ahn\'qiraj. Purtroppo, sul campo i nostri soldati non possono sempre contare sulla presenza di un guaritore che possa tenerli in vita. Fidati, lo so per esperienza. Così stiamo cercando di preparare quante più Forniture di Primo Soccorso possibile.$B$BSe hai tempo, mi servirebbero delle Bende di Lana. Portamele e io farò in modo che i tuoi sforzi vengano riconosciuti.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8604, 'koKR', '호드에 양모 붕대 조달!', '오그리마의 정기의 골짜기에 있는 치료사 롱러너에게 양모 붕대 20개를 가져가야 합니다.', '$c|1이여;여;, 반갑네. 나는 호드를 도와 안퀴라즈 전쟁을 준비하기 위해 여기에서 양모 붕대를 모으고 있다네. 치열한 전쟁터에서 우리 병사들의 생명을 사제들에게만 맡겨 둘 수는 없는 일이지. 이건 내가 실전에서 얻은 교훈이라네. 그래서 우리는 응급치료 물품을 모으고 있는 중일세.$B$B자네도 시간이 된다면 양모 붕대를 많이 모아다 주게나. 그 보답은 꼭 해주겠네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8604, 'ptBR', 'A Horda precisa de bandagens de lã!', 'Leve 20 Bandagens de Lã ao Curador Pisa Léguas no Vale dos Espíritos em Orgrimmar.', 'Salve, $c. Estou aqui nos preparativos para a guerra em Ahn\'Qiraj coordenando os esforços da Horda para juntar bandagens de lã. Infelizmente, lá no campo de batalha, nossos solados não podem contar com a presença de um curador para mantê-los vivos. Confie em mim. Eu sei isso de antemão. Por isso, estamos juntando esses kits de primeiros socorros para eles.$B$BSe você tiver tempo, preciso de muitas bandagens de lã. Traz pra mim que eu garanto a recompensa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8604, 'ruRU', 'Орде не хватает шерстяных бинтов!', 'Принесите 20 шерстяных бинтов целителю клана Долгий Бег на Аллею Духов Оргриммара.', 'Приветствую, |3-6($c). Мне поручено собирать шерстяные бинты для подготовки к войне в Ан\'Кираже. К сожалению, на поле боя солдаты не могут рассчитывать, что лекари успеют добраться сразу до всех. Поэтому мы должны уделить как можно больше внимания оказанию первой помощи.$B$BЕсли у тебя есть время, помоги мне собрать шерстяные бинты. Принеси их сюда, а я позабочусь, чтобы тебе выдали положенное вознаграждение.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8604, 'zhCN', '部落需要绒线绷带！', '把20卷绒线绷带交给奥格瑞玛精神谷的医师朗兰纳。', '你好，$c。为了准备安其拉之战，我在这里帮助部落协调收集绒线绷带。在战场上，士兵们不能指望总有医师跟着他们，所以我们要为他们准备急救包。$B$B如果你有时间的话，我需要很多绒线绷带，请把绷带交给我，我会奖励你的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8604, 'zhTW', '部落需要絨線繃帶!', '帶20個絨線繃帶給奧格瑪精神谷的醫者朗蘭納。', '你好，$c。為了安其拉戰役的準備，我在這裡協調部落收集絨線繃帶的事。戰場上，我們的士兵們無法仰賴醫者得以生存。因此，我們要將這些急救工具收集好給他們。$B$B如果你有時間，我需要很多的絨線繃帶。把它們拿來給我，我就會好好地獎勵你。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8605, 'deDE', 'Die Horde braucht mehr Wollverbände!', '', '', '', 'Kehrt zu Heiler Fernläufer im Tal der Geister in Orgrimmar zurück.', '', '', '', '', 18019);
